async createTerminalProcess(terminalId) {
  try {
    const pty = require('node-pty');
    const os = require('os');

    const shell = os.platform() === 'win32' ? 'powershell.exe' : 'bash';
    const startupCwd = this.terminalState.cwd || __dirname;

    console.log(`ğŸ–¥ï¸ Creating terminal ${terminalId} with CWD: ${startupCwd}`);

