        ...this.terminalState.environment,
        CLAUDE_APP_INTEGRATION: 'true',
        CLAUDE_APP_PORT: '9223',
        CLAUDE_TERMINAL_ID: terminalId.toString(),
      },
    });

    // Set up data handling
    ptyProcess.onData(data => {
      if (this.mainWindow && !this.mainWindow.isDestroyed()) {
        this.mainWindow.webContents.send('terminal-data', {
          terminalId: terminalId,
          data: data,
        });
      }
    });

    // Handle exit
    ptyProcess.onExit(async ({ exitCode }) => {
      console.log(`ğŸ”´ Terminal ${terminalId} exited with code ${exitCode}`);
      this.terminalProcesses.delete(terminalId);

      if (this.mainWindow && !this.mainWindow.isDestroyed()) {
        this.mainWindow.webContents.send('terminal-exit', {
          terminalId: terminalId,
          exitCode: exitCode,
        });
      }
    });

    // Store terminal process
    this.terminalProcesses.set(terminalId, ptyProcess);

    console.log(`âœ… Terminal ${terminalId} created successfully`);
    return {
      success: true,
      terminalId: terminalId,
      pid: ptyProcess.pid,
    };

  } catch (error) {
    console.error(`âŒ Failed to create terminal ${terminalId}:`, error);
    return {
      success: false,
      error: error.message,
    };
  }
}

