if (!gotTheLock) {
  app.quit();
} else {
  app.on('second-instance', (_event, _commandLine, _workingDirectory) => {
    // Someone tried to run a second instance, we should focus our window.
    if (containerApp.mainWindow) {
      if (containerApp.mainWindow.isMinimized()) containerApp.mainWindow.restore();
      containerApp.mainWindow.focus();
