            } catch (error) {
              console.warn('âš ï¸ Terminal resize failed:', error);
            }
          }
        }, 100);
      });

      // Pipe data from backend to terminal UI
      window.electronAPI.onTerminalData(data => {
        console.log('ğŸ“¥ Terminal received data:', data);
        term.write(data);
      });

      // Pipe data from terminal UI to backend
      term.onData(data => {
        console.log('ğŸ“¤ Terminal sending data:', data);
        window.electronAPI.writeToTerminal(data);
      });

      // Initial prompt request - send newline to trigger bash prompt
      setTimeout(() => {
        console.log('ğŸ”„ Requesting initial bash prompt...');
        window.electronAPI.writeToTerminal('\n');
      }, 500);

      console.log('âœ… Real, live terminal initialized.');

      // Add drag-and-drop functionality to terminal
      setupTerminalDragAndDrop(terminalContainer, term);

      // Give initial focus to terminal since it's the active tab
      term.focus();
    } else {
      console.log('âš ï¸ Terminal libraries not available - showing placeholder');
      terminalContainer.innerHTML = `
                <div style="
                    display: flex; 
                    align-items: center; 
                    justify-content: center; 
                    height: 100%; 
                    color: #888; 
                    font-family: monospace;
                    background: #1e1e1e;
                ">
                    Terminal libraries loading... Please restart the app.
                </div>
            `;
    }
  } else {
    console.error('âŒ Terminal container div not found!');
  }

  // --- Initial Status Check ---
  console.log('ğŸ” Requesting initial Chrome status...');
  window.electronAPI.getChromeStatus().then(updateUI);
  updateConnectionStatus();

  // Set up periodic updates
  setInterval(updateConnectionStatus, 5000); // Update every 5 seconds

  // Setup webview error listeners for initial webview
  setupWebviewListeners();

  // **STABLE ERROR MONITORING SYSTEM**
  console.log('ğŸ”§ Setting up stable error monitoring system...');

  const errorCount = { total: 0, chrome: 0, app: 0 };
  let errorBuffer = []; // Buffer to prevent spam
  const _lastErrorTime = 0;
  const ERROR_THROTTLE_MS = 1000; // Max 1 error per second per type
  const MAX_ERRORS_PER_MINUTE = 10; // Prevent spam
  let errorTimestamps = [];

  // Throttled error logging to prevent infinite loops and spam
  function throttledAddError(error) {
    const now = Date.now();

    // Remove old timestamps (older than 1 minute)
    errorTimestamps = errorTimestamps.filter(t => now - t < 60000);

    // Check rate limit
    if (errorTimestamps.length >= MAX_ERRORS_PER_MINUTE) {
      return; // Drop error to prevent spam
    }

    // Check for duplicate recent errors
    const recentSimilar = errorBuffer.find(e => 
      e.message === error.message && 
            e.source === error.source && 
            (now - e.timestamp) < ERROR_THROTTLE_MS,
    );

    if (recentSimilar) {
      return; // Drop duplicate
    }

    // Add to buffer and clean old entries
    errorBuffer.push(error);
    errorBuffer = errorBuffer.filter(e => now - e.timestamp < 5000); // Keep 5 seconds
    errorTimestamps.push(now);

    // Safe error logging
    try {
      addErrorLogEntry(error);

      if (error.source.includes('Chrome') || error.source.includes('WebSocket') || error.source.includes('Network')) {
        errorCount.chrome++;
      } else {
        errorCount.app++;
      }
      errorCount.total++;
      updateErrorCounts(errorCount.total, errorCount.chrome, errorCount.app);
    } catch (e) {
      // Prevent infinite loops if error logging itself fails
      console.warn('Error logging failed:', e.message);
    }
  }

  // Safe console error capture (prevents infinite loops)
  function captureConsoleErrors() {
    const originalConsoleError = console.error;
    const originalConsoleWarn = console.warn;

    let isLoggingError = false; // Prevent recursion

    console.error = function(...args) {
      if (!isLoggingError) {
        isLoggingError = true;
        try {
          const errorMessage = args.join(' ');
          // Skip our own error logging messages to prevent loops
          if (!errorMessage.includes('addErrorLogEntry') && !errorMessage.includes('Error logging failed')) {
            throttledAddError({
              timestamp: Date.now(),
              message: errorMessage.substring(0, 200), // Limit message length
              source: 'App Errors',
              type: 'error',
            });
          }
        } finally {
          isLoggingError = false;
        }
      }
      originalConsoleError.apply(console, args);
    };

    console.warn = function(...args) {
      if (!isLoggingError) {
        isLoggingError = true;
        try {
          const warningMessage = args.join(' ');
          if (!warningMessage.includes('addErrorLogEntry') && !warningMessage.includes('Error logging failed')) {
            throttledAddError({
              timestamp: Date.now(),
              message: warningMessage.substring(0, 200),
              source: 'Warnings',
              type: 'warning',
            });
          }
        } finally {
          isLoggingError = false;
        }
      }
      originalConsoleWarn.apply(console, args);
    };
  }

  // Efficient WebSocket error monitoring
  function setupWebSocketErrorMonitoring() {
    if (!window.WebSocket) return; // Skip if WebSocket not available

    const originalWebSocket = window.WebSocket;
    window.WebSocket = function(url, protocols) {
      const ws = new originalWebSocket(url, protocols);

      // Only log significant WebSocket errors, not every connection attempt
      ws.addEventListener('error', (_event) => {
        throttledAddError({
          timestamp: Date.now(),
          message: `WebSocket error: ${url.substring(0, 50)}...`,
          source: 'WebSocket',
          type: 'error',
        });
      });

      ws.addEventListener('close', (event) => {
        // Only log abnormal closures
        if (event.code !== 1000 && event.code !== 1001) {
          throttledAddError({
            timestamp: Date.now(),
            message: `WebSocket closed abnormally: Code ${event.code}`,
            source: 'WebSocket',
            type: 'warning',
          });
        }
      });

      return ws;
    };
  }

  // Chrome webview error monitoring
  function setupWebviewErrorMonitoring() {
    const chromeViewport = document.getElementById('chrome-viewport');
    if (chromeViewport) {
      chromeViewport.addEventListener('consolemessage', (event) => {
        if (event.level >= 2) { // Error level
          const error = {
            timestamp: Date.now(),
            message: event.message,
            source: 'Chrome Errors',
            type: event.level === 2 ? 'warning' : 'error',
          };
          addErrorLogEntry(error);
          errorCount.chrome++;
          errorCount.total++;
          updateErrorCounts(errorCount.total, errorCount.chrome, errorCount.app);
        }
      });
    }
  }

  // Efficient global error handlers
  window.addEventListener('error', (event) => {
    // Skip script loading errors and resource errors that are not critical
    if (event.filename && event.filename.includes('chrome-extension://')) return;

    throttledAddError({
      timestamp: Date.now(),
      message: `${event.error?.message || event.message} at ${event.filename}:${event.lineno}`.substring(0, 150),
      source: 'JavaScript Error',
      type: 'error',
    });
  });

  // Efficient promise rejection handler
  window.addEventListener('unhandledrejection', (event) => {
    throttledAddError({
      timestamp: Date.now(),
      message: `Promise Rejection: ${event.reason}`.substring(0, 150),
      source: 'Promise Rejection',
      type: 'error',
    });
  });

  // Lightweight network error monitoring (disabled by default to prevent overhead)
  function setupNetworkErrorMonitoring() {
    // Only monitor critical network failures, not every request
    const criticalFetch = ['localhost', '127.0.0.1', 'chrome-container'];
    const originalFetch = window.fetch;

    window.fetch = function(...args) {
      const url = args[0];
      const isCritical = criticalFetch.some(domain => url.toString().includes(domain));

      if (isCritical) {
        return originalFetch.apply(this, args).catch(error => {
          throttledAddError({
            timestamp: Date.now(),
            message: `Critical network error: ${error.message}`,
            source: 'Network Error',
            type: 'error',
          });
          throw error;
        });
      }
      return originalFetch.apply(this, args);
    };
  }

  // Minimal media error monitoring (only for critical failures)
  function setupMediaErrorMonitoring() {
    // Using passive listeners to reduce overhead
    document.addEventListener('error', (event) => {
      if (event.target && event.target.tagName === 'VIDEO') { // Only videos, not images
        throttledAddError({
          timestamp: Date.now(),
          message: `Video loading failed: ${event.target.src.substring(0, 50)}...`,
          source: 'Media Error',
          type: 'warning',
        });
      }
    }, { passive: true, capture: true });
  }

  // Disabled extension monitoring (too resource intensive)
  function setupExtensionErrorMonitoring() {
    // Disabled to prevent CPU overhead
    return;
  }

  // Minimal performance monitoring (reduced frequency)
  function setupPerformanceMonitoring() {
    // Only monitor critical performance issues
    if (window.performance && window.performance.memory) {
      // Check memory only every 2 minutes to reduce overhead
      setInterval(() => {
        try {
          const memory = window.performance.memory;
          const usedMemory = memory.usedJSHeapSize / memory.jsHeapSizeLimit;
          if (usedMemory > 0.9) { // Only warn at 90% usage
            throttledAddError({
              timestamp: Date.now(),
              message: `Critical memory usage: ${Math.round(usedMemory * 100)}%`,
              source: 'Memory Critical',
              type: 'error',
            });
          }
        } catch {
          // Ignore performance monitoring errors
        }
      }, 120000); // Every 2 minutes
    }
  }

  // Lightweight Electron error monitoring
  function setupElectronErrorMonitoring() {
    // Only monitor critical IPC failures, not all communications
    if (window.electronAPI && window.electronAPI.onError) {
      try {
        window.electronAPI.onError((error) => {
          throttledAddError({
            timestamp: Date.now(),
            message: `IPC Error: ${error.message}`.substring(0, 100),
            source: 'IPC Error',
            type: 'error',
          });
        });
      } catch {
        // Silently ignore if onError not available
      }
    }
  }

  // Initialize all monitoring systems
  captureConsoleErrors();
  setupWebSocketErrorMonitoring();
  setupWebviewErrorMonitoring();
  setupNetworkErrorMonitoring();
  setupMediaErrorMonitoring();
  setupExtensionErrorMonitoring();
  setupPerformanceMonitoring();
  setupElectronErrorMonitoring();

  // Cleanup mechanism to prevent memory leaks
  function cleanupErrorSystem() {
    // Clean old error timestamps every 5 minutes
    setInterval(() => {
      const now = Date.now();
      errorTimestamps = errorTimestamps.filter(t => now - t < 300000); // Keep 5 minutes
      errorBuffer = errorBuffer.filter(e => now - e.timestamp < 10000); // Keep 10 seconds

      // Limit error log DOM elements to prevent memory buildup
      if (errorLog && errorLog.children.length > 50) {
        // Remove oldest entries beyond 50
        while (errorLog.children.length > 50) {
          errorLog.removeChild(errorLog.lastChild);
        }
      }
    }, 300000); // Every 5 minutes
  }

  // Test pipeline error disabled to prevent spam
  // setTimeout(() => {
  //     throttledAddError({
  //         timestamp: Date.now(),
  //         message: 'This is a test error to verify the logging pipeline works',
  //         source: 'Test Pipeline',
  //         type: 'info'
  //     });
  // }, 3000);

  // Legacy onNewErrorLog support (if available)
  if (window.electronAPI && window.electronAPI.onNewErrorLog) {
    try {
      window.electronAPI.onNewErrorLog((error) => {
        throttledAddError({
          timestamp: error.timestamp || Date.now(),
          message: error.message,
          source: error.source || 'External',
          type: error.type || 'error',
        });
      });
      console.log('âœ… Legacy error log listener setup complete');
    } catch (e) {
      console.warn('Legacy error listener setup failed:', e.message);
    }
  }

  // Start cleanup mechanism
  cleanupErrorSystem();

  console.log('âœ… Stable error monitoring system activated');

  // Screenshot functionality setup
  setupScreenshotFeature();

  // Listen for cross-window messages to open planning tab
  window.addEventListener('message', async (event) => {
    console.log('ğŸ“¬ Received message:', event.data);

    if (event.data && event.data.type === 'open-planning-tab') {
      // Switch to planning tab
      const planningTabBtn = document.querySelector('[data-tab="planning"]');
      const planningTabContent = document.getElementById('planning-tab');

      if (planningTabBtn && planningTabContent) {
        // Remove active class from all tabs
        tabButtons.forEach(btn => btn.classList.remove('active'));
        tabContents.forEach(content => content.classList.remove('active'));

        // Activate planning tab
        planningTabBtn.classList.add('active');
        planningTabContent.classList.add('active');

        console.log('âœ… Switched to planning tab');

        // If screenshot data was sent, add it to the collection
        if (event.data.screenshot) {
          const screenshot = event.data.screenshot;
          screenshots.unshift(screenshot);
          renderScreenshots();
          console.log('âœ… Added screenshot from main app');
        }
      }
    }
  });

  // Listen for IPC messages from main process
  if (window.electronAPI) {
    // Set up the listeners
    if (window.electronAPI.onScreenshotCaptured) {
      window.electronAPI.onScreenshotCaptured((screenshot) => {
        console.log('ğŸ“¸ Received screenshot from main process');
        screenshots.unshift(screenshot);
        renderScreenshots();
      });
    }

    if (window.electronAPI.onOpenPlanningTab) {
      window.electronAPI.onOpenPlanningTab(() => {
        console.log('ğŸ“ Received request to open planning tab');
        const planningTabBtn = document.querySelector('[data-tab="planning"]');
        const planningTabContent = document.getElementById('planning-tab');

        if (planningTabBtn && planningTabContent) {
          // Remove active class from all tabs
          tabButtons.forEach(btn => btn.classList.remove('active'));
          tabContents.forEach(content => content.classList.remove('active'));

          // Activate planning tab
          planningTabBtn.classList.add('active');
          planningTabContent.classList.add('active');

          console.log('âœ… Switched to planning tab via IPC');
        }
      });
    }
  }

  // Also check sessionStorage for screenshots from main app
  const checkForExternalScreenshots = () => {
    try {
      const externalScreenshots = JSON.parse(window.sessionStorage.getItem('screenshots') || '[]');
      if (externalScreenshots.length > 0) {
        // Add any new screenshots not already in our collection
        externalScreenshots.forEach(extScreenshot => {
          if (!screenshots.find(s => s.id === extScreenshot.id)) {
            screenshots.unshift(extScreenshot);
          }
        });
        renderScreenshots();
        console.log('âœ… Loaded external screenshots from sessionStorage');
      }
    } catch (error) {
      console.warn('Could not load external screenshots:', error);
    }
  };

  // Check for external screenshots on load and periodically
  checkForExternalScreenshots();
  setInterval(checkForExternalScreenshots, 5000); // Check every 5 seconds

  // Test electronAPI availability
  console.log('ğŸ§ª Testing electronAPI:', {
    electronAPI: !!window.electronAPI,
    testConnection: window.electronAPI ? window.electronAPI.testConnection() : 'not available',
    ensureDir: !!(window.electronAPI && window.electronAPI.ensureDir),
    saveFile: !!(window.electronAPI && window.electronAPI.saveFile),
    captureScreen: !!(window.electronAPI && window.electronAPI.captureScreen),
  });

  // Initialize VS Code Terminal Manager
  initializeVSCodeTerminalManager();

  console.log('âœ… App.js initialization complete with all UI connections');
});

// VS Code Terminal Manager Implementation
class VSCodeTerminalManager {
  constructor() {
    this.terminals = new Map();
    this.activeTerminalId = null;
    this.nextTerminalId = 1;
    this.terminalContainer = document.getElementById('terminal-container');
    this.tabBar = document.getElementById('terminal-tab-bar');
    this.dropdownMenu = document.getElementById('terminal-dropdown-menu');

    this.setupEventListeners();
    this.createInitialTerminal();
  }

  setupEventListeners() {
    // New terminal button
    const newTerminalBtn = document.getElementById('new-terminal-btn');
    if (newTerminalBtn) {
      newTerminalBtn.addEventListener('click', () => this.createNewTerminal());
    }

    // Split terminal button
    const splitTerminalBtn = document.getElementById('split-terminal-btn');
    if (splitTerminalBtn) {
      splitTerminalBtn.addEventListener('click', () => this.splitActiveTerminal());
    }

    // Dropdown menu
    const dropdownBtn = document.getElementById('terminal-dropdown-btn');
    if (dropdownBtn) {
      dropdownBtn.addEventListener('click', () => this.toggleDropdown());
    }

    // Dropdown menu items
    if (this.dropdownMenu) {
      this.dropdownMenu.addEventListener('click', (e) => {
        const item = e.target.closest('.dropdown-item');
        if (item) {
          const action = item.dataset.action;
          this.handleDropdownAction(action);
          this.hideDropdown();
        }
      });
    }

    // Click outside to close dropdown
    document.addEventListener('click', (e) => {
      if (!e.target.closest('.terminal-dropdown-menu') && !e.target.closest('#terminal-dropdown-btn')) {
        this.hideDropdown();
      }
    });

    // Keyboard shortcuts
    document.addEventListener('keydown', (e) => {
      if (e.ctrlKey && e.shiftKey) {
        if (e.code === 'Backquote') { // Ctrl+Shift+`
          e.preventDefault();
          this.createNewTerminal();
        } else if (e.code === 'Digit5') { // Ctrl+Shift+5
          e.preventDefault();
          this.splitActiveTerminal();
        }
      }
    });
  }

  async createInitialTerminal() {
    // Replace the existing terminal with our managed one
    await this.createNewTerminal(1);
  }

  async createNewTerminal(terminalId = null) {
    const id = terminalId || this.nextTerminalId++;

    try {
      // Create terminal instance
      const Terminal = window.Terminal || window.electronAPI?.Terminal;
      const FitAddon = window.FitAddon?.FitAddon || window.FitAddon || window.electronAPI?.FitAddon;

      if (!Terminal || !FitAddon) {
        throw new Error('Terminal libraries not available');
      }

      const terminal = new Terminal({
        cursorBlink: true,
        cursorStyle: 'bar',
        cursorWidth: 2,
        fontFamily: '\'Cascadia Code\', \'SF Mono\', Monaco, \'Inconsolata\', \'Roboto Mono\', \'Source Code Pro\', monospace',
        fontSize: 14,
        lineHeight: 1.4,
        theme: {
          background: '#181818',
          foreground: '#cccccc',
          cursor: '#cccccc',
          cursorAccent: '#181818',
          selection: '#3a3d41',
          black: '#000000',
          brightBlack: '#666666',
          red: '#cd3131',
          brightRed: '#f14c4c',
          green: '#0dbc79',
          brightGreen: '#23d18b',
          yellow: '#e5e510',
          brightYellow: '#f5f543',
          blue: '#2472c8',
          brightBlue: '#3b8eea',
          magenta: '#bc3fbc',
          brightMagenta: '#d670d6',
          cyan: '#11a8cd',
          brightCyan: '#29b8db',
          white: '#e5e5e5',
          brightWhite: '#ffffff',
        },
        allowProposedApi: true,
        cols: 80,
        rows: 24,
      });

      const fitAddon = new FitAddon();
      terminal.loadAddon(fitAddon);

      // Create DOM elements
      this.createTerminalElements(id, terminal, fitAddon);

      // Store terminal data
      this.terminals.set(id, {
        terminal,
        fitAddon,
        process: null,
        title: 'bash',
        isRunning: false,
      });

      // Set as active
      this.setActiveTerminal(id);

      // Start terminal process
      await this.startTerminalProcess(id);

      console.log(`âœ… Created terminal ${id}`);
      return id;

    } catch (error) {
      console.error(`âŒ Failed to create terminal ${id}:`, error);
      throw error;
    }
  }

  createTerminalElements(id, terminal, fitAddon) {
    // Create tab
    const tab = document.createElement('div');
    tab.className = 'terminal-tab';
    tab.dataset.terminalId = id;
    tab.innerHTML = `
      <span class="tab-icon">âš¡</span>
      <span class="tab-title">bash</span>
      <button class="tab-close-btn" title="Close Terminal">Ã—</button>
    `;

    // Add tab event listeners
    tab.addEventListener('click', (e) => {
      if (!e.target.classList.contains('tab-close-btn')) {
        this.setActiveTerminal(id);
      }
    });

    const closeBtn = tab.querySelector('.tab-close-btn');
    closeBtn.addEventListener('click', (e) => {
      e.stopPropagation();
      this.closeTerminal(id);
    });

    this.tabBar.appendChild(tab);

    // Create terminal instance container
    const instance = document.createElement('div');
    instance.className = 'terminal-instance';
    instance.id = `terminal-${id}`;
    instance.dataset.terminalId = id;

    const wrapper = document.createElement('div');
    wrapper.className = 'terminal-wrapper';

    const xtermContainer = document.createElement('div');
    xtermContainer.className = 'xterm-container';
    xtermContainer.id = `xterm-${id}`;

    wrapper.appendChild(xtermContainer);
    instance.appendChild(wrapper);
    this.terminalContainer.appendChild(instance);

    // Open terminal in container
    terminal.open(xtermContainer);

    // Fit terminal
    setTimeout(() => {
      fitAddon.fit();
    }, 100);

    // Handle resize - with ResizeObserver check
    if (typeof ResizeObserver !== 'undefined') {
      const resizeObserver = new ResizeObserver(() => {
        fitAddon.fit();
      });
      resizeObserver.observe(xtermContainer);
    } else {
      // Fallback for browsers without ResizeObserver
      window.addEventListener('resize', () => {
        fitAddon.fit();
      });
    }
  }

  async startTerminalProcess(id) {
    const terminalData = this.terminals.get(id);
    if (!terminalData || !window.electronAPI) return;

    try {
      // Create terminal process in main process
      const result = await window.electronAPI.createTerminal(id);
      if (!result.success) {
        throw new Error(result.error);
      }

      // Set up terminal data handlers
      if (window.electronAPI.onTerminalData) {
        window.electronAPI.onTerminalData((data) => {
          if (data.terminalId === id) {
            terminalData.terminal.write(data.data);
          }
        });
      }

      // Set up terminal input
      terminalData.terminal.onData((data) => {
        if (window.electronAPI.writeToTerminal) {
          window.electronAPI.writeToTerminal(data, id);
        }
      });

      // Set up resize handler
      terminalData.terminal.onResize((size) => {
        if (window.electronAPI.resizeTerminal) {
          window.electronAPI.resizeTerminal(size.cols, size.rows, id);
        }
      });

      // Set up exit handler
      if (window.electronAPI.onTerminalExit) {
        window.electronAPI.onTerminalExit((data) => {
          if (data.terminalId === id) {
            console.log(`Terminal ${id} exited with code ${data.exitCode}`);
            this.handleTerminalExit(id, data.exitCode);
          }
        });
      }

      terminalData.isRunning = true;
      terminalData.pid = result.pid;
      console.log(`âœ… Started terminal process for ${id} (PID: ${result.pid})`);

    } catch (error) {
      console.error(`âŒ Failed to start terminal process for ${id}:`, error);
    }
  }

  setActiveTerminal(id) {
    // Remove active class from all tabs and instances
    this.tabBar.querySelectorAll('.terminal-tab').forEach(tab => {
      tab.classList.remove('active');
    });
    this.terminalContainer.querySelectorAll('.terminal-instance').forEach(instance => {
      instance.classList.remove('active');
    });

    // Set active terminal
    const tab = this.tabBar.querySelector(`[data-terminal-id="${id}"]`);
    const instance = this.terminalContainer.querySelector(`[data-terminal-id="${id}"]`);

    if (tab && instance) {
      tab.classList.add('active');
      instance.classList.add('active');
      this.activeTerminalId = id;

      // Focus terminal
      const terminalData = this.terminals.get(id);
      if (terminalData) {
        terminalData.terminal.focus();
      }
    }
  }

  closeTerminal(id) {
    const terminalData = this.terminals.get(id);
    if (!terminalData) return;

    // Clean up terminal
    terminalData.terminal.dispose();

    // Remove DOM elements
    const tab = this.tabBar.querySelector(`[data-terminal-id="${id}"]`);
    const instance = this.terminalContainer.querySelector(`[data-terminal-id="${id}"]`);

    if (tab) tab.remove();
    if (instance) instance.remove();

    // Remove from map
    this.terminals.delete(id);

    // If this was the active terminal, activate another one
    if (this.activeTerminalId === id) {
      const remainingTerminals = Array.from(this.terminals.keys());
      if (remainingTerminals.length > 0) {
        this.setActiveTerminal(remainingTerminals[0]);
      } else {
        this.activeTerminalId = null;
      }
    }

    console.log(`âœ… Closed terminal ${id}`);
  }


  toggleDropdown() {
    const isVisible = this.dropdownMenu.style.display !== 'none';
    if (isVisible) {
      this.hideDropdown();
    } else {
      this.showDropdown();
    }
  }

  showDropdown() {
    this.dropdownMenu.style.display = 'block';
  }

  hideDropdown() {
    this.dropdownMenu.style.display = 'none';
  }

  handleDropdownAction(action) {
    switch (action) {
    case 'new-terminal':
      this.createNewTerminal();
      break;
    case 'split-terminal':
      this.splitActiveTerminal();
      break;
    case 'kill-all':
      this.killAllTerminals();
      break;
    }
  }

  splitActiveTerminal() {
    if (!this.activeTerminalId) {
      console.warn('No active terminal to split');
      return;
    }

    const activeTerminal = this.terminals.get(this.activeTerminalId);
    if (!activeTerminal) {
      console.warn('Active terminal not found');
      return;
    }

    // Create new terminal instance
    const newTerminalId = this.nextTerminalId++;
    const newTitle = `bash (${newTerminalId})`;

    // Add tab for new terminal
    this.addTerminalTab(newTerminalId, newTitle);

    // Get current terminal container
    const currentContainer = document.getElementById(`terminal-${this.activeTerminalId}`);

    // Create split container if not exists
    let splitContainer = currentContainer.querySelector('.terminal-split-container');
    if (!splitContainer) {
      splitContainer = document.createElement('div');
      splitContainer.className = 'terminal-split-container';
      splitContainer.style.cssText = 'display: flex; height: 100%; width: 100%;';

      // Move current terminal into split container
      const currentWrapper = currentContainer.querySelector('.terminal-wrapper');
      currentWrapper.style.cssText = 'flex: 1; border-right: 1px solid #3c3c3c;';
      splitContainer.appendChild(currentWrapper);
      currentContainer.appendChild(splitContainer);
    }

    // Create new terminal instance
    const newTerminalInstance = document.createElement('div');
    newTerminalInstance.className = 'terminal-instance';
    newTerminalInstance.id = `terminal-${newTerminalId}`;
    newTerminalInstance.dataset.terminalId = newTerminalId;
    newTerminalInstance.style.display = 'none';

    const newWrapper = document.createElement('div');
    newWrapper.className = 'terminal-wrapper';
    newWrapper.style.cssText = 'flex: 1; border-left: 1px solid #3c3c3c;';

    const newXtermContainer = document.createElement('div');
    newXtermContainer.className = 'xterm-container';
    newXtermContainer.id = `xterm-${newTerminalId}`;

    newWrapper.appendChild(newXtermContainer);
    newTerminalInstance.appendChild(newWrapper);
    this.terminalContainer.appendChild(newTerminalInstance);

    // Add new terminal wrapper to split container
    splitContainer.appendChild(newWrapper);

    // Initialize new terminal
    this.initializeTerminal(newTerminalId, newTitle);

    // Switch to new terminal
    this.switchToTerminal(newTerminalId);

    console.log(`Terminal split: created terminal ${newTerminalId} from ${this.activeTerminalId}`);
  }

  killAllTerminals() {
    const terminalIds = Array.from(this.terminals.keys());
    terminalIds.forEach(id => this.closeTerminal(id));
  }
}

// Initialize the terminal manager
function initializeVSCodeTerminalManager() {
  // Wait for xterm to be available
  const checkXterm = () => {
    if (window.Terminal && (window.FitAddon || window.FitAddon?.FitAddon)) {
      new VSCodeTerminalManager();
    } else {
      setTimeout(checkXterm, 100);
    }
  };
  checkXterm();
}

