        console.log('âœ… Switched to planning tab');

        // If screenshot data was sent, add it to the collection
        if (event.data.screenshot) {
          const screenshot = event.data.screenshot;
          screenshots.unshift(screenshot);
          renderScreenshots();
          console.log('âœ… Added screenshot from main app');
        }
      }
    }
  });

  // Listen for IPC messages from main process
  if (window.electronAPI) {
    // Set up the listeners
    if (window.electronAPI.onScreenshotCaptured) {
      window.electronAPI.onScreenshotCaptured((screenshot) => {
        console.log('ğŸ“¸ Received screenshot from main process');
        screenshots.unshift(screenshot);
        renderScreenshots();
      });
    }

    if (window.electronAPI.onOpenPlanningTab) {
      window.electronAPI.onOpenPlanningTab(() => {
        console.log('ğŸ“ Received request to open planning tab');
        const planningTabBtn = document.querySelector('[data-tab="planning"]');
        const planningTabContent = document.getElementById('planning-tab');

        if (planningTabBtn && planningTabContent) {
          // Remove active class from all tabs
          tabButtons.forEach(btn => btn.classList.remove('active'));
          tabContents.forEach(content => content.classList.remove('active'));

          // Activate planning tab
          planningTabBtn.classList.add('active');
          planningTabContent.classList.add('active');

          console.log('âœ… Switched to planning tab via IPC');
        }
      });
    }
  }

  // Also check sessionStorage for screenshots from main app
  const checkForExternalScreenshots = () => {
    try {
      const externalScreenshots = JSON.parse(window.sessionStorage.getItem('screenshots') || '[]');
      if (externalScreenshots.length > 0) {
        // Add any new screenshots not already in our collection
        externalScreenshots.forEach(extScreenshot => {
          if (!screenshots.find(s => s.id === extScreenshot.id)) {
            screenshots.unshift(extScreenshot);
          }
        });
        renderScreenshots();
        console.log('âœ… Loaded external screenshots from sessionStorage');
      }
    } catch (error) {
      console.warn('Could not load external screenshots:', error);
    }
  };

  // Check for external screenshots on load and periodically
  checkForExternalScreenshots();
  setInterval(checkForExternalScreenshots, 5000); // Check every 5 seconds

  // Test electronAPI availability
  console.log('ğŸ§ª Testing electronAPI:', {
    electronAPI: !!window.electronAPI,
    testConnection: window.electronAPI ? window.electronAPI.testConnection() : 'not available',
    ensureDir: !!(window.electronAPI && window.electronAPI.ensureDir),
    saveFile: !!(window.electronAPI && window.electronAPI.saveFile),
    captureScreen: !!(window.electronAPI && window.electronAPI.captureScreen),
  });

  // Initialize VS Code Terminal Manager
  initializeVSCodeTerminalManager();

  console.log('âœ… App.js initialization complete with all UI connections');
});

// VS Code Terminal Manager Implementation
class VSCodeTerminalManager {
  constructor() {
    this.terminals = new Map();
    this.activeTerminalId = null;
    this.nextTerminalId = 1;
    this.terminalContainer = document.getElementById('terminal-container');
    this.tabBar = document.getElementById('terminal-tab-bar');
    this.dropdownMenu = document.getElementById('terminal-dropdown-menu');

    this.setupEventListeners();
    this.createInitialTerminal();
  }

  setupEventListeners() {
    // New terminal button
    const newTerminalBtn = document.getElementById('new-terminal-btn');
    if (newTerminalBtn) {
      newTerminalBtn.addEventListener('click', () => this.createNewTerminal());
    }

    // Split terminal button
    const splitTerminalBtn = document.getElementById('split-terminal-btn');
    if (splitTerminalBtn) {
      splitTerminalBtn.addEventListener('click', () => this.splitActiveTerminal());
    }

    // Dropdown menu
    const dropdownBtn = document.getElementById('terminal-dropdown-btn');
    if (dropdownBtn) {
      dropdownBtn.addEventListener('click', () => this.toggleDropdown());
    }

    // Dropdown menu items
    if (this.dropdownMenu) {
      this.dropdownMenu.addEventListener('click', (e) => {
        const item = e.target.closest('.dropdown-item');
        if (item) {
          const action = item.dataset.action;
          this.handleDropdownAction(action);
          this.hideDropdown();
        }
      });
    }

    // Click outside to close dropdown
    document.addEventListener('click', (e) => {
      if (!e.target.closest('.terminal-dropdown-menu') && !e.target.closest('#terminal-dropdown-btn')) {
        this.hideDropdown();
      }
    });

    // Keyboard shortcuts
    document.addEventListener('keydown', (e) => {
      if (e.ctrlKey && e.shiftKey) {
        if (e.code === 'Backquote') { // Ctrl+Shift+`
          e.preventDefault();
          this.createNewTerminal();
        } else if (e.code === 'Digit5') { // Ctrl+Shift+5
          e.preventDefault();
          this.splitActiveTerminal();
        }
      }
    });
  }

  async createInitialTerminal() {
    // Replace the existing terminal with our managed one
    await this.createNewTerminal(1);
  }

  async createNewTerminal(terminalId = null) {
    const id = terminalId || this.nextTerminalId++;

    try {
      // Create terminal instance
      const Terminal = window.Terminal || window.electronAPI?.Terminal;
      const FitAddon = window.FitAddon?.FitAddon || window.FitAddon || window.electronAPI?.FitAddon;

      if (!Terminal || !FitAddon) {
        throw new Error('Terminal libraries not available');
      }

      const terminal = new Terminal({
        cursorBlink: true,
        cursorStyle: 'bar',
        cursorWidth: 2,
        fontFamily: '\'Cascadia Code\', \'SF Mono\', Monaco, \'Inconsolata\', \'Roboto Mono\', \'Source Code Pro\', monospace',
        fontSize: 14,
        lineHeight: 1.4,
        theme: {
          background: '#181818',
          foreground: '#cccccc',
          cursor: '#cccccc',
          cursorAccent: '#181818',
          selection: '#3a3d41',
          black: '#000000',
          brightBlack: '#666666',
          red: '#cd3131',
          brightRed: '#f14c4c',
          green: '#0dbc79',
          brightGreen: '#23d18b',
          yellow: '#e5e510',
          brightYellow: '#f5f543',
          blue: '#2472c8',
          brightBlue: '#3b8eea',
          magenta: '#bc3fbc',
          brightMagenta: '#d670d6',
          cyan: '#11a8cd',
          brightCyan: '#29b8db',
          white: '#e5e5e5',
          brightWhite: '#ffffff',
        },
        allowProposedApi: true,
        cols: 80,
        rows: 24,
      });

      const fitAddon = new FitAddon();
      terminal.loadAddon(fitAddon);

      // Create DOM elements
      this.createTerminalElements(id, terminal, fitAddon);

      // Store terminal data
      this.terminals.set(id, {
        terminal,
        fitAddon,
        process: null,
        title: 'bash',
        isRunning: false,
      });

      // Set as active
      this.setActiveTerminal(id);

      // Start terminal process
      await this.startTerminalProcess(id);

      console.log(`âœ… Created terminal ${id}`);
      return id;

    } catch (error) {
      console.error(`âŒ Failed to create terminal ${id}:`, error);
      throw error;
    }
  }

  createTerminalElements(id, terminal, fitAddon) {
    // Create tab
    const tab = document.createElement('div');
    tab.className = 'terminal-tab';
    tab.dataset.terminalId = id;
    tab.innerHTML = `
      <span class="tab-icon">âš¡</span>
      <span class="tab-title">bash</span>
      <button class="tab-close-btn" title="Close Terminal">Ã—</button>
    `;

    // Add tab event listeners
    tab.addEventListener('click', (e) => {
      if (!e.target.classList.contains('tab-close-btn')) {
        this.setActiveTerminal(id);
      }
    });

    const closeBtn = tab.querySelector('.tab-close-btn');
    closeBtn.addEventListener('click', (e) => {
      e.stopPropagation();
      this.closeTerminal(id);
    });

    this.tabBar.appendChild(tab);

    // Create terminal instance container
    const instance = document.createElement('div');
    instance.className = 'terminal-instance';
    instance.id = `terminal-${id}`;
    instance.dataset.terminalId = id;

    const wrapper = document.createElement('div');
    wrapper.className = 'terminal-wrapper';

    const xtermContainer = document.createElement('div');
    xtermContainer.className = 'xterm-container';
    xtermContainer.id = `xterm-${id}`;

    wrapper.appendChild(xtermContainer);
    instance.appendChild(wrapper);
    this.terminalContainer.appendChild(instance);

    // Open terminal in container
    terminal.open(xtermContainer);

    // Fit terminal
    setTimeout(() => {
      fitAddon.fit();
    }, 100);

    // Handle resize - with ResizeObserver check
    if (typeof ResizeObserver !== 'undefined') {
      const resizeObserver = new ResizeObserver(() => {
        fitAddon.fit();
      });
      resizeObserver.observe(xtermContainer);
    } else {
      // Fallback for browsers without ResizeObserver
      window.addEventListener('resize', () => {
        fitAddon.fit();
      });
    }
  }

  async startTerminalProcess(id) {
    const terminalData = this.terminals.get(id);
    if (!terminalData || !window.electronAPI) return;

    try {
      // Create terminal process in main process
      const result = await window.electronAPI.createTerminal(id);
      if (!result.success) {
        throw new Error(result.error);
      }

      // Set up terminal data handlers
      if (window.electronAPI.onTerminalData) {
        window.electronAPI.onTerminalData((data) => {
          if (data.terminalId === id) {
            terminalData.terminal.write(data.data);
          }
        });
      }

      // Set up terminal input
      terminalData.terminal.onData((data) => {
        if (window.electronAPI.writeToTerminal) {
          window.electronAPI.writeToTerminal(data, id);
        }
      });

      // Set up resize handler
      terminalData.terminal.onResize((size) => {
        if (window.electronAPI.resizeTerminal) {
          window.electronAPI.resizeTerminal(size.cols, size.rows, id);
        }
      });

      // Set up exit handler
      if (window.electronAPI.onTerminalExit) {
        window.electronAPI.onTerminalExit((data) => {
          if (data.terminalId === id) {
            console.log(`Terminal ${id} exited with code ${data.exitCode}`);
            this.handleTerminalExit(id, data.exitCode);
          }
        });
      }

      terminalData.isRunning = true;
      terminalData.pid = result.pid;
      console.log(`âœ… Started terminal process for ${id} (PID: ${result.pid})`);

    } catch (error) {
      console.error(`âŒ Failed to start terminal process for ${id}:`, error);
    }
  }

  handleTerminalExit(id, exitCode) {
    const terminalData = this.terminals.get(id);
    if (terminalData) {
      terminalData.isRunning = false;
      terminalData.pid = null;

      // Update tab appearance to show terminal is dead
      const tab = this.tabBar.querySelector(`[data-terminal-id="${id}"]`);
      if (tab) {
        tab.classList.add('terminal-dead');
        const tabTitle = tab.querySelector('.tab-title');
        if (tabTitle) {
          tabTitle.textContent = `bash (dead)`;
        }
      }

      // If this was the active terminal and it died with an error, auto-restart
      if (this.activeTerminalId === id && exitCode !== 0) {
        setTimeout(() => {
          this.startTerminalProcess(id);
        }, 1000);
      }
    }
  }

  setActiveTerminal(id) {
    // Remove active class from all tabs and instances
    this.tabBar.querySelectorAll('.terminal-tab').forEach(tab => {
      tab.classList.remove('active');
    });
    this.terminalContainer.querySelectorAll('.terminal-instance').forEach(instance => {
      instance.classList.remove('active');
    });

    // Set active terminal
    const tab = this.tabBar.querySelector(`[data-terminal-id="${id}"]`);
    const instance = this.terminalContainer.querySelector(`[data-terminal-id="${id}"]`);

    if (tab && instance) {
      tab.classList.add('active');
      instance.classList.add('active');
      this.activeTerminalId = id;

      // Focus terminal
      const terminalData = this.terminals.get(id);
      if (terminalData) {
        terminalData.terminal.focus();
      }
    }
  }

  closeTerminal(id) {
    const terminalData = this.terminals.get(id);
    if (!terminalData) return;

    // Kill terminal process in main process
    if (window.electronAPI.closeTerminal) {
      window.electronAPI.closeTerminal(id);
    }

    // Clean up terminal
    terminalData.terminal.dispose();

    // Remove DOM elements
    const tab = this.tabBar.querySelector(`[data-terminal-id="${id}"]`);
    const instance = this.terminalContainer.querySelector(`[data-terminal-id="${id}"]`);

    if (tab) tab.remove();
    if (instance) instance.remove();

    // Remove from map
    this.terminals.delete(id);

    // If this was the active terminal, activate another one
    if (this.activeTerminalId === id) {
      const remainingTerminals = Array.from(this.terminals.keys());
      if (remainingTerminals.length > 0) {
        this.setActiveTerminal(remainingTerminals[0]);
      } else {
        this.activeTerminalId = null;
      }
    }

    console.log(`âœ… Closed terminal ${id}`);
  }


  toggleDropdown() {
    const isVisible = this.dropdownMenu.style.display !== 'none';
    if (isVisible) {
      this.hideDropdown();
    } else {
      this.showDropdown();
    }
  }

  showDropdown() {
    this.dropdownMenu.style.display = 'block';
  }

  hideDropdown() {
    this.dropdownMenu.style.display = 'none';
  }

  handleDropdownAction(action) {
    switch (action) {
    case 'new-terminal':
      this.createNewTerminal();
      break;
    case 'split-terminal':
      this.splitActiveTerminal();
      break;
    case 'kill-all':
      this.killAllTerminals();
      break;
    }
  }

  splitActiveTerminal() {
    if (!this.activeTerminalId) {
      console.warn('No active terminal to split');
      return;
    }

    const activeTerminal = this.terminals.get(this.activeTerminalId);
    if (!activeTerminal) {
      console.warn('Active terminal not found');
      return;
    }

    // Create new terminal instance
    const newTerminalId = this.nextTerminalId++;
    const newTitle = `bash (${newTerminalId})`;

    // Add tab for new terminal
    this.addTerminalTab(newTerminalId, newTitle);

    // Get current terminal container
    const currentContainer = document.getElementById(`terminal-${this.activeTerminalId}`);

    // Create split container if not exists
    let splitContainer = currentContainer.querySelector('.terminal-split-container');
    if (!splitContainer) {
      splitContainer = document.createElement('div');
      splitContainer.className = 'terminal-split-container';
      splitContainer.style.cssText = 'display: flex; height: 100%; width: 100%;';

      // Move current terminal into split container
      const currentWrapper = currentContainer.querySelector('.terminal-wrapper');
      currentWrapper.style.cssText = 'flex: 1; border-right: 1px solid #3c3c3c;';
      splitContainer.appendChild(currentWrapper);
      currentContainer.appendChild(splitContainer);
    }

    // Create new terminal instance
    const newTerminalInstance = document.createElement('div');
    newTerminalInstance.className = 'terminal-instance';
    newTerminalInstance.id = `terminal-${newTerminalId}`;
    newTerminalInstance.dataset.terminalId = newTerminalId;
    newTerminalInstance.style.display = 'none';

    const newWrapper = document.createElement('div');
    newWrapper.className = 'terminal-wrapper';
    newWrapper.style.cssText = 'flex: 1; border-left: 1px solid #3c3c3c;';

    const newXtermContainer = document.createElement('div');
    newXtermContainer.className = 'xterm-container';
    newXtermContainer.id = `xterm-${newTerminalId}`;

    newWrapper.appendChild(newXtermContainer);
    newTerminalInstance.appendChild(newWrapper);
    this.terminalContainer.appendChild(newTerminalInstance);

    // Add new terminal wrapper to split container
    splitContainer.appendChild(newWrapper);

    // Initialize new terminal
    this.initializeTerminal(newTerminalId, newTitle);

    // Switch to new terminal
    this.switchToTerminal(newTerminalId);

    console.log(`Terminal split: created terminal ${newTerminalId} from ${this.activeTerminalId}`);
  }

  killAllTerminals() {
    const terminalIds = Array.from(this.terminals.keys());
    terminalIds.forEach(id => this.closeTerminal(id));
  }
}

// Initialize the terminal manager
function initializeVSCodeTerminalManager() {
  // Wait for xterm to be available
  const checkXterm = () => {
    if (window.Terminal && (window.FitAddon || window.FitAddon?.FitAddon)) {
      new VSCodeTerminalManager();
    } else {
      setTimeout(checkXterm, 100);
    }
  };
  checkXterm();
}

