        actions = ActionChains(self.driver)

        # Add natural human-like curve and variability
        for i in range(steps):
            progress = i / steps

            # Bezier curve for natural movement
            control_x = (start_x + end_x) / 2 + random.randint(-20, 20)
            control_y = (start_y + end_y) / 2 + random.randint(-20, 20)

            # Calculate position with curve
            t = progress
            x = (1-t)**2 * start_x + 2*(1-t)*t * control_x + t**2 * end_x
            y = (1-t)**2 * start_y + 2*(1-t)*t * control_y + t**2 * end_y

            # Add micro-jitter like real humans
            x += random.uniform(-1, 1)
            y += random.uniform(-1, 1)

            # Variable speed - humans don't move at constant velocity
            sleep_time = random.uniform(0.005, 0.02)

            actions.move_to_element_with_offset(self.driver.find_element('tag name', 'body'), int(x), int(y))
            actions.pause(sleep_time)

        actions.perform()

