    def inject_snape_enhancements(self):
        """Connect to existing Unified Diagnostics and enhance for SNAPE testing"""
        diagnostic_script = """
        // SNAPE Enhancement Layer for Existing Unified Diagnostics
        window.SNAPEDiagnostics = {
            unifiedSystem: window.unifiedDiagnostics, // Connect to existing system

            data: {
                consoleLogs: [],
                performanceEntries: [],
                errorTraces: [],
                stateSnapshots: [],
                functionTimings: new Map(),
                memoryUsage: [],
                unifiedCaptures: []
            },

            init() {
                // Check connection to Unified Diagnostics
                if (!this.unifiedSystem) {
                    console.warn('âš ï¸ Unified Diagnostics System not found - checking alternatives...');

                    // Check for alternative references
                    this.unifiedSystem = window.canvasStateDiag || window.diagnostics;

                    if (!this.unifiedSystem) {
                        console.error('âŒ No Unified Diagnostics System available - creating minimal fallback');
                        this.createMinimalFallback();
                    }
                } else {
                    console.log('âœ… Successfully connected to Unified Diagnostics System');
                    console.log('ğŸ”— Available methods:', Object.keys(this.unifiedSystem));
                }

                this.enhanceUnifiedSystem();
                this.startSNAPEMonitoring();
                console.log('ğŸš€ SNAPE Enhancement Layer Active');
            },

            createMinimalFallback() {
                this.unifiedSystem = {
                    log: (type, data) => {
                        console.log(`FALLBACK[${type}]:`, data);
                        this.data.consoleLogs.push({type, data, timestamp: Date.now()});
                    },
                    captureFullState: () => {
                        const state = {
                            fallback: true, 
                            timestamp: Date.now(),
                            dom: {
                                shapes: document.querySelectorAll('.tl-shape').length,
                                canvases: document.querySelectorAll('canvas').length
                            }
                        };
                        console.log('ğŸ“Š Fallback state capture:', state);
                        return state;
                    },
                    simulateThreeFingerTouch: (type, pos) => {
                        console.log(`ğŸ–ï¸ Fallback three-finger ${type}:`, pos);
                        this.data.consoleLogs.push({
                            type: 'three_finger_fallback',
                            action: type,
                            position: pos,
                            timestamp: Date.now()
                        });
                    }
                };
            },

            enhanceUnifiedSystem() {
                // Add SNAPE-specific logging to unified system
                if (this.unifiedSystem && this.unifiedSystem.log) {
                    this.unifiedSystem.log('SNAPE_INIT', {
                        message: 'SNAPE diagnostics connected to unified system',
                        timestamp: Date.now(),
                        version: '2.0'
                    });
                }

                // Monitor existing unified system calls
                if (this.unifiedSystem && this.unifiedSystem.captureFullState) {
                    console.log('ğŸ”§ Enhanced state capture available');
                }

                if (this.unifiedSystem && this.unifiedSystem.simulateThreeFingerTouch) {
                    console.log('ğŸ–ï¸ Three-finger touch simulation available');
                }
            },

            startSNAPEMonitoring() {
                // Monitor performance
                if (window.PerformanceObserver) {
                    const observer = new PerformanceObserver((list) => {
                        this.data.performanceEntries.push(...list.getEntries());
                    });
                    observer.observe({entryTypes: ['measure', 'navigation', 'paint']});
                }

                // Memory monitoring
                this.startMemoryMonitoring();

                // Function timing for TLDraw
                this.monitorTLDrawFunctions();
            },

            monitorTLDrawFunctions() {
                if (window.tlEditor) {
                    const methods = ['updateShapes', 'createShape', 'deleteShapes', 'selectShape'];
                    methods.forEach(method => {
                        if (window.tlEditor[method]) {
                            const original = window.tlEditor[method];
                            window.tlEditor[method] = (...args) => {
                                const start = performance.now();
                                const result = original.apply(window.tlEditor, args);
                                const duration = performance.now() - start;
                                this.data.functionTimings.set(method, duration);

                                if (duration > 10) {
                                    console.warn(`âš ï¸ Slow TLDraw function: ${method} took ${duration}ms`);
                                    // Log to unified system
                                    if (this.unifiedSystem.log) {
                                        this.unifiedSystem.log('SLOW_FUNCTION', {method, duration});
                                    }
                                }
                                return result;
                            };
                        }
                    });
                }
            },

            startMemoryMonitoring() {
                setInterval(() => {
                    if (performance.memory) {
                        this.data.memoryUsage.push({
                            timestamp: Date.now(),
                            used: performance.memory.usedJSHeapSize,
                            total: performance.memory.totalJSHeapSize,
                            limit: performance.memory.jsHeapSizeLimit
                        });
                    }
                }, 1000);
            },

            captureState(action, context = {}) {
                const snapshot = {
                    timestamp: performance.now(),
                    action: action,
                    context: context,
                    snape: {
                        dom: {
                            shapes: document.querySelectorAll('.tl-shape').length,
                            canvases: document.querySelectorAll('canvas').length,
                            activeElement: document.activeElement?.tagName,
                            scrollPosition: {x: window.scrollX, y: window.scrollY}
                        },
                        editor: {},
                        memory: performance.memory ? {
                            used: performance.memory.usedJSHeapSize,
                            total: performance.memory.totalJSHeapSize
                        } : null
                    },
                    unified: null,
                    coordinates: context.coordinates || null
                };

                // Enhanced state tracking for coordinate analysis
                if (context.coordinates && context.action_type) {
                    // Track this as a state change triggered by coordinate action
                    const stateChangeData = {
                        dom_changes: {
                            shape_count_delta: this.lastShapeCount ? (snapshot.snape.dom.shapes - this.lastShapeCount) : 0,
                            scroll_delta: this.lastScrollPosition ? 
                                (snapshot.snape.dom.scrollPosition.y - this.lastScrollPosition.y) : 0
                        },
                        editor_changes: {},
                        menu_state: document.querySelector('.context-menu') ? 'visible' : 'hidden'
                    };

                    // Store for coordinate correlation
                    if (!window.coordinateStateTracker) {
                        window.coordinateStateTracker = {
                            actions: [],
                            states: [],
                            mappings: []
                        };
                    }

                    window.coordinateStateTracker.states.push({
                        timestamp: snapshot.timestamp,
                        state_type: action,
                        change_data: stateChangeData,
                        triggered_by_coordinates: context.coordinates
                    });
                }

                // Update tracking data
                this.lastShapeCount = snapshot.snape.dom.shapes;
                this.lastScrollPosition = snapshot.snape.dom.scrollPosition;

                // Capture from Unified Diagnostics System
                if (this.unifiedSystem && this.unifiedSystem.captureFullState) {
                    try {
                        snapshot.unified = this.unifiedSystem.captureFullState();
                        console.log('ğŸ”— Captured from Unified Diagnostics');
                        this.data.unifiedCaptures.push(snapshot.unified);
                    } catch (e) {
                        console.warn('âš ï¸ Failed to capture from Unified Diagnostics:', e.message);
                        snapshot.unified = {error: e.message};
                    }
                } else {
                    console.warn('âš ï¸ Unified Diagnostics captureFullState not available');
                }

                // Capture TLDraw editor state
                try {
                    if (window.tlEditor) {
                        snapshot.snape.editor = {
                            camera: window.tlEditor.getCamera(),
                            selectedShapes: window.tlEditor.getSelectedShapes().length,
                            currentTool: window.tlEditor.getCurrentTool?.()?.id
                        };
                    }
                } catch (e) {
                    snapshot.snape.editor.error = e.message;
                }

                this.data.stateSnapshots.push(snapshot);

                // Log to Unified Diagnostics System
                if (this.unifiedSystem && this.unifiedSystem.log) {
                    this.unifiedSystem.log('SNAPE_STATE_CAPTURE', {action, timestamp: snapshot.timestamp});
                }

                console.log(`ğŸ“Š SNAPE Enhanced State captured: ${action}`);
                return snapshot;
            },

            generateReport() {
                const report = {
                    summary: {
                        totalLogs: this.data.consoleLogs.length,
                        stateCaptures: this.data.stateSnapshots.length,
                        unifiedCaptures: this.data.unifiedCaptures.length,
                        slowFunctions: [...this.data.functionTimings.entries()]
                            .filter(([name, time]) => time > 50)
                            .sort((a, b) => b[1] - a[1]),
                        unifiedSystemConnected: !!this.unifiedSystem,
                        unifiedSystemMethods: this.unifiedSystem ? Object.keys(this.unifiedSystem) : []
                    },
                    data: this.data,
                    analysis: this.analyzePerformance(),
                    unifiedIntegration: {
                        connected: !!this.unifiedSystem,
                        capturesSuccessful: this.data.unifiedCaptures.length,
                        errors: this.data.stateSnapshots.filter(s => s.unified && s.unified.error).length
                    }
                };

                console.log('ğŸ“‹ SNAPE Enhanced Report Generated');

                // Send final report to Unified Diagnostics
                if (this.unifiedSystem && this.unifiedSystem.log) {
                    this.unifiedSystem.log('SNAPE_FINAL_REPORT', report.summary);
                }

                return report;
            },

            analyzePerformance() {
                const analysis = {
                    performanceIssues: [],
                    recommendations: [],
                    unifiedSystemHealth: 'unknown'
                };

                // Check for slow functions
                for (let [name, time] of this.data.functionTimings) {
                    if (time > 100) {
                        analysis.performanceIssues.push(`${name} is slow: ${time}ms`);
                        analysis.recommendations.push(`Optimize ${name} function`);
                    }
                }

                // Check memory growth
                if (this.data.memoryUsage.length > 5) {
                    const first = this.data.memoryUsage[0];
                    const last = this.data.memoryUsage[this.data.memoryUsage.length - 1];
                    const growth = last.used - first.used;
                    if (growth > 10 * 1024 * 1024) { // 10MB
                        analysis.performanceIssues.push(`Memory leak detected: ${growth / 1024 / 1024}MB growth`);
                        analysis.recommendations.push('Investigate memory leaks');
                    }
                }

                // Assess unified system integration
                if (this.data.unifiedCaptures.length > 0) {
                    analysis.unifiedSystemHealth = 'healthy';
                } else if (this.unifiedSystem) {
                    analysis.unifiedSystemHealth = 'connected_but_no_captures';
                    analysis.recommendations.push('Check Unified Diagnostics capture functionality');
                } else {
                    analysis.unifiedSystemHealth = 'not_connected';
                    analysis.recommendations.push('Ensure Unified Diagnostics System is loaded');
                }

                return analysis;
            }
        };

        // Auto-initialize
        window.SNAPEDiagnostics.init();
        """

        print("ğŸ”§ Injecting SNAPE enhancements...")
        self.driver.execute_script(diagnostic_script)
        time.sleep(0.5)  # Brief pause for initialization

        # Verify connection
        connection_status = self.driver.execute_script("""
            return {
                snapeReady: !!window.SNAPEDiagnostics,
                unifiedConnected: !!(window.SNAPEDiagnostics && window.SNAPEDiagnostics.unifiedSystem),
                unifiedMethods: window.SNAPEDiagnostics && window.SNAPEDiagnostics.unifiedSystem 
                    ? Object.keys(window.SNAPEDiagnostics.unifiedSystem) : []
            };
        """)

        print(f"ğŸ”— Connection Status: {connection_status}")
        return connection_status

    def collect_browser_logs(self):
        """Collect all browser logs"""
        try:
            logs = self.driver.get_log('browser')
            self.diagnostic_data['console_logs'].extend(logs)
            print(f"ğŸ“Š Collected {len(logs)} browser logs")
        except Exception as e:
            print(f"âš ï¸ Log collection error: {e}")

    def cleanup(self):
        """Clean shutdown with final data collection"""
        try:
            print("ğŸ”„ Collecting final data...")
            self.collect_browser_logs()

            # Get final report from SNAPE system
            final_report = self.driver.execute_script("return window.SNAPEDiagnostics ? window.SNAPEDiagnostics.generateReport() : {error: 'SNAPE not available'};")
            self.diagnostic_data['snape_final_report'] = final_report

            print(f"ğŸ“‹ Final report collected: {len(str(final_report))} characters")
        except Exception as e:
            print(f"âš ï¸ Cleanup error: {e}")
        finally:
            if self.driver:
                print("ğŸ”’ Closing browser...")
                self.driver.quit()

class HumanSimulator:
    """Realistic human interaction patterns with visual highlight pointers"""

    @staticmethod
    def create_highlight_pointer(driver, x, y, color="red", size=30):
        """Create visual highlight pointer at coordinates"""
        pointer_script = f"""
        // Remove existing pointers
        document.querySelectorAll('.snape-highlight-pointer').forEach(p => p.remove());

        // Create new highlight pointer
        const pointer = document.createElement('div');
        pointer.className = 'snape-highlight-pointer';
        pointer.style.position = 'fixed';
        pointer.style.left = '{x - size//2}px';
        pointer.style.top = '{y - size//2}px';
        pointer.style.width = '{size}px';
        pointer.style.height = '{size}px';
        pointer.style.background = 'radial-gradient(circle, {color}, transparent)';
        pointer.style.border = '3px solid {color}';
        pointer.style.borderRadius = '50%';
        pointer.style.pointerEvents = 'none';
        pointer.style.zIndex = '999999';
        pointer.style.opacity = '0.8';
        pointer.style.animation = 'pulse 1s infinite alternate';

        // Add pulse animation if not exists
        if (!document.getElementById('snape-pointer-styles')) {{
            const style = document.createElement('style');
            style.id = 'snape-pointer-styles';
            style.textContent = `
                @keyframes pulse {{
                    0% {{ transform: scale(1); opacity: 0.8; }}
                    100% {{ transform: scale(1.2); opacity: 0.6; }}
                }}
            `;
            document.head.appendChild(style);
        }}

        document.body.appendChild(pointer);
        console.log('ğŸ¯ Highlight pointer created at ({x}, {y})');
        """
        driver.execute_script(pointer_script)

    @staticmethod
    def human_curve_movement_with_highlights(driver, start_x, start_y, end_x, end_y, steps=25):
        """Generate human-like curved movement with visual highlight pointers"""
        print(f"ğŸ¯ Moving from ({start_x}, {start_y}) to ({end_x}, {end_y}) with highlights")

        # Create starting highlight
        HumanSimulator.create_highlight_pointer(driver, start_x, start_y, "green", 25)
        time.sleep(0.3)

        actions = ActionChains(driver)

        # Generate human-like path points
        path_points = []
        for i in range(steps):
            t = i / steps

            # Bezier curve with control points for natural movement
            control_x = (start_x + end_x) / 2 + random.gauss(0, 30)
            control_y = (start_y + end_y) / 2 + random.gauss(0, 30)

            # Calculate smooth curve position
            x = (1-t)**2 * start_x + 2*(1-t)*t * control_x + t**2 * end_x
            y = (1-t)**2 * start_y + 2*(1-t)*t * control_y + t**2 * end_y

            # Add human micro-movements and tremor
            x += random.gauss(0, 1.5)
            y += random.gauss(0, 1.5)

            path_points.append((int(x), int(y)))

        # Execute movement with intermediate highlights
        for i, (x, y) in enumerate(path_points):
            # Show highlight pointer every few steps
            if i % 5 == 0:
                HumanSimulator.create_highlight_pointer(driver, x, y, "blue", 20)

            # Human-like variable speed
            if i < len(path_points) * 0.3:  # Slow start
                pause_time = random.uniform(0.02, 0.04)
            elif i > len(path_points) * 0.7:  # Slow end
                pause_time = random.uniform(0.015, 0.03)
            else:  # Faster middle
                pause_time = random.uniform(0.005, 0.015)

            body = driver.find_element('tag name', 'body')
            actions.move_to_element_with_offset(body, x, y)
            actions.pause(pause_time)

        # Final highlight at destination
        HumanSimulator.create_highlight_pointer(driver, end_x, end_y, "red", 35)

        actions.perform()
        time.sleep(0.5)  # Brief pause to show final position

    @staticmethod
    def highlight_click_sequence(driver, x, y, click_type="left", tracker=None):
        """Perform click with visual highlighting and coordinate tracking"""
        timestamp = time.time() * 1000  # Convert to milliseconds
        print(f"ğŸ–±ï¸ Human-like {click_type} click at ({x}, {y})")

        # Track action coordinate
        if tracker:
            action_record = tracker.track_action_coordinate(
                f"{click_type}_click", x, y, timestamp,
                additional_data={'pre_click_highlight': True}
            )

        # Pre-click highlight
        HumanSimulator.create_highlight_pointer(driver, x, y, "yellow", 40)

        # Track coordinate action in browser
        driver.execute_script(f"""
            if (!window.coordinateStateTracker) {{
                window.coordinateStateTracker = {{ actions: [], states: [], mappings: [] }};
            }}
            window.coordinateStateTracker.actions.push({{
                timestamp: {timestamp},
                action_type: '{click_type}_click',
                coordinates: {{x: {x}, y: {y}}},
                phase: 'pre_click'
            }});
        """)

        # Human-like pre-click pause (targeting)
        time.sleep(random.uniform(0.2, 0.5))

        # Smaller highlight during click
        HumanSimulator.create_highlight_pointer(driver, x, y, "orange", 25)

        # Execute click with micro-adjustments
        final_x = x + random.gauss(0, 2)
        final_y = y + random.gauss(0, 2)

        click_timestamp = time.time() * 1000

        actions = ActionChains(driver)
        body = driver.find_element('tag name', 'body')
        actions.move_to_element_with_offset(body, int(final_x), int(final_y))

        if click_type == "right":
            actions.context_click()
        else:
            actions.click()

        actions.perform()

        # Track actual click coordinates
        driver.execute_script(f"""
            window.coordinateStateTracker.actions.push({{
                timestamp: {click_timestamp},
                action_type: '{click_type}_click',
                coordinates: {{x: {int(final_x)}, y: {int(final_y)}}},
                phase: 'click_executed',
                coordinate_adjustment: {{
                    planned: {{x: {x}, y: {y}}},
                    actual: {{x: {int(final_x)}, y: {int(final_y)}}}
                }}
            }});
        """)

        # Post-click highlight
        HumanSimulator.create_highlight_pointer(driver, int(final_x), int(final_y), "green", 30)
        time.sleep(0.3)

        return int(final_x), int(final_y)

    @staticmethod
    def human_pause():
        """Random human-like pause"""
        time.sleep(random.uniform(0.1, 0.4))

    @staticmethod
    def scroll_like_human_with_highlights(driver, direction='down', distance=None, tracker=None):
        """Human-like scrolling with trackpad feel and coordinate tracking"""
        if distance is None:
            distance = random.randint(150, 400)

        scroll_distance = distance if direction == 'down' else -distance
        timestamp = time.time() * 1000

        print(f"ğŸ“œ Human-like {direction} scroll: {distance}px")

        # Show scroll indicator
        center_x, center_y = 800, 400
        HumanSimulator.create_highlight_pointer(driver, center_x, center_y, "purple", 50)

        # Track scroll action coordinate
        if tracker:
            action_record = tracker.track_action_coordinate(
                'scroll', center_x, center_y, timestamp,
                additional_data={
                    'direction': direction,
                    'distance': distance,
                    'scroll_distance': scroll_distance
                }
            )

        # Track scroll in browser
        driver.execute_script(f"""
            if (!window.coordinateStateTracker) {{
                window.coordinateStateTracker = {{ actions: [], states: [], mappings: [] }};
            }}
            const beforeScroll = {{
                x: window.scrollX,
                y: window.scrollY
            }};
            window.coordinateStateTracker.actions.push({{
                timestamp: {timestamp},
                action_type: 'scroll',
                coordinates: {{x: {center_x}, y: {center_y}}},
                scroll_data: {{
                    direction: '{direction}',
                    planned_distance: {distance},
                    before_scroll: beforeScroll
                }}
            }});
        """)

        # Multi-step scroll like trackpad with acceleration
        steps = random.randint(4, 10)
        for i in range(steps):
            # Acceleration curve - start slow, peak in middle, end slow
            t = i / steps
            acceleration = 4 * t * (1 - t)  # Parabolic acceleration

            base_step = scroll_distance // steps
            accelerated_step = int(base_step * (0.5 + acceleration))

            # Add natural variation
            step_distance = accelerated_step + random.randint(-15, 15)

            # Visual feedback during scroll
            if i % 2 == 0:
                indicator_y = center_y + (i * 30) if direction == 'down' else center_y - (i * 30)
                HumanSimulator.create_highlight_pointer(driver, center_x, indicator_y, "cyan", 25)

            driver.execute_script(f"window.scrollBy(0, {step_distance});")
            time.sleep(random.uniform(0.08, 0.2))

        # Final position indicator
        HumanSimulator.create_highlight_pointer(driver, center_x, center_y, "lime", 40)

        # Track final scroll position
        final_timestamp = time.time() * 1000
        driver.execute_script(f"""
            const afterScroll = {{
                x: window.scrollX,
                y: window.scrollY
            }};
            window.coordinateStateTracker.actions.push({{
                timestamp: {final_timestamp},
                action_type: 'scroll_complete',
                coordinates: {{x: {center_x}, y: {center_y}}},
                scroll_result: {{
                    after_scroll: afterScroll,
                    actual_delta: afterScroll.y - window.coordinateStateTracker.actions.find(a => a.action_type === 'scroll' && a.timestamp === {timestamp}).scroll_data.before_scroll.y
                }}
            }});
        """)

        time.sleep(0.4)

class OptimizedSNAPE:
