    def __init__(self):
        self.chrome_system = ChromeMockDiagnostics()
        self.test_results = []
        self.connection_status = {}

    def execute_unified_test_sequence(self):
        """Execute the complete test sequence with Unified Diagnostics integration"""
        print("ğŸš€ SNAPE OPTIMIZED - UNIFIED DIAGNOSTICS INTEGRATION")
        print("=" * 55)

        try:
            # 1. Setup Chrome with Unified Diagnostics awareness
            driver = self.chrome_system.setup_optimized_chrome()

            # 2. Connect to existing Unified Diagnostics and enhance
            self.connection_status = self.chrome_system.inject_snape_enhancements()

            if not self.connection_status['snapeReady']:
                print("âŒ SNAPE enhancement failed to initialize")
                return {"error": "SNAPE initialization failed"}

            if self.connection_status['unifiedConnected']:
                print(f"âœ… Connected to Unified Diagnostics with methods: {self.connection_status['unifiedMethods']}")
            else:
                print("âš ï¸ Unified Diagnostics not connected - using fallback mode")

            # 3. Execute enhanced test sequence
            print("\nğŸ¯ Executing Unified Test Sequence...")

            # Test 1: Initial state capture
            driver.execute_script("window.SNAPEDiagnostics.captureState('test_sequence_start');")

            # Test 2: Scroll test with visual highlights and coordinate tracking
            print("ğŸ“œ Testing scroll with visual highlights and Unified Diagnostics capture...")
            HumanSimulator.scroll_like_human_with_highlights(driver, 'down', 200, self.chrome_system.coordinate_tracker)
            driver.execute_script("""
                window.SNAPEDiagnostics.captureState('scroll_completed_with_highlights', {
                    action_type: 'scroll',
                    coordinates: {x: 800, y: 400}
                });
            """)

            # Test 3: Right-click with visual highlights and enhanced timing
            print("ğŸ¯ Testing right-click with visual highlights and unified state capture...")
            canvas_center_x, canvas_center_y = 800, 400

            # Human-like approach with highlights
            approach_start_x = canvas_center_x - random.randint(150, 250)
            approach_start_y = canvas_center_y - random.randint(150, 250)

            HumanSimulator.human_curve_movement_with_highlights(driver, 
                approach_start_x, approach_start_y,
                canvas_center_x, canvas_center_y)

            # Human-like right-click with coordinate tracking
            start_time = time.time()
            final_x, final_y = HumanSimulator.highlight_click_sequence(
                driver, canvas_center_x, canvas_center_y, "right", self.chrome_system.coordinate_tracker)
            click_time = (time.time() - start_time) * 1000

            driver.execute_script(f"""
                window.SNAPEDiagnostics.captureState('right_click_with_highlights_completed', {{
                    action_type: 'right_click',
                    coordinates: {{x: {final_x}, y: {final_y}}},
                    clickTime: {click_time},
                    plannedPosition: {{x: {canvas_center_x}, y: {canvas_center_y}}},
                    actualPosition: {{x: {final_x}, y: {final_y}}},
                    method: 'human_like_with_highlights'
                }});
            """)

            # Test 4: Three-finger touch using Unified Diagnostics system
            print("ğŸ–ï¸ Testing three-finger touch via Unified Diagnostics...")
            start_pos = {'x': 300, 'y': 300}
            end_pos = {'x': 500, 'y': 400}

            three_finger_result = driver.execute_script(f"""
                if (window.SNAPEDiagnostics.unifiedSystem && 
                    window.SNAPEDiagnostics.unifiedSystem.simulateThreeFingerTouch) {{

                    console.log('ğŸ”— Using Unified Diagnostics three-finger simulation');

                    window.SNAPEDiagnostics.captureState('unified_three_finger_start', {{
                        start: {start_pos},
                        end: {end_pos},
                        method: 'unified_diagnostics'
                    }});

                    // Execute three-finger sequence
                    window.SNAPEDiagnostics.unifiedSystem.simulateThreeFingerTouch('down', {start_pos});

                    setTimeout(() => {{
                        window.SNAPEDiagnostics.unifiedSystem.simulateThreeFingerTouch('move', {end_pos});
                        window.SNAPEDiagnostics.captureState('unified_three_finger_moving');
                    }}, 200);

                    setTimeout(() => {{
                        window.SNAPEDiagnostics.unifiedSystem.simulateThreeFingerTouch('up', {end_pos});
                        window.SNAPEDiagnostics.captureState('unified_three_finger_completed');
                    }}, 500);

                    return {{success: true, method: 'unified_diagnostics'}};
                }} else {{
                    console.warn('âš ï¸ Unified Diagnostics three-finger not available');
                    window.SNAPEDiagnostics.captureState('three_finger_fallback_used');
                    return {{success: false, method: 'fallback', reason: 'unified_not_available'}};
                }}
            """)

            print(f"ğŸ–ï¸ Three-finger test result: {three_finger_result}")

            # Wait for async operations
            time.sleep(2)

            # Test 5: Performance stress test with visual highlights
            print("âš¡ Running performance stress test with visual highlights and Unified Diagnostics...")
            for i in range(5):
                # Random target coordinates like a human would pick
                x = random.randint(300, 1200)
                y = random.randint(250, 650)

                print(f"ğŸ¯ Stress test {i+1}: targeting ({x}, {y})")

                # Human-like movement with highlights to random position
                current_x = 800  # Assume starting from center
                current_y = 400

                HumanSimulator.human_curve_movement_with_highlights(driver, 
                    current_x, current_y, x, y, steps=15)

                # Human-like click with coordinate tracking
                final_x, final_y = HumanSimulator.highlight_click_sequence(
                    driver, x, y, "left", self.chrome_system.coordinate_tracker)

                driver.execute_script(f"""
                    window.SNAPEDiagnostics.captureState('stress_test_with_highlights_{i}', {{
                        iteration: {i},
                        targetPosition: {{x: {x}, y: {y}}},
                        actualPosition: {{x: {final_x}, y: {final_y}}},
                        testType: 'human_like_stress_test_with_highlights'
                    }});
                """)

                # Human-like pause between actions
                time.sleep(random.uniform(0.3, 0.8))

            # Collect coordinate tracking data from browser
            print("ğŸ“Š Collecting coordinate tracking data...")
            browser_coordinate_data = driver.execute_script("""
                return window.coordinateStateTracker || {actions: [], states: [], mappings: []};
            """)

            # Process browser coordinate data into tracker
            for action in browser_coordinate_data.get('actions', []):
                self.chrome_system.coordinate_tracker.track_action_coordinate(
                    action['action_type'],
                    action['coordinates']['x'],
                    action['coordinates']['y'],
                    action['timestamp'],
                    action.get('additional_data', {})
                )

            for state in browser_coordinate_data.get('states', []):
                state_record = self.chrome_system.coordinate_tracker.track_state_change(
                    state['state_type'],
                    state['change_data'],
                    state['timestamp'],
                    state.get('triggered_by_action')
                )

                # Find corresponding action and create mapping
                for action_record in self.chrome_system.coordinate_tracker.action_coordinates:
                    if abs(action_record['timestamp'] - state['timestamp']) < 1000:  # Within 1 second
                        self.chrome_system.coordinate_tracker.map_coordinate_to_state(
                            action_record, state_record, 'high'
                        )
                        break

            # Generate MD report
            print("ğŸ“ Generating coordinate vs state change MD report...")
            md_report = self.chrome_system.coordinate_tracker.generate_md_report()

            # Save MD report to file
            report_filename = f"SNAPE-Coordinate-State-Analysis-{int(time.time())}.md"
            with open(report_filename, 'w') as f:
                f.write(md_report)
            print(f"ğŸ“‹ MD report saved: {report_filename}")

            # Final comprehensive data collection
            print("ğŸ“Š Collecting comprehensive final data...")
            time.sleep(1)
            self.chrome_system.collect_browser_logs()

            # Generate enhanced report with Unified Diagnostics data
            final_report = driver.execute_script("return window.SNAPEDiagnostics.generateReport();")

            # Analysis and scoring
            print("\nğŸ“‹ UNIFIED TEST RESULTS WITH COORDINATE ANALYSIS:")
            print(f"âœ… SNAPE Ready: {self.connection_status['snapeReady']}")
            print(f"ğŸ”— Unified Connected: {self.connection_status['unifiedConnected']}")
            print(f"ğŸ“Š State Captures: {final_report['summary']['stateCaptures']}")
            print(f"ğŸ”— Unified Captures: {final_report['summary']['unifiedCaptures']}")
            print(f"âš¡ Function Timings: {len(final_report['summary']['slowFunctions'])}")
            print(f"ğŸ¯ Unified System Health: {final_report['analysis']['unifiedSystemHealth']}")

            # Coordinate analysis results
            coord_tracker = self.chrome_system.coordinate_tracker
            print(f"\nğŸ¯ COORDINATE VS STATE ANALYSIS:")
            print(f"ğŸ“ Action Coordinates Tracked: {len(coord_tracker.action_coordinates)}")
            print(f"ğŸ”„ State Changes Tracked: {len(coord_tracker.state_changes)}")
            print(f"ğŸ”— Coordinate-State Mappings: {len(coord_tracker.coordinate_state_mapping)}")
            print(f"ğŸ“‹ MD Report Generated: {report_filename}")

            if coord_tracker.coordinate_state_mapping:
                avg_response_time = sum(m['time_delta'] for m in coord_tracker.coordinate_state_mapping) / len(coord_tracker.coordinate_state_mapping)
                print(f"â±ï¸ Average Coordinate Response Time: {avg_response_time:.2f}ms")

                immediate_responses = len([m for m in coord_tracker.coordinate_state_mapping if m['time_delta'] < 50])
                print(f"âš¡ Immediate Responses: {immediate_responses}/{len(coord_tracker.coordinate_state_mapping)} ({immediate_responses/len(coord_tracker.coordinate_state_mapping)*100:.1f}%)")

            if final_report['summary']['slowFunctions']:
                print("\nğŸŒ Slow Functions Detected:")
                for func_name, time_ms in final_report['summary']['slowFunctions']:
                    print(f"  â€¢ {func_name}: {time_ms:.2f}ms")

            if final_report['analysis']['performanceIssues']:
                print("\nâš ï¸ Performance Issues:")
                for issue in final_report['analysis']['performanceIssues']:
                    print(f"  â€¢ {issue}")

            if final_report['analysis']['recommendations']:
                print("\nğŸ’¡ Recommendations:")
                for rec in final_report['analysis']['recommendations']:
                    print(f"  â€¢ {rec}")

            # Screenshot with unified data
            driver.save_screenshot("snape_unified_test_complete.png")
            print("ğŸ“¸ Final screenshot saved")

            self.test_results = final_report
            return final_report

        except Exception as e:
            print(f"âŒ Unified test execution error: {e}")
            return {"error": str(e), "connection_status": self.connection_status}

        finally:
            print("ğŸ”„ Cleaning up...")
            self.chrome_system.cleanup()

    def grade_unified_optimization(self):
        """Grade the unified optimization effectiveness"""
        if not self.test_results or 'error' in self.test_results:
            return {"score": 0, "grade": "F", "issues": ["Test failed to complete"]}

        score = 100
        issues = []
        strengths = []

        # Unified Diagnostics Integration (30 points)
        if self.connection_status.get('unifiedConnected'):
            strengths.append("Successfully connected to Unified Diagnostics System")
            if self.test_results['summary']['unifiedCaptures'] > 0:
                strengths.append(f"Captured {self.test_results['summary']['unifiedCaptures']} unified states")
            else:
                score -= 15
                issues.append("Connected but no unified captures")
        else:
            score -= 25
            issues.append("Failed to connect to Unified Diagnostics System")

        # Performance Analysis (25 points)
        slow_funcs = len(self.test_results['summary']['slowFunctions'])
        if slow_funcs > 0:
            score -= min(slow_funcs * 5, 20)
            issues.append(f"{slow_funcs} slow functions detected")
        else:
            strengths.append("No slow functions detected")

        # System Health (25 points)
        health = self.test_results['analysis']['unifiedSystemHealth']
        if health == 'healthy':
            strengths.append("Unified System health: HEALTHY")
        elif health == 'connected_but_no_captures':
            score -= 15
            issues.append("Unified system connected but not capturing")
        else:
            score -= 20
            issues.append("Unified system health issues")

        # Test Completeness (20 points)
        state_captures = self.test_results['summary']['stateCaptures']
        if state_captures >= 5:
            strengths.append(f"Comprehensive testing: {state_captures} state captures")
        else:
            score -= 10
            issues.append(f"Limited testing: only {state_captures} state captures")

        # Grade calculation
        if score >= 95:
            grade = "A+"
        elif score >= 90:
            grade = "A"
        elif score >= 85:
            grade = "A-"
        elif score >= 80:
            grade = "B+"
        elif score >= 75:
            grade = "B"
        elif score >= 70:
            grade = "B-"
        elif score >= 65:
            grade = "C+"
        elif score >= 60:
            grade = "C"
        else:
            grade = "F"

        return {
            "score": score,
            "grade": grade,
            "issues": issues,
            "strengths": strengths,
            "recommendations": self.test_results['analysis']['recommendations'],
            "unified_integration": self.test_results['unifiedIntegration']
        }

def main():
    """Execute SNAPE Unified Diagnostics Integration Test"""
    print("ğŸš€ SNAPE OPTIMIZED - UNIFIED DIAGNOSTICS INTEGRATION v2.0")
    print("=" * 60)
    print("ğŸ”— Connecting to existing Unified Diagnostics System")
    print("ğŸ¯ Testing: Scroll, Right-click, Three-finger, Performance")
    print()

    snape = OptimizedSNAPE()
    results = snape.execute_unified_test_sequence()

    if results and 'error' not in results:
        grade_data = snape.grade_unified_optimization()

        print(f"\nğŸ¯ UNIFIED OPTIMIZATION SCORE: {grade_data['score']}/100 ({grade_data['grade']})")

        if grade_data['strengths']:
            print("\nâœ… Strengths:")
            for strength in grade_data['strengths']:
                print(f"  â€¢ {strength}")

        if grade_data['issues']:
            print("\nâš ï¸ Issues Found:")
            for issue in grade_data['issues']:
                print(f"  â€¢ {issue}")

        if grade_data['recommendations']:
            print("\nğŸ’¡ Recommendations:")
            for rec in grade_data['recommendations']:
                print(f"  â€¢ {rec}")

        print(f"\nğŸ”— Unified Integration Status:")
        integration = grade_data['unified_integration']
        print(f"  â€¢ Connected: {integration['connected']}")
        print(f"  â€¢ Successful Captures: {integration['capturesSuccessful']}")
        print(f"  â€¢ Errors: {integration['errors']}")

    else:
        print(f"\nâŒ Test Failed: {results.get('error', 'Unknown error')}")
        if 'connection_status' in results:
            print(f"Connection Status: {results['connection_status']}")

    print("\nâœ… SNAPE Unified Diagnostics Integration Test Complete!")

if __name__ == "__main__":
    main()

