#!/usr/bin/env python3
"""
SNAPE Unified Testing System
Comprehensive human-like pointer behavior with all gestures, zoom, and state tracking
No backend input dependencies - only reads state data and errors from backend
"""

import time
import math
import random
import json
import subprocess
import sys
import os
from pathlib import Path
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import TimeoutException, NoSuchElementException

class SNAPEUnifiedSystem:
    """
    Unified SNAPE system for all human-like testing with trackpad gestures
    """

    def __init__(self, server_url="http://localhost:8000"):
        self.server_url = server_url
        self.driver = None
        self.wait = None

        # State tracking
        self.action_coordinates = []
        self.state_changes = []
        self.coordinate_mappings = []
        self.test_results = []
        self.console_logs = []
        self.performance_data = []
        self.error_traces = []

        print("ğŸš€ SNAPE UNIFIED TESTING SYSTEM")
        print("=" * 50)
        print("ğŸ¯ All pointer behaviors, gestures, zoom, state tracking")
        print("ğŸ” Backend error reading + state comparison")
        print("ğŸ–±ï¸ Human-like trackpad gestures and shortcuts")

    def setup_unified_chrome(self):
        """Setup Chrome with all optimizations"""
        print("ğŸ”§ Setting up unified Chrome system...")

        chrome_options = Options()

        # Performance optimizations
        chrome_options.add_argument("--no-sandbox")
        chrome_options.add_argument("--disable-dev-shm-usage")
        chrome_options.add_argument("--disable-web-security")
        chrome_options.add_argument("--disable-features=VizDisplayCompositor")
        chrome_options.add_argument("--disable-blink-features=AutomationControlled")
        chrome_options.add_argument("--window-size=1600,1000")
        chrome_options.add_argument("--window-position=50,50")

        # Enhanced logging
        chrome_options.add_argument("--enable-logging")
        chrome_options.add_argument("--log-level=0")
        chrome_options.set_capability('goog:loggingPrefs', {
            'browser': 'ALL', 
            'performance': 'ALL',
            'driver': 'ALL'
        })

        # Anti-detection
        chrome_options.add_experimental_option("excludeSwitches", ["enable-automation"])
        chrome_options.add_experimental_option('useAutomationExtension', False)

        print("ğŸš€ Launching Chrome...")
        self.driver = webdriver.Chrome(options=chrome_options)
        self.driver.execute_script("Object.defineProperty(navigator, 'webdriver', {get: () => undefined})")
        self.wait = WebDriverWait(self.driver, 10)

        # Navigate immediately
        print("ğŸŒ Navigating to canvas...")
        self.driver.get(f"{self.server_url}/canvas.html")

        return self.driver

    def inject_unified_diagnostics(self):
        """Inject comprehensive diagnostics and pointer system"""
        print("ğŸ’‰ Injecting unified diagnostics...")

        unified_script = """
        // SNAPE Unified Diagnostic System
        window.SNAPEUnified = {
            // State tracking
            actionCoordinates: [],
            stateChanges: [],
            coordinateMappings: [],
            consoleLogs: [],
            performanceEntries: [],
            errorTraces: [],
            functionTimings: new Map(),

            // Visual pointer system
            activePointers: [],
            trailElements: [],
            spellCircles: [],

            init() {
                this.setupStyles();
                this.startMonitoring();
                this.connectToExistingSystems();
                console.log('âœ… SNAPE Unified System Active');
            },

            setupStyles() {
                const style = document.createElement('style');
                style.id = 'snape-unified-styles';
                style.textContent = `
                    .snape-pointer {
                        position: fixed;
                        width: 30px;
                        height: 30px;
                        background: radial-gradient(circle, #ff4444, #cc0000);
                        border: 2px solid #ffffff;
                        border-radius: 50%;
                        pointer-events: none;
                        z-index: 999999;
                        box-shadow: 
                            0 0 20px rgba(255, 68, 68, 0.8),
                            0 0 40px rgba(255, 0, 0, 0.6);
                        animation: snapePulse 1s infinite alternate;
                        transform: scale(1);
                        transition: all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);
                    }

                    .snape-trail {
                        position: fixed;
                        width: 8px;
                        height: 8px;
                        background: #ff4444;
                        border-radius: 50%;
                        pointer-events: none;
                        z-index: 999998;
                        opacity: 0.7;
                        animation: fadeTrail 1.5s ease-out forwards;
                    }

                    .snape-zoom-indicator {
                        position: fixed;
                        border: 3px solid #00ff00;
                        border-radius: 50%;
                        pointer-events: none;
                        z-index: 999997;
                        background: rgba(0, 255, 0, 0.1);
                        animation: zoomPulse 2s ease-out forwards;
                    }

                    .snape-three-finger {
                        position: fixed;
                        width: 25px;
                        height: 25px;
                        background: radial-gradient(circle, #9d4edd, #7209b7);
                        border: 2px solid #f72585;
                        border-radius: 50%;
                        pointer-events: none;
                        z-index: 999999;
                        box-shadow: 0 0 15px #9d4edd;
                        animation: threeFingerPulse 1.5s infinite alternate;
                    }

                    @keyframes snapePulse {
                        0% { transform: scale(1); opacity: 0.9; }
                        100% { transform: scale(1.2); opacity: 1; }
                    }

                    @keyframes fadeTrail {
                        0% { opacity: 0.7; transform: scale(1); }
                        100% { opacity: 0; transform: scale(0.3); }
                    }

                    @keyframes zoomPulse {
                        0% { transform: scale(0.5); opacity: 1; border-width: 5px; }
                        100% { transform: scale(2); opacity: 0; border-width: 1px; }
                    }

                    @keyframes threeFingerPulse {
                        0% { transform: scale(1); }
                        100% { transform: scale(1.3); }
                    }
                `;
                document.head.appendChild(style);
            },

            startMonitoring() {
                // Console override for logging
                const originalLog = console.log;
                const originalError = console.error;
                const originalWarn = console.warn;

                console.log = (...args) => {
                    this.consoleLogs.push({
                        type: 'log', 
                        args: args.map(String), 
                        timestamp: Date.now(),
                        stack: new Error().stack
                    });
                    originalLog.apply(console, args);
                };

                console.error = (...args) => {
                    this.consoleLogs.push({
                        type: 'error', 
                        args: args.map(String), 
                        timestamp: Date.now(),
                        stack: new Error().stack
                    });
                    this.errorTraces.push({
                        message: args.join(' '),
                        timestamp: Date.now(),
                        stack: new Error().stack
                    });
                    originalError.apply(console, args);
                };

                console.warn = (...args) => {
                    this.consoleLogs.push({
                        type: 'warn', 
                        args: args.map(String), 
                        timestamp: Date.now(),
                        stack: new Error().stack
                    });
                    originalWarn.apply(console, args);
                };

                // Performance monitoring
                if (window.PerformanceObserver) {
                    const observer = new PerformanceObserver((list) => {
                        this.performanceEntries.push(...list.getEntries());
                    });
                    observer.observe({entryTypes: ['measure', 'navigation', 'paint']});
                }

                // Function timing for TLDraw
                this.monitorTLDrawFunctions();
            },

            monitorTLDrawFunctions() {
                if (window.tlEditor) {
                    const methods = ['updateShapes', 'createShape', 'deleteShapes', 'selectShape', 'setCamera'];
                    methods.forEach(method => {
                        if (window.tlEditor[method]) {
                            const original = window.tlEditor[method];
                            window.tlEditor[method] = (...args) => {
                                const start = performance.now();
                                const result = original.apply(window.tlEditor, args);
                                const duration = performance.now() - start;
                                this.functionTimings.set(method, duration);

                                if (duration > 10) {
                                    console.warn(`âš ï¸ Slow function: ${method} took ${duration}ms`);
                                }
                                return result;
                            };
                        }
                    });
                }

                // Check again in case tlEditor loads later
                setTimeout(() => {
                    if (!this.functionsMonitored && window.tlEditor) {
                        this.monitorTLDrawFunctions();
                        this.functionsMonitored = true;
                    }
                }, 2000);
            },

            connectToExistingSystems() {
                // Connect to existing diagnostic systems
                this.unifiedDiagnostics = window.unifiedDiagnostics || window.canvasStateDiag;
                if (this.unifiedDiagnostics) {
                    console.log('ğŸ”— Connected to existing diagnostics system');
                } else {
                    console.warn('âš ï¸ No existing diagnostics system found');
                }
            },

            // Pointer management
            createPointer(x, y, type = 'default', color = '#ff4444') {
                const pointer = document.createElement('div');
                pointer.className = `snape-pointer snape-${type}`;
                pointer.style.left = (x - 15) + 'px';
                pointer.style.top = (y - 15) + 'px';

                if (color !== '#ff4444') {
                    pointer.style.background = `radial-gradient(circle, ${color}, ${this.darkenColor(color)})`;
                }

                document.body.appendChild(pointer);
                this.activePointers.push(pointer);

                console.log(`ğŸ¯ Pointer created at (${x}, ${y})`);
                return pointer;
            },

            movePointer(pointer, x, y, withTrail = true) {
                if (pointer && pointer.parentNode) {
                    pointer.style.left = (x - 15) + 'px';
                    pointer.style.top = (y - 15) + 'px';

                    if (withTrail) {
                        this.createTrail(x, y);
                    }
                }
            },

            createTrail(x, y) {
                const trail = document.createElement('div');
                trail.className = 'snape-trail';
                trail.style.left = (x - 4) + 'px';
                trail.style.top = (y - 4) + 'px';
                document.body.appendChild(trail);

                this.trailElements.push(trail);

                setTimeout(() => {
                    if (trail.parentNode) {
                        trail.remove();
                    }
                    this.trailElements = this.trailElements.filter(t => t !== trail);
                }, 1500);
            },

            createZoomIndicator(x, y, direction) {
                const indicator = document.createElement('div');
                indicator.className = 'snape-zoom-indicator';
                indicator.style.left = (x - 50) + 'px';
                indicator.style.top = (y - 50) + 'px';
                indicator.style.width = '100px';
                indicator.style.height = '100px';

                // Add zoom direction indicator
                indicator.innerHTML = `<div style="
                    position: absolute; 
                    top: 50%; 
                    left: 50%; 
                    transform: translate(-50%, -50%);
                    font-size: 20px;
                    color: #00ff00;
                    font-weight: bold;
                ">${direction === 'in' ? 'ğŸ”+' : 'ğŸ”-'}</div>`;

                document.body.appendChild(indicator);

                setTimeout(() => {
                    if (indicator.parentNode) {
                        indicator.remove();
                    }
                }, 2000);
            },

            createThreeFingerPointers(startX, startY) {
                const positions = [
                    {x: startX, y: startY},
                    {x: startX + 40, y: startY + 20},
                    {x: startX + 80, y: startY + 40}
                ];

                const pointers = positions.map((pos, i) => {
                    const pointer = document.createElement('div');
                    pointer.className = 'snape-three-finger';
                    pointer.style.left = (pos.x - 12.5) + 'px';
                    pointer.style.top = (pos.y - 12.5) + 'px';
                    pointer.innerHTML = `<div style="
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        font-size: 10px;
                        color: white;
                        font-weight: bold;
                    ">${i + 1}</div>`;
                    document.body.appendChild(pointer);
                    return pointer;
                });

                this.threeFingerPointers = pointers;
                return pointers;
            },

            moveThreeFingerPointers(deltaX, deltaY) {
                if (this.threeFingerPointers) {
                    this.threeFingerPointers.forEach(pointer => {
                        const currentX = parseInt(pointer.style.left) + 12.5;
                        const currentY = parseInt(pointer.style.top) + 12.5;
                        pointer.style.left = (currentX + deltaX - 12.5) + 'px';
                        pointer.style.top = (currentY + deltaY - 12.5) + 'px';
                    });
                }
            },

            removeThreeFingerPointers() {
                if (this.threeFingerPointers) {
                    this.threeFingerPointers.forEach(pointer => {
                        if (pointer.parentNode) {
                            pointer.remove();
                        }
                    });
                    this.threeFingerPointers = null;
                }
            },

            // Action tracking
            trackAction(actionType, x, y, additionalData = {}) {
                const timestamp = Date.now();
                const action = {
                    type: actionType,
                    coordinates: {x, y},
                    timestamp,
                    additionalData
                };

                this.actionCoordinates.push(action);
                console.log(`ğŸ“ Action tracked: ${actionType} at (${x}, ${y})`);
                return action;
            },

            trackStateChange(stateType, changeData, triggeredBy = null) {
                const timestamp = Date.now();
                const stateChange = {
                    type: stateType,
                    data: changeData,
                    timestamp,
                    triggeredBy
                };

                this.stateChanges.push(stateChange);
                console.log(`ğŸ”„ State change: ${stateType}`);
                return stateChange;
            },

            compareStates(beforeState, afterState, expectedChanges) {
                const comparison = {
                    timestamp: Date.now(),
                    before: beforeState,
                    after: afterState,
                    expected: expectedChanges,
                    matches: {},
                    failures: []
                };

                // Compare expected changes
                for (const [key, expectedValue] of Object.entries(expectedChanges)) {
                    const beforeValue = this.getNestedValue(beforeState, key);
                    const afterValue = this.getNestedValue(afterState, key);

                    if (typeof expectedValue === 'function') {
                        const result = expectedValue(beforeValue, afterValue);
                        comparison.matches[key] = result;
                        if (!result) {
                            comparison.failures.push({
                                key,
                                expected: 'function check',
                                before: beforeValue,
                                after: afterValue
                            });
                        }
                    } else {
                        const match = afterValue === expectedValue;
                        comparison.matches[key] = match;
                        if (!match) {
                            comparison.failures.push({
                                key,
                                expected: expectedValue,
                                actual: afterValue,
                                before: beforeValue
                            });
                        }
                    }
                }

                comparison.success = comparison.failures.length === 0;

                if (!comparison.success) {
                    console.error('âŒ State comparison failed:', comparison.failures);
                } else {
                    console.log('âœ… State comparison passed');
                }

                return comparison;
            },

            getNestedValue(obj, path) {
                return path.split('.').reduce((current, key) => current && current[key], obj);
            },

            darkenColor(color) {
                // Simple color darkening
                return color.replace(/[0-9a-f]/g, (match) => {
                    const val = parseInt(match, 16);
                    return Math.max(0, val - 3).toString(16);
                });
            },

            // State capture
            captureCurrentState() {
                const state = {
                    timestamp: Date.now(),
                    dom: {
                        shapes: document.querySelectorAll('.tl-shape').length,
                        canvases: document.querySelectorAll('canvas').length,
                        activeElement: document.activeElement?.tagName,
                        scrollPosition: {x: window.scrollX, y: window.scrollY}
                    },
                    editor: {},
                    console: this.consoleLogs.slice(-5),
                    errors: this.errorTraces.slice(-3),
                    performance: {
                        memory: performance.memory ? {
                            used: performance.memory.usedJSHeapSize,
                            total: performance.memory.totalJSHeapSize
                        } : null,
                        functionTimes: Object.fromEntries(this.functionTimings)
                    }
                };

                // Capture TLDraw state
                try {
                    if (window.tlEditor) {
                        state.editor = {
                            camera: window.tlEditor.getCamera(),
                            selectedShapes: window.tlEditor.getSelectedShapes().length,
                            currentTool: window.tlEditor.getCurrentTool?.()?.id,
                            zoom: window.tlEditor.getZoomLevel?.() || window.tlEditor.getCamera?.()?.z
                        };
                    }
                } catch (e) {
                    state.editor.error = e.message;
                }

                // Connect to existing diagnostics
                if (this.unifiedDiagnostics && this.unifiedDiagnostics.captureFullState) {
                    try {
                        state.unified = this.unifiedDiagnostics.captureFullState();
                    } catch (e) {
                        state.unified = {error: e.message};
                    }
                }

                return state;
            },

            // Cleanup
            removeAllPointers() {
                this.activePointers.forEach(pointer => {
                    if (pointer.parentNode) pointer.remove();
                });
                this.activePointers = [];

                this.trailElements.forEach(trail => {
                    if (trail.parentNode) trail.remove();
                });
                this.trailElements = [];

                this.removeThreeFingerPointers();
            },

            // Report generation
            generateReport() {
                return {
                    summary: {
                        totalActions: this.actionCoordinates.length,
                        totalStateChanges: this.stateChanges.length,
                        totalMappings: this.coordinateMappings.length,
                        consoleErrors: this.consoleLogs.filter(log => log.type === 'error').length,
                        errorTraces: this.errorTraces.length
                    },
                    actions: this.actionCoordinates,
                    stateChanges: this.stateChanges,
                    mappings: this.coordinateMappings,
                    consoleLogs: this.consoleLogs,
                    errorTraces: this.errorTraces,
                    performance: {
                        functionTimes: Object.fromEntries(this.functionTimings),
                        slowFunctions: [...this.functionTimings.entries()]
                            .filter(([name, time]) => time > 50)
                            .sort((a, b) => b[1] - a[1])
                    },
                    timestamp: Date.now()
                };
            }
        };

        // Auto-initialize
        window.SNAPEUnified.init();
        """

        self.driver.execute_script(unified_script)
        time.sleep(1)

        # Verify initialization
        status = self.driver.execute_script("""
            return {
                snapeReady: !!window.SNAPEUnified,
                functionsAvailable: window.SNAPEUnified ? Object.keys(window.SNAPEUnified).length : 0,
