    def generate_comprehensive_report(self):
        """Generate comprehensive test report"""
        print("\nğŸ“Š Generating comprehensive report...")

        report = self.driver.execute_script("return window.SNAPEUnified.generateReport();")

        # Collect browser logs
        try:
            browser_logs = self.driver.get_log('browser')
            report['browserLogs'] = browser_logs[-20:]  # Last 20 logs
        except Exception as e:
            report['browserLogs'] = [{'error': f'Log collection failed: {e}'}]

        # Generate summary
        print(f"\nğŸ“‹ SNAPE UNIFIED SYSTEM REPORT")
        print(f"=" * 40)
        print(f"ğŸ“ Total Actions Tracked: {report['summary']['totalActions']}")
        print(f"ğŸ”„ Total State Changes: {report['summary']['totalStateChanges']}")
        print(f"ğŸ”— Coordinate Mappings: {report['summary']['totalMappings']}")
        print(f"âŒ Console Errors: {report['summary']['consoleErrors']}")
        print(f"ğŸš¨ Error Traces: {report['summary']['errorTraces']}")

        # Performance analysis
        slow_functions = report['performance']['slowFunctions']
        if slow_functions:
            print(f"\nğŸŒ Slow Functions Detected:")
            for func_name, time_ms in slow_functions[:5]:
                print(f"   {func_name}: {time_ms:.2f}ms")
        else:
            print(f"\nâœ… No slow functions detected")

        # Error analysis
        if report['summary']['consoleErrors'] > 0:
            print(f"\nâš ï¸ Console Errors Found:")
            error_logs = [log for log in report['consoleLogs'] if log['type'] == 'error']
            for error in error_logs[-3:]:  # Last 3 errors
                print(f"   {error['args'][0] if error['args'] else 'Unknown error'}")

        # Failed state changes
        failed_changes = [change for change in report['stateChanges'] 
                         if change.get('data', {}).get('comparison', {}).get('success') == False]
        if failed_changes:
            print(f"\nâŒ Failed State Changes:")
            for change in failed_changes:
                print(f"   {change['type']}: {change.get('data', {}).get('comparison', {}).get('failures', [])}")

        # Save report to file
        report_filename = f"snape_unified_report_{int(time.time())}.json"
        with open(report_filename, 'w') as f:
            json.dump(report, f, indent=2, default=str)

        print(f"\nğŸ“„ Report saved: {report_filename}")
        return report

    def run_unified_tests(self):
        """Run all unified SNAPE tests"""
        print("ğŸš€ SNAPE UNIFIED SYSTEM - STARTING TESTS")
        print("=" * 60)

        try:
            # Setup
            driver = self.setup_unified_chrome()
            time.sleep(3)  # Wait for page load

            # Inject diagnostics
            status = self.inject_unified_diagnostics()
            if not status['snapeReady']:
                print("âŒ SNAPE system not ready")
                return

            # Take initial screenshot
            self.driver.save_screenshot("snape_unified_start.png")
            print("ğŸ“¸ Initial state captured")

            # Run comprehensive tests
            test_results = self.comprehensive_pointer_test()

            # Generate report
            report = self.generate_comprehensive_report()

            # Clean up pointers
            self.driver.execute_script("window.SNAPEUnified.removeAllPointers();")

            # Final screenshot
            self.driver.save_screenshot("snape_unified_complete.png")
            print("ğŸ“¸ Final state captured")

            # Grade results
            self.grade_test_results(test_results, report)

            print("\nâœ… SNAPE UNIFIED TESTING COMPLETE!")

        except Exception as e:
            print(f"âŒ Unified test failed: {e}")
            if self.driver:
                self.driver.save_screenshot("snape_unified_error.png")

        finally:
            if self.driver:
                print("ğŸ”’ Closing browser...")
                time.sleep(3)  # Brief observation time
                self.driver.quit()

    def grade_test_results(self, test_results, report):
        """Grade the test results"""
        print(f"\nğŸ¯ GRADING TEST RESULTS")
        print(f"=" * 30)

        score = 100
        issues = []

        # Check test completion
        zoom_tests = [r for r in test_results if r[0] == 'zoom_test']
        if zoom_tests:
            zoom_result = zoom_tests[0][1]
            if zoom_result.get('success'):
                print("âœ… Zoom test: PASSED")
            else:
                print("âŒ Zoom test: FAILED")
                score -= 20
                issues.append("Zoom functionality issues")
        else:
            score -= 25
            issues.append("No zoom tests completed")

        # Check three-finger tests
        three_finger_tests = [r for r in test_results if r[0] == 'three_finger_test']
        if three_finger_tests:
            three_finger_result = three_finger_tests[0][1]
            if three_finger_result.get('success'):
                print("âœ… Three-finger test: PASSED")
            else:
                print("âš ï¸ Three-finger test: LIMITED (visual only)")
                score -= 10
        else:
            score -= 15
            issues.append("Three-finger tests incomplete")

        # Check performance
        slow_functions = len(report['performance']['slowFunctions'])
        if slow_functions > 0:
            print(f"âš ï¸ Performance: {slow_functions} slow functions")
            score -= min(slow_functions * 5, 20)
        else:
            print("âœ… Performance: No slow functions")

        # Check errors
        error_count = report['summary']['consoleErrors']
        if error_count > 0:
            print(f"âš ï¸ Errors: {error_count} console errors")
            score -= min(error_count * 3, 15)
        else:
            print("âœ… Errors: No console errors")

        # Final grade
        if score >= 95:
            grade = "A+"
        elif score >= 90:
            grade = "A"
        elif score >= 85:
            grade = "A-"
        elif score >= 80:
            grade = "B+"
        elif score >= 75:
            grade = "B"
        elif score >= 70:
            grade = "B-"
        else:
            grade = "C"

        print(f"\nğŸ† FINAL GRADE: {score}/100 ({grade})")

        if issues:
            print(f"\nğŸ“ Issues to address:")
            for issue in issues:
                print(f"   â€¢ {issue}")

        return {'score': score, 'grade': grade, 'issues': issues}

def main():
    """Main entry point for SNAPE Unified System"""
    print("ğŸš€ SNAPE UNIFIED TESTING SYSTEM")
    print("=" * 50)
    print("ğŸ¯ All pointer behaviors, gestures, zoom testing")
    print("ğŸ” Backend error reading + state comparison")
    print("ğŸ–±ï¸ Human-like trackpad gestures and shortcuts")
    print("ğŸ“Š Comprehensive reporting and failed state tracking")
    print()

    snape = SNAPEUnifiedSystem()
    snape.run_unified_tests()

if __name__ == "__main__":
    main()

