                    transform: translate(-50%, -50%);
                    font-size: 20px;
                    color: #00ff00;
                    font-weight: bold;
                ">${direction === 'in' ? 'ğŸ”+' : 'ğŸ”-'}</div>`;

                document.body.appendChild(indicator);

                setTimeout(() => {
                    if (indicator.parentNode) {
                        indicator.remove();
                    }
                }, 2000);
            },

            createThreeFingerPointers(startX, startY) {
                const positions = [
                    {x: startX, y: startY},
                    {x: startX + 40, y: startY + 20},
                    {x: startX + 80, y: startY + 40}
                ];

                const pointers = positions.map((pos, i) => {
                    const pointer = document.createElement('div');
                    pointer.className = 'snape-three-finger';
                    pointer.style.left = (pos.x - 12.5) + 'px';
                    pointer.style.top = (pos.y - 12.5) + 'px';
                    pointer.innerHTML = `<div style="
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        font-size: 10px;
                        color: white;
                        font-weight: bold;
                    ">${i + 1}</div>`;
                    document.body.appendChild(pointer);
                    return pointer;
                });

                this.threeFingerPointers = pointers;
                return pointers;
            },

            moveThreeFingerPointers(deltaX, deltaY) {
                if (this.threeFingerPointers) {
                    this.threeFingerPointers.forEach(pointer => {
                        const currentX = parseInt(pointer.style.left) + 12.5;
                        const currentY = parseInt(pointer.style.top) + 12.5;
                        pointer.style.left = (currentX + deltaX - 12.5) + 'px';
                        pointer.style.top = (currentY + deltaY - 12.5) + 'px';
                    });
                }
            },

            removeThreeFingerPointers() {
                if (this.threeFingerPointers) {
                    this.threeFingerPointers.forEach(pointer => {
                        if (pointer.parentNode) {
                            pointer.remove();
                        }
                    });
                    this.threeFingerPointers = null;
                }
            },

            // Action tracking
            trackAction(actionType, x, y, additionalData = {}) {
                const timestamp = Date.now();
                const action = {
                    type: actionType,
                    coordinates: {x, y},
                    timestamp,
                    additionalData
                };

                this.actionCoordinates.push(action);
                console.log(`ğŸ“ Action tracked: ${actionType} at (${x}, ${y})`);
                return action;
            },

            trackStateChange(stateType, changeData, triggeredBy = null) {
                const timestamp = Date.now();
                const stateChange = {
                    type: stateType,
                    data: changeData,
                    timestamp,
                    triggeredBy
                };

                this.stateChanges.push(stateChange);
                console.log(`ğŸ”„ State change: ${stateType}`);
                return stateChange;
            },

            compareStates(beforeState, afterState, expectedChanges) {
                const comparison = {
                    timestamp: Date.now(),
                    before: beforeState,
                    after: afterState,
                    expected: expectedChanges,
                    matches: {},
                    failures: []
                };

                // Compare expected changes
                for (const [key, expectedValue] of Object.entries(expectedChanges)) {
                    const beforeValue = this.getNestedValue(beforeState, key);
                    const afterValue = this.getNestedValue(afterState, key);

                    if (typeof expectedValue === 'function') {
                        const result = expectedValue(beforeValue, afterValue);
                        comparison.matches[key] = result;
                        if (!result) {
                            comparison.failures.push({
                                key,
                                expected: 'function check',
                                before: beforeValue,
                                after: afterValue
                            });
                        }
                    } else {
                        const match = afterValue === expectedValue;
                        comparison.matches[key] = match;
                        if (!match) {
                            comparison.failures.push({
                                key,
                                expected: expectedValue,
                                actual: afterValue,
                                before: beforeValue
                            });
                        }
                    }
                }

                comparison.success = comparison.failures.length === 0;

                if (!comparison.success) {
                    console.error('âŒ State comparison failed:', comparison.failures);
                } else {
                    console.log('âœ… State comparison passed');
                }

                return comparison;
            },

            getNestedValue(obj, path) {
                return path.split('.').reduce((current, key) => current && current[key], obj);
            },

            darkenColor(color) {
                // Simple color darkening
                return color.replace(/[0-9a-f]/g, (match) => {
                    const val = parseInt(match, 16);
                    return Math.max(0, val - 3).toString(16);
                });
            },

            // State capture
            captureCurrentState() {
                const state = {
                    timestamp: Date.now(),
                    dom: {
                        shapes: document.querySelectorAll('.tl-shape').length,
                        canvases: document.querySelectorAll('canvas').length,
                        activeElement: document.activeElement?.tagName,
                        scrollPosition: {x: window.scrollX, y: window.scrollY}
                    },
                    editor: {},
                    console: this.consoleLogs.slice(-5),
                    errors: this.errorTraces.slice(-3),
                    performance: {
                        memory: performance.memory ? {
                            used: performance.memory.usedJSHeapSize,
                            total: performance.memory.totalJSHeapSize
                        } : null,
                        functionTimes: Object.fromEntries(this.functionTimings)
                    }
                };

                // Capture TLDraw state
                try {
                    if (window.tlEditor) {
                        state.editor = {
                            camera: window.tlEditor.getCamera(),
                            selectedShapes: window.tlEditor.getSelectedShapes().length,
                            currentTool: window.tlEditor.getCurrentTool?.()?.id,
                            zoom: window.tlEditor.getZoomLevel?.() || window.tlEditor.getCamera?.()?.z
                        };
                    }
                } catch (e) {
                    state.editor.error = e.message;
                }

                // Connect to existing diagnostics
                if (this.unifiedDiagnostics && this.unifiedDiagnostics.captureFullState) {
                    try {
                        state.unified = this.unifiedDiagnostics.captureFullState();
                    } catch (e) {
                        state.unified = {error: e.message};
                    }
                }

                return state;
            },

            // Cleanup
            removeAllPointers() {
                this.activePointers.forEach(pointer => {
                    if (pointer.parentNode) pointer.remove();
                });
                this.activePointers = [];

                this.trailElements.forEach(trail => {
                    if (trail.parentNode) trail.remove();
                });
                this.trailElements = [];

                this.removeThreeFingerPointers();
            },

            // Report generation
            generateReport() {
                return {
                    summary: {
                        totalActions: this.actionCoordinates.length,
                        totalStateChanges: this.stateChanges.length,
                        totalMappings: this.coordinateMappings.length,
                        consoleErrors: this.consoleLogs.filter(log => log.type === 'error').length,
                        errorTraces: this.errorTraces.length
                    },
                    actions: this.actionCoordinates,
                    stateChanges: this.stateChanges,
                    mappings: this.coordinateMappings,
                    consoleLogs: this.consoleLogs,
                    errorTraces: this.errorTraces,
                    performance: {
                        functionTimes: Object.fromEntries(this.functionTimings),
                        slowFunctions: [...this.functionTimings.entries()]
                            .filter(([name, time]) => time > 50)
                            .sort((a, b) => b[1] - a[1])
                    },
                    timestamp: Date.now()
                };
            }
        };

        // Auto-initialize
        window.SNAPEUnified.init();
        """

        self.driver.execute_script(unified_script)
        time.sleep(1)

        # Verify initialization
        status = self.driver.execute_script("""
            return {
                snapeReady: !!window.SNAPEUnified,
                functionsAvailable: window.SNAPEUnified ? Object.keys(window.SNAPEUnified).length : 0,
                diagnosticsConnected: !!(window.SNAPEUnified && window.SNAPEUnified.unifiedDiagnostics)
            };
        """)

        print(f"âœ… Unified diagnostics injected: {status}")
        return status

    def human_like_movement(self, start_x, start_y, end_x, end_y, steps=25):
        """Generate human-like curved movement with highlighted pointers"""
        print(f"ğŸ¯ Human movement: ({start_x}, {start_y}) â†’ ({end_x}, {end_y})")

        # Create starting pointer
        self.driver.execute_script(f"""
            window.SNAPEUnified.createPointer({start_x}, {start_y}, 'movement', '#00ff00');
        """)

        time.sleep(0.3)

        # Generate human-like path
        actions = ActionChains(self.driver)

        for i in range(1, steps + 1):
            t = i / steps

            # Bezier curve with control points
            control_x = (start_x + end_x) / 2 + random.gauss(0, 30)
            control_y = (start_y + end_y) / 2 + random.gauss(0, 30)

            # Calculate smooth curve position
            x = (1-t)**2 * start_x + 2*(1-t)*t * control_x + t**2 * end_x
            y = (1-t)**2 * start_y + 2*(1-t)*t * control_y + t**2 * end_y

            # Add human micro-movements
            x += random.gauss(0, 1.5)
            y += random.gauss(0, 1.5)

            # Update pointer position
            if i % 3 == 0:  # Update every 3rd step for performance
                self.driver.execute_script(f"""
                    const pointers = window.SNAPEUnified.activePointers;
                    if (pointers.length > 0) {{
                        window.SNAPEUnified.movePointer(pointers[pointers.length - 1], {int(x)}, {int(y)}, true);
                    }}
                """)

            # Human-like variable speed
            if i < steps * 0.3:  # Slow start
                pause_time = random.uniform(0.02, 0.04)
            elif i > steps * 0.7:  # Slow end
                pause_time = random.uniform(0.015, 0.03)
            else:  # Faster middle
                pause_time = random.uniform(0.005, 0.015)

            body = self.driver.find_element('tag name', 'body')
            actions.move_to_element_with_offset(body, int(x), int(y))
            actions.pause(pause_time)

        actions.perform()
        time.sleep(0.5)

    def zoom_test_sequence(self, center_x=800, center_y=400):
        """Test zoom in/out with visual indicators and state tracking"""
        print(f"ğŸ” Zoom test sequence at ({center_x}, {center_y})")

        # Capture initial state
        initial_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")

        # Track zoom action
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackAction('zoom_test_start', {center_x}, {center_y}, {{
                testType: 'zoom_sequence',
                expectedActions: ['zoom_in', 'zoom_out']
            }});
        """)

        # Zoom in sequence
        print("ğŸ”+ Zoom in test...")
        self.driver.execute_script(f"""
            window.SNAPEUnified.createPointer({center_x}, {center_y}, 'zoom', '#00ff00');
            window.SNAPEUnified.createZoomIndicator({center_x}, {center_y}, 'in');
        """)

        # Simulate trackpad zoom in (scroll with Ctrl)
        actions = ActionChains(self.driver)
        body = self.driver.find_element('tag name', 'body')
        actions.move_to_element_with_offset(body, center_x, center_y)
        actions.key_down('ctrl')

        # Multiple scroll steps for smooth zoom  
        for i in range(5):
            actions.scroll_by_amount(0, 3)  # Smaller scroll amount
            actions.pause(0.1)

        actions.key_up('ctrl')
        actions.perform()

        time.sleep(1)

        # Capture mid-zoom state
        mid_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")

        # Track zoom in completion
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackStateChange('zoom_in_complete', {{
                camera: window.tlEditor ? window.tlEditor.getCamera() : null
            }}, 'zoom_in_action');
        """)

        # Zoom out sequence
        print("ğŸ”- Zoom out test...")
        self.driver.execute_script(f"""
            window.SNAPEUnified.createZoomIndicator({center_x}, {center_y}, 'out');
        """)

        actions = ActionChains(self.driver)
        actions.move_to_element_with_offset(body, center_x, center_y)
        actions.key_down('ctrl')

        # Multiple scroll steps for smooth zoom out
        for i in range(5):
            actions.scroll_by_amount(0, -120)  # Negative = zoom out
            actions.pause(0.1)

        actions.key_up('ctrl')
        actions.perform()

        time.sleep(1)

        # Capture final state
        final_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")

        # State comparison
        zoom_comparison = self.driver.execute_script(f"""
            return window.SNAPEUnified.compareStates(
                {json.dumps(initial_state)},
                {json.dumps(final_state)},
                {{
                    'editor.camera.z': (before, after) => {{
                        // Zoom should have changed and returned close to original
                        return Math.abs((after || 1) - (before || 1)) < 0.5;
                    }}
                }}
            );
        """)

        # Track zoom completion
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackStateChange('zoom_sequence_complete', {{
                comparison: {json.dumps(zoom_comparison)},
                initialZoom: {initial_state.get('editor', {}).get('camera', {}).get('z', 1)},
                finalZoom: {final_state.get('editor', {}).get('camera', {}).get('z', 1)}
            }}, 'zoom_out_action');
        """)

        print(f"ğŸ“Š Zoom test result: {'âœ… PASSED' if zoom_comparison.get('success') else 'âŒ FAILED'}")

        if not zoom_comparison.get('success'):
            print(f"   Failures: {zoom_comparison.get('failures', [])}")

        return zoom_comparison

    def three_finger_gesture_test(self, start_x=300, start_y=300, end_x=500, end_y=400):
        """Test three-finger gesture with visual feedback"""
        print(f"ğŸ–ï¸ Three-finger gesture: ({start_x}, {start_y}) â†’ ({end_x}, {end_y})")

        # Capture initial state
        initial_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")

        # Track action
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackAction('three_finger_start', {start_x}, {start_y}, {{
                endPosition: {{x: {end_x}, y: {end_y}}},
                gestureType: 'pan'
            }});
        """)

        # Create three-finger visual pointers
        self.driver.execute_script(f"""
            window.SNAPEUnified.createThreeFingerPointers({start_x}, {start_y});
        """)

        time.sleep(1)

        # Animate three-finger movement
        steps = 20
        for i in range(1, steps + 1):
            progress = i / steps
            current_x = start_x + (end_x - start_x) * progress
            current_y = start_y + (end_y - start_y) * progress

            # Add slight curve for natural movement
            curve = math.sin(progress * math.pi) * 15

            self.driver.execute_script(f"""
                window.SNAPEUnified.moveThreeFingerPointers({(current_x - start_x) / steps}, {(current_y - start_y) / steps});
            """)

            time.sleep(0.05)

        # Simulate actual three-finger gesture if system supports it
        three_finger_result = self.driver.execute_script(f"""
            if (window.SNAPEUnified.unifiedDiagnostics && 
                window.SNAPEUnified.unifiedDiagnostics.simulateThreeFingerTouch) {{

                window.SNAPEUnified.unifiedDiagnostics.simulateThreeFingerTouch('down', {{ x: {start_x}, y: {start_y} }});

                setTimeout(() => {{
                    window.SNAPEUnified.unifiedDiagnostics.simulateThreeFingerTouch('move', {{ x: {end_x}, y: {end_y} }});
                }}, 100);

                setTimeout(() => {{
                    window.SNAPEUnified.unifiedDiagnostics.simulateThreeFingerTouch('up', {{ x: {end_x}, y: {end_y} }});
                }}, 300);

                return {{ success: true, method: 'unified_diagnostics' }};
            }} else {{
                return {{ success: false, method: 'visual_only', reason: 'no_three_finger_support' }};
            }}
        """)

        time.sleep(1)

        # Capture final state
        final_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")

        # Track completion
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackStateChange('three_finger_complete', {{
                result: {json.dumps(three_finger_result)},
                visualAnimation: true
            }}, 'three_finger_gesture');
        """)

        # Clean up visual pointers
        self.driver.execute_script("window.SNAPEUnified.removeThreeFingerPointers();")

        print(f"ğŸ–ï¸ Three-finger result: {three_finger_result}")
        return three_finger_result

