            print(f"\nâš ï¸ Console Errors Found:")
            error_logs = [log for log in report['consoleLogs'] if log['type'] == 'error']
            for error in error_logs[-3:]:  # Last 3 errors
                print(f"   {error['args'][0] if error['args'] else 'Unknown error'}")

        # Failed state changes
        failed_changes = [change for change in report['stateChanges'] 
                         if change.get('data', {}).get('comparison', {}).get('success') == False]
        if failed_changes:
            print(f"\nâŒ Failed State Changes:")
            for change in failed_changes:
                print(f"   {change['type']}: {change.get('data', {}).get('comparison', {}).get('failures', [])}")

        # Save report to file
        report_filename = f"snape_unified_report_{int(time.time())}.json"
        with open(report_filename, 'w') as f:
            json.dump(report, f, indent=2, default=str)

        print(f"\nğŸ“„ Report saved: {report_filename}")
        return report

    def run_unified_tests(self):
        """Run all unified SNAPE tests"""
        print("ğŸš€ SNAPE UNIFIED SYSTEM - STARTING TESTS")
        print("=" * 60)

        try:
            # Setup
            driver = self.setup_unified_chrome()
            time.sleep(3)  # Wait for page load

            # Inject diagnostics
            status = self.inject_unified_diagnostics()
            if not status['snapeReady']:
                print("âŒ SNAPE system not ready")
                return

            # Take initial screenshot
            self.driver.save_screenshot("snape_unified_start.png")
            print("ğŸ“¸ Initial state captured")

            # Run comprehensive tests
            test_results = self.comprehensive_pointer_test()

            # Generate report
            report = self.generate_comprehensive_report()

            # Clean up pointers
            self.driver.execute_script("window.SNAPEUnified.removeAllPointers();")

            # Final screenshot
            self.driver.save_screenshot("snape_unified_complete.png")
