                self.driver.quit()

    def grade_test_results(self, test_results, report):
        """Grade the test results"""
        print(f"\nğŸ¯ GRADING TEST RESULTS")
        print(f"=" * 30)

        score = 100
        issues = []

        # Check test completion
        zoom_tests = [r for r in test_results if r[0] == 'zoom_test']
        if zoom_tests:
            zoom_result = zoom_tests[0][1]
            if zoom_result.get('success'):
                print("âœ… Zoom test: PASSED")
            else:
                print("âŒ Zoom test: FAILED")
                score -= 20
                issues.append("Zoom functionality issues")
        else:
            score -= 25
            issues.append("No zoom tests completed")

        # Check three-finger tests
        three_finger_tests = [r for r in test_results if r[0] == 'three_finger_test']
        if three_finger_tests:
            three_finger_result = three_finger_tests[0][1]
            if three_finger_result.get('success'):
                print("âœ… Three-finger test: PASSED")
            else:
                print("âš ï¸ Three-finger test: LIMITED (visual only)")
                score -= 10
        else:
            score -= 15
            issues.append("Three-finger tests incomplete")

        # Check performance
        slow_functions = len(report['performance']['slowFunctions'])
        if slow_functions > 0:
            print(f"âš ï¸ Performance: {slow_functions} slow functions")
            score -= min(slow_functions * 5, 20)
        else:
            print("âœ… Performance: No slow functions")

        # Check errors
        error_count = report['summary']['consoleErrors']
        if error_count > 0:
            print(f"âš ï¸ Errors: {error_count} console errors")
            score -= min(error_count * 3, 15)
        else:
            print("âœ… Errors: No console errors")

        # Final grade
        if score >= 95:
            grade = "A+"
        elif score >= 90:
            grade = "A"
        elif score >= 85:
            grade = "A-"
        elif score >= 80:
            grade = "B+"
        elif score >= 75:
            grade = "B"
        elif score >= 70:
            grade = "B-"
        else:
            grade = "C"

        print(f"\nğŸ† FINAL GRADE: {score}/100 ({grade})")

        if issues:
            print(f"\nğŸ“ Issues to address:")
            for issue in issues:
                print(f"   â€¢ {issue}")

        return {'score': score, 'grade': grade, 'issues': issues}

def main():
    """Main entry point for SNAPE Unified System"""
    print("ğŸš€ SNAPE UNIFIED TESTING SYSTEM")
    print("=" * 50)
    print("ğŸ¯ All pointer behaviors, gestures, zoom testing")
