                console.error = (...args) => {
                    this.consoleLogs.push({
                        type: 'error', 
                        args: args.map(String), 
                        timestamp: Date.now(),
                        stack: new Error().stack
                    });
                    this.errorTraces.push({
                        message: args.join(' '),
