            window.SNAPEUnified.createZoomIndicator({center_x}, {center_y}, 'in');
        """)

        # Use JavaScript wheel events for more reliable zoom
        self.driver.execute_script(f"""
            const canvas = document.querySelector('canvas') || document.body;
            const rect = canvas.getBoundingClientRect();

            // Simulate zoom in with wheel events
            for (let i = 0; i < 5; i++) {{
                const wheelEvent = new WheelEvent('wheel', {{
                    clientX: {center_x},
                    clientY: {center_y},
                    deltaY: -120, // Negative for zoom in
                    ctrlKey: true,
                    bubbles: true,
                    cancelable: true
                }});
                canvas.dispatchEvent(wheelEvent);
            }}
        """)

        time.sleep(1)

        # Capture mid-zoom state
        mid_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")

        # Track zoom in completion
