    def three_finger_gesture_test(self, start_x=300, start_y=300, end_x=500, end_y=400):
        """Test three-finger pan gesture with visual feedback"""
        print(f"ğŸ–ï¸ Three-finger pan: ({start_x}, {start_y}) â†’ ({end_x}, {end_y})")

