    def pinch_zoom_test(self, center_x=800, center_y=400, zoom_type='in'):
        """Test pinch-to-zoom gesture"""
        print(f"ğŸ¤ Pinch zoom {zoom_type} test at ({center_x}, {center_y})")

        # Track pinch action
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackAction('pinch_zoom_start', {center_x}, {center_y}, {{
                zoomType: '{zoom_type}',
                gestureType: 'pinch'
            }});
        """)

        # Create visual feedback for pinch
        self.driver.execute_script(f"""
            // Create two pointers for pinch gesture
            const pointer1 = window.SNAPEUnified.createPointer({center_x - 30}, {center_y}, 'pinch', '#ff00ff');
            const pointer2 = window.SNAPEUnified.createPointer({center_x + 30}, {center_y}, 'pinch', '#ff00ff');
        """)

        time.sleep(0.5)

        # Simulate pinch gesture
        zoom_direction = 1 if zoom_type == 'in' else -1
        steps = 10

        for i in range(steps):
            progress = (i + 1) / steps

            if zoom_type == 'in':
                # Pinch in - fingers move together
                finger1_x = center_x - 30 + (progress * 25)
                finger2_x = center_x + 30 - (progress * 25)
            else:
                # Pinch out - fingers move apart
                finger1_x = center_x - 30 - (progress * 25)
                finger2_x = center_x + 30 + (progress * 25)

            # Update pointer positions
            self.driver.execute_script(f"""
                const pointers = window.SNAPEUnified.activePointers;
                if (pointers.length >= 2) {{
                    window.SNAPEUnified.movePointer(pointers[pointers.length - 2], {finger1_x}, {center_y}, true);
                    window.SNAPEUnified.movePointer(pointers[pointers.length - 1], {finger2_x}, {center_y}, true);
                }}
            """)

            time.sleep(0.1)

        # Simulate actual pinch zoom via touch events
        pinch_result = self.driver.execute_script(f"""
            try {{
                const canvas = document.querySelector('canvas') || document.body;

                // Create pinch gesture with touch events
                const touches = [
                    {{ identifier: 0, clientX: {center_x - 30}, clientY: {center_y} }},
                    {{ identifier: 1, clientX: {center_x + 30}, clientY: {center_y} }}
                ];

                const touchStart = new TouchEvent('touchstart', {{
                    touches: touches,
                    bubbles: true,
                    cancelable: true
                }});
                canvas.dispatchEvent(touchStart);

                // Simulate pinch movement
                const finalDistance = '{zoom_type}' === 'in' ? 10 : 70;
                const touchesEnd = [
                    {{ identifier: 0, clientX: {center_x} - finalDistance/2, clientY: {center_y} }},
                    {{ identifier: 1, clientX: {center_x} + finalDistance/2, clientY: {center_y} }}
                ];

                setTimeout(() => {{
                    const touchMove = new TouchEvent('touchmove', {{
                        touches: touchesEnd,
                        bubbles: true,
                        cancelable: true
                    }});
                    canvas.dispatchEvent(touchMove);
                }}, 200);

                setTimeout(() => {{
                    const touchEnd = new TouchEvent('touchend', {{
                        touches: [],
                        bubbles: true,
                        cancelable: true
                    }});
                    canvas.dispatchEvent(touchEnd);
                }}, 400);

                // Also try direct zoom via TLDraw
                if (window.tlEditor && window.tlEditor.setCamera) {{
                    const currentCamera = window.tlEditor.getCamera();
                    const zoomFactor = '{zoom_type}' === 'in' ? 1.2 : 0.8;

                    window.tlEditor.setCamera({{
                        x: currentCamera.x,
                        y: currentCamera.y,
                        z: currentCamera.z * zoomFactor
                    }});
                }}

                return {{ success: true, method: 'touch_and_camera' }};
            }} catch (e) {{
                return {{ success: false, error: e.message }};
            }}
        """)

        time.sleep(1)

        # Track completion
        self.driver.execute_script(f"""
