    def canvas_scroll_test(self, scroll_direction='down', scroll_amount=300):
        """Test actual canvas page scrolling up and down"""
        print(f"ğŸ“œ Canvas scroll test: {scroll_direction} by {scroll_amount}px")

        # Capture initial scroll state
        initial_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")
        initial_scroll_y = initial_state.get('dom', {}).get('scrollPosition', {}).get('y', 0)

        # Track scroll action
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackAction('canvas_scroll_start', 800, 400, {{
                direction: '{scroll_direction}',
                amount: {scroll_amount},
                initialScrollY: {initial_scroll_y}
            }});
        """)

        # Create visual pointer for scroll
        center_x, center_y = 800, 400
        self.driver.execute_script(f"""
            window.SNAPEUnified.createPointer({center_x}, {center_y}, 'scroll', '#ffaa00');
        """)

        # Perform actual page scroll with multiple steps for smooth scrolling
        print(f"ğŸ“œ Scrolling canvas page {scroll_direction}...")
        scroll_delta = scroll_amount if scroll_direction == 'down' else -scroll_amount
        steps = 10
        step_amount = scroll_delta // steps

        for i in range(steps):
            self.driver.execute_script(f"window.scrollBy(0, {step_amount});")

            # Update pointer position to show scroll progress
            progress_y = center_y + (i * 10) if scroll_direction == 'down' else center_y - (i * 10)
            self.driver.execute_script(f"""
                const pointers = window.SNAPEUnified.activePointers;
                if (pointers.length > 0) {{
                    window.SNAPEUnified.movePointer(pointers[pointers.length - 1], {center_x}, {progress_y}, true);
                }}
            """)

            time.sleep(0.05)

        time.sleep(1)

        # Capture final scroll state
        final_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")
        final_scroll_y = final_state.get('dom', {}).get('scrollPosition', {}).get('y', 0)
        actual_scroll_delta = final_scroll_y - initial_scroll_y

        # Track scroll completion with validation
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackStateChange('canvas_scroll_complete', {{
                initialScrollY: {initial_scroll_y},
                finalScrollY: {final_scroll_y},
                expectedDelta: {scroll_delta},
                actualDelta: {actual_scroll_delta},
                success: Math.abs({actual_scroll_delta} - {scroll_delta}) < 50
            }}, 'canvas_scroll_action');
        """)

        success = abs(actual_scroll_delta - scroll_delta) < 50
        print(f"ğŸ“Š Canvas scroll result: {'âœ… PASSED' if success else 'âŒ FAILED'}")
        print(f"   Expected: {scroll_delta}px, Actual: {actual_scroll_delta}px")

        return {
            'success': success,
            'initialScrollY': initial_scroll_y,
            'finalScrollY': final_scroll_y,
            'expectedDelta': scroll_delta,
            'actualDelta': actual_scroll_delta
        }

