        steps = 15
        for i in range(1, steps + 1):
            progress = i / steps
            current_x = start_x + (end_x - start_x) * progress
            current_y = start_y + (end_y - start_y) * progress

            # Add natural drawing curve
            curve = math.sin(progress * math.pi) * 10
            curved_x = current_x + curve
            curved_y = current_y

            # Move three-finger pointers
            self.driver.execute_script(f"""
                window.SNAPEUnified.moveThreeFingerPointers({(curved_x - start_x) / steps}, {(curved_y - start_y) / steps});
