            # Simulate drawing movement
            elif i < steps:
                self.driver.execute_script(f"""
                    const canvas = document.querySelector('canvas') || document.body;
                    const pointerMove = new PointerEvent('pointermove', {{
                        clientX: {curved_x},
                        clientY: {curved_y},
                        pointerId: 1,
                        bubbles: true,
                        cancelable: true
                    }});
                    canvas.dispatchEvent(pointerMove);
