            # If this is the end of drawing, simulate mouseup
            elif i == steps:
                self.driver.execute_script(f"""
                    const canvas = document.querySelector('canvas') || document.body;
                    const pointerUp = new PointerEvent('pointerup', {{
                        clientX: {curved_x},
                        clientY: {curved_y},
                        pointerId: 1,
                        bubbles: true,
                        cancelable: true
                    }});
                    canvas.dispatchEvent(pointerUp);

                    console.log('ğŸ–ï¸ Three-finger drawing completed at ({curved_x}, {curved_y})');
                """)

            time.sleep(0.08)

        time.sleep(1)

        # Capture final state to check if shapes were created
        final_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")
