        # Create three-finger visual pointers
        self.driver.execute_script(f"""
            window.SNAPEUnified.createThreeFingerPointers({start_x}, {start_y});
        """)

        time.sleep(1)

        # Animate three-finger pan movement
        steps = 20
        for i in range(1, steps + 1):
            progress = i / steps
            current_x = start_x + (end_x - start_x) * progress
            current_y = start_y + (end_y - start_y) * progress

            # Add slight curve for natural movement
            curve = math.sin(progress * math.pi) * 15

            self.driver.execute_script(f"""
                window.SNAPEUnified.moveThreeFingerPointers({(current_x - start_x) / steps}, {(current_y - start_y) / steps});
            """)

            time.sleep(0.05)

        # Simulate actual three-finger pan gesture
        three_finger_result = self.driver.execute_script(f"""
            // Try multiple methods for three-finger pan
            let panResult = {{ success: false, methods: [] }};

            // Method 1: Use unified diagnostics if available
            if (window.SNAPEUnified.unifiedDiagnostics && 
                window.SNAPEUnified.unifiedDiagnostics.simulateThreeFingerTouch) {{

                try {{
                    window.SNAPEUnified.unifiedDiagnostics.simulateThreeFingerTouch('down', {{ x: {start_x}, y: {start_y} }});

                    setTimeout(() => {{
                        window.SNAPEUnified.unifiedDiagnostics.simulateThreeFingerTouch('move', {{ x: {end_x}, y: {end_y} }});
                    }}, 100);

                    setTimeout(() => {{
                        window.SNAPEUnified.unifiedDiagnostics.simulateThreeFingerTouch('up', {{ x: {end_x}, y: {end_y} }});
                    }}, 300);

                    panResult.success = true;
                    panResult.methods.push('unified_diagnostics');
                }} catch (e) {{
                    panResult.methods.push('unified_diagnostics_failed: ' + e.message);
                }}
            }}

            // Method 2: Direct camera manipulation if TLDraw available
            if (window.tlEditor && window.tlEditor.setCamera) {{
                try {{
                    const currentCamera = window.tlEditor.getCamera();
                    const deltaX = {end_x - start_x};
                    const deltaY = {end_y - start_y};

                    window.tlEditor.setCamera({{
                        x: currentCamera.x - deltaX,
                        y: currentCamera.y - deltaY,
                        z: currentCamera.z
                    }});

                    panResult.success = true;
                    panResult.methods.push('tldraw_camera');
                }} catch (e) {{
                    panResult.methods.push('tldraw_camera_failed: ' + e.message);
                }}
            }}

            // Method 3: Touch events simulation
            try {{
                const canvas = document.querySelector('canvas') || document.body;

                // Create three touch points
                const touches = [
                    {{ identifier: 0, clientX: {start_x}, clientY: {start_y} }},
                    {{ identifier: 1, clientX: {start_x + 40}, clientY: {start_y + 20} }},
                    {{ identifier: 2, clientX: {start_x + 80}, clientY: {start_y + 40} }}
                ];

                const touchStart = new TouchEvent('touchstart', {{
                    touches: touches,
                    bubbles: true,
                    cancelable: true
                }});
                canvas.dispatchEvent(touchStart);

                // Simulate pan movement
                setTimeout(() => {{
                    const touchesMove = [
                        {{ identifier: 0, clientX: {end_x}, clientY: {end_y} }},
                        {{ identifier: 1, clientX: {end_x + 40}, clientY: {end_y + 20} }},
                        {{ identifier: 2, clientX: {end_x + 80}, clientY: {end_y + 40} }}
                    ];

                    const touchMove = new TouchEvent('touchmove', {{
                        touches: touchesMove,
                        bubbles: true,
                        cancelable: true
                    }});
                    canvas.dispatchEvent(touchMove);
                }}, 200);

                // End touch
                setTimeout(() => {{
                    const touchEnd = new TouchEvent('touchend', {{
                        touches: [],
                        bubbles: true,
                        cancelable: true
                    }});
                    canvas.dispatchEvent(touchEnd);
                }}, 400);

                panResult.success = true;
                panResult.methods.push('touch_events');
            }} catch (e) {{
                panResult.methods.push('touch_events_failed: ' + e.message);
            }}

            return panResult;
        """)

        time.sleep(1)

        # Capture final state
        final_state = self.driver.execute_script("return window.SNAPEUnified.captureCurrentState();")

        # Track completion
        self.driver.execute_script(f"""
            window.SNAPEUnified.trackStateChange('three_finger_pan_complete', {{
                result: {json.dumps(three_finger_result)},
                visualAnimation: true,
