    56→class SlitherChromeContainer {
    57→    constructor() {
    58→        this.mainWindow = null;
    59→        this.chromeLauncher = new NativeChromeManager();
    60→        this.connectionPool = new ConnectionPool();
    61→        this.chromeInstance = null;
    62→        this.messageQueue = [];
    63→        this.ptyProcess = null; // Real terminal process
    64→        this.metricsInterval = null; // Live metrics timer
    65→        this.cdpClient = null; // CDP connection client
    66→    }
    67→
    68→    async createMainWindow() {
    69→        this.mainWindow = new BrowserWindow({
    70→            width: 1400,
    71→            height: 900,
    72→            webPreferences: {
    73→                preload: path.join(__dirname, 'preload.js'),
    74→                contextIsolation: true,  // Secure by default
    75→                nodeIntegration: false,  // Secure by default
    76→                webviewTag: true         // Enable <webview> for Chrome DevTools
    77→            },
    78→            icon: path.join(__dirname, 'assets', 'icon.png'),
    79→            title: 'Slither Chrome Container v1.0'
    80→        });
    81→
    82→        await this.mainWindow.loadFile('renderer/index.html');
    83→        
    84→        // Force hard refresh to load new terminal interface - clear ALL cache
    85→        this.mainWindow.webContents.session.clearCache();
    86→        this.mainWindow.webContents.reloadIgnoringCache();
    87→
    88→        // Enable dev tools to debug preload issues
    89→        this.mainWindow.webContents.openDevTools();
    90→
    91→        // Handle window closed - but don't cleanup, container should stay running
    92→        this.mainWindow.on('closed', () => {
    93→            console.log('🔄 Main window closed, but container continues running...');
    94→            this.mainWindow = null;
    95→        });
    96→    }
    97→
    98→    // Wait for Chrome to be ready before accepting connections
    99→    async waitForChromeReady(port, timeout = 10000) {
   100→        const startTime = Date.now();
   101→        while (Date.now() - startTime < timeout) {
   102→            try {
   103→                const response = await fetch(`http://localhost:${port}/json/version`);
   104→                if (response.ok) {
   105→                    console.log(`✅ Chrome on port ${port} is ready.`);
   106→                    return true;
   107→                }
   108→            } catch (error) {
   109→                // Ignore connection errors and retry
   110→            }
   111→            // Wait 200ms before trying again
   112→            await new Promise(resolve => setTimeout(resolve, 200));
   113→        }
   114→        throw new Error(`Chrome on port ${port} did not become ready in time.`);
   115→    }
   116→
   117→    async connectToChrome(port) {
   118→        if (this.cdpClient) {
   119→            try {
   120→                await this.cdpClient.close();
   121→            } catch (e) {
   122→                // Ignore errors closing old client
   123→            }
   124→            this.cdpClient = null;
   125→        }
   126→
   127→        try {
   128→            console.log(`🔗 Connecting to Chrome on port ${port} using CDP...`);
   129→            // Connect to the first available target (usually the primary page)
   130→            this.cdpClient = await CDP({ port });
   131→
   132→            // Enable Page and Runtime domains for commands
   133→            await Promise.all([
   134→                this.cdpClient.Page.enable(),
   135→                this.cdpClient.Runtime.enable(),
   136→                this.cdpClient.Target.setDiscoverTargets({ discover: true })
   137→            ]);
   138→
   139→            // Listen for disconnection events
   140→            this.cdpClient.on('disconnect', () => {
   141→                console.log('🔴 CDP client disconnected.');
   142→                this.cdpClient = null;
   143→                this.updateRendererStatus();
   144→            });
   145→
   146→            // **ADD THIS BLOCK TO LISTEN FOR BACKEND ERRORS**
   147→            this.cdpClient.on('error', (err) => {
   148→                console.error('🔴 CDP Client Error:', err);
   149→                const errorObject = {
   150→                    source: 'CDP Backend',
   151→                    message: err.message,
   152→                    timestamp: new Date().toISOString(),
   153→                    type: 'error'
   154→                };
   155→                if (this.mainWindow && !this.mainWindow.isDestroyed()) {
   156→                    this.mainWindow.webContents.send('new-error-log', errorObject);
   157→                }
   158→            });
   159→
   160→            console.log('✅ CDP connection successful.');
   161→            return true;
   162→        } catch (error) {
   163→            console.error('❌ CDP connection failed:', error);
   164→            this.cdpClient = null;
   165→            return false;
   166→        }
   167→    }
   168→
   169→    async initializeChrome() {
   170→        // **ANTI-GHOSTING FIX**: Always kill any lingering instances before starting.
   171→        console.log('🧹 Checking for and killing any lingering Chrome instances...');
   172→        if (this.chromeInstances && this.chromeInstances.length > 0) {
   173→            const instancesToKill = [...this.chromeInstances];
   174→            for (const instance of instancesToKill) {
   175→                await this.chromeLauncher.killChromeInstance(instance);
   176→            }
   177→            await new Promise(resolve => setTimeout(resolve, 500)); // Give processes time to die
   178→            this.chromeInstances = [];
   179→            this.chromeInstance = null;
   180→            console.log('✅ Lingering instances cleaned up.');
   181→        }
   182→
   183→        try {
   184→            console.log('🚀 Initializing a new Chrome instance...');
   185→            if (!this.chromeInstances) this.chromeInstances = [];
   186→
   187→            const chromeResult = await this.chromeLauncher.launchChrome();
   188→            if (!chromeResult.success) throw new Error(chromeResult.error || 'Failed to launch Chrome');
   189→
   190→            const newInstance = chromeResult.chromeInstance;
   191→            await this.waitForChromeReady(newInstance.port);
   192→
   193→            this.chromeInstances.push(newInstance);
   194→            if (!this.chromeInstance) this.chromeInstance = newInstance;
   195→            if (!this.connectionPool.isInitialized()) await this.connectionPool.initialize(this.chromeInstance.port);
   196→            
   197→            // Establish CDP connection for stable WebSocket communication
   198→            await this.connectToChrome(newInstance.port);
   199→
   200→            console.log(`✅ Chrome instance #${this.chromeInstances.length} launched on port: ${newInstance.port} (PID: ${newInstance.pid})`);
   201→            
   202→            newInstance.process.on('exit', (code) => {
   203→                console.log(`🔴 Chrome instance with PID ${newInstance.pid} exited with code: ${code}.`);
   204→                this.chromeInstances = this.chromeInstances.filter(inst => inst.pid !== newInstance.pid);
   205→                if (this.chromeInstance && this.chromeInstance.pid === newInstance.pid) {
   206→                    this.chromeInstance = this.chromeInstances[0] || null;
   207→                }
   208→                this.updateRendererStatus();
   209→            });
   210→
   211→            this.updateRendererStatus();
   212→            return { success: true };
   213→        } catch (error) {
   214→            console.error('❌ Failed to initialize Chrome:', error);
   215→            this.updateRendererStatus({ error: error.message });
   216→            return { success: false, error: error.message };
   217→        }
   218→    }
   219→
   220→    setupIPC() {
   221→        console.log('🔌 Setting up IPC handlers...');
   222→        
   223→        // Handle get chrome status request (register this first)
   224→        console.log('📝 Registering handler: get-chrome-status');
   225→        ipcMain.handle('get-chrome-status', async (event) => {
   226→            try {
   227→                const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
   228→                const status = {
   229→                    running: isRunning,
   230→                    pid: this.chromeInstance?.pid || null,
   231→                    port: this.chromeInstance?.port || null,
   232→                    instanceCount: this.chromeInstances?.length || 0,
   233→                    memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024)
   234→                };
   235→                
   236→                safeLog('📡 Sending chrome status:', status);
   237→                return status;
   238→            } catch (error) {
   239→                console.error('❌ Error getting chrome status:', error);
   240→                return {
   241→                    running: false,
   242→                    error: error.message,
   243→                    instanceCount: 0
   244→                };
   245→            }
   246→        });
   247→
   248→        // Handle DevTools reconnection (full implementation)
   249→        console.log('📝 Registering handler: reconnect-devtools');
   250→        ipcMain.handle('reconnect-devtools', async (event) => {
   251→            try {
   252→                console.log('🔗 Reconnecting to Chrome DevTools...');
   253→                
   254→                if (!this.chromeInstance || !this.chromeInstance.port) {
   255→                    throw new Error('No Chrome instance available to reconnect to');
   256→                }
   257→
   258→                // Close existing CDP connection if it exists
   259→                if (this.cdpClient) {
   260→                    try {
   261→                        await this.cdpClient.close();
   262→                        console.log('🔴 Closed existing CDP connection');
   263→                    } catch (error) {
   264→                        console.log('⚠️ Error closing old CDP connection (ignored):', error.message);
   265→                    }
   266→                    this.cdpClient = null;
   267→                }
   268→
   269→                // Wait for Chrome to be ready
   270→                await this.waitForChromeReady(this.chromeInstance.port, 5000);
   271→                
   272→                // Re-establish CDP connection
   273→                const connected = await this.connectToChrome(this.chromeInstance.port);
   274→                
   275→                if (connected) {
   276→                    console.log('✅ DevTools reconnection successful');
   277→                    this.updateRendererStatus();
   278→                    return { success: true, message: 'DevTools reconnected successfully' };
   279→                } else {
   280→                    throw new Error('Failed to establish CDP connection');
   281→                }
   282→                
   283→            } catch (error) {
   284→                console.error('❌ DevTools reconnection failed:', error);
   285→                this.updateRendererStatus();
   286→                return { success: false, error: error.message };
   287→            }
   288→        });
   289→        console.log('✅ reconnect-devtools handler registered successfully');
   290→
   291→        // Handle MD file import
   292→        ipcMain.handle('import-md-file', async () => {
   293→            try {
   294→                const result = await dialog.showOpenDialog(this.mainWindow, {
   295→                    properties: ['openFile', 'multiSelections'],
   296→                    filters: [
   297→                        { name: 'Markdown Files', extensions: ['md'] },
   298→                        { name: 'Text Files', extensions: ['txt'] },
   299→                        { name: 'All Files', extensions: ['*'] }
   300→                    ]
   301→                });
   302→
   303→                if (!result.canceled && result.filePaths.length > 0) {
   304→                    const fs = require('fs');
   305→                    const path = require('path');
   306→                    
   307→                    const files = [];
   308→                    
   309→                    for (const filePath of result.filePaths) {
   310→                        try {
   311→                            const content = fs.readFileSync(filePath, 'utf8');
   312→                            const fileName = path.basename(filePath);
   313→                            
   314→                            files.push({
   315→                                name: fileName,
   316→                                path: filePath,
   317→                                content: content,
   318→                                size: content.length
   319→                            });
   320→                            
   321→                            console.log(`✅ Loaded file: ${fileName} (${content.length} chars)`);
   322→                        } catch (error) {
   323→                            console.error(`❌ Error reading file ${filePath}:`, error);
   324→                        }
   325→                    }
   326→                    
   327→                    return {
   328→                        success: true,
   329→                        files: files,
   330→                        count: files.length
   331→                    };
   332→                } else {
   333→                    return {
   334→                        success: false,
   335→                        error: 'No files selected',
   336→                        files: []
   337→                    };
   338→                }
   339→            } catch (error) {
   340→                console.error('❌ MD import error:', error);
   341→                return {
   342→                    success: false,
   343→                    error: error.message,
   344→                    files: []
   345→                };
   346→            }
   347→        });
   348→
   349→        // Handle connection status requests
   350→        ipcMain.handle('get-connection-status', async () => {
   351→            try {
   352→                // Get real Chrome status
   353→                const chromeRunning = this.chromeInstance !== null;
   354→                const chromePid = this.chromeInstance?.pid || null;
   355→                const debugPort = this.chromeInstance?.port || null;
   356→                
   357→                // Get real connection data
   358→                const activeConnections = this.connectionPool.getActiveConnectionCount();
   359→                const sessions = this.connectionPool.getActiveSessions();
   360→                
   361→                // Check if Chrome is actually responsive
   362→                let chromeResponsive = false;
   363→                if (chromeRunning && debugPort) {
   364→                    try {
   365→                        const response = await fetch(`http://localhost:${debugPort}/json/version`, {
   366→                            signal: AbortSignal.timeout(2000)
   367→                        });
   368→                        chromeResponsive = response.ok;
   369→                    } catch (error) {
   370→                        chromeResponsive = false;
   371→                    }
   372→                }
   373→                
   374→                // Check WebSocket server status
   375→                let websocketRunning = false;
   376→                try {
   377→                    const wsResponse = await fetch('http://localhost:9223/health', {
   378→                        signal: AbortSignal.timeout(2000)
   379→                    });
   380→                    websocketRunning = wsResponse.ok;
   381→                } catch (error) {
   382→                    websocketRunning = false;
   383→                }
   384→                
   385→                // Check Slither connection
   386→                let slitherConnected = false;
   387→                try {
   388→                    const slitherResponse = await fetch('http://localhost:9223/status', {
   389→                        signal: AbortSignal.timeout(2000)
   390→                    });
   391→                    if (slitherResponse.ok) {
   392→                        const data = await slitherResponse.json();
   393→                        slitherConnected = data.connected || false;
   394→                    }
   395→                } catch (error) {
   396→                    slitherConnected = false;
   397→                }
   398→                
   399→                // Check if Chrome needs installation (only if not running)
   400→                let needsInstallation = false;
   401→                if (!chromeRunning) {
   402→                    needsInstallation = !(await this.chromeLauncher.isChromeInstalled());
   403→                }
   404→                
   405→                return {
   406→                    chromeRunning,
   407→                    chromePid,
   408→                    debugPort,
   409→                    chromeResponsive,
   410→                    websocketRunning,
   411→                    slitherConnected,
   412→                    activeConnections,
   413→                    sessions,
   414→                    error: null,
   415→                    needsInstallation
   416→                };
   417→            } catch (error) {
   418→                console.error('Error getting connection status:', error);
   419→                return {
   420→                    chromeRunning: false,
   421→                    chromePid: null,
   422→                    debugPort: null,
   423→                    chromeResponsive: false,
   424→                    websocketRunning: false,
   425→                    slitherConnected: false,
   426→                    activeConnections: 0,
   427→                    sessions: [],
   428→                    error: error.message,
   429→                    needsInstallation: false
   430→                };
   431→            }
   432→        });
   433→
   434→        // Handle clear sessions request
   435→        ipcMain.handle('clear-sessions', async () => {
   436→            console.log('🧹 Clearing all Slither sessions...');
   437→            await this.connectionPool.clearAllSessions();
   438→            return true;
   439→        });
   440→
   441→        // **NEW**: Handle force kill WebSocket connections
   442→        ipcMain.handle('force-kill-websockets', async () => {
   443→            console.log('🚨 Force killing all WebSocket connections...');
   444→            this.connectionPool.forceKillAllConnections();
   445→            return { success: true, message: 'All WebSocket connections force killed' };
   446→        });
   447→
   448→        // **NEW**: Handle WebSocket server restart
   449→        ipcMain.handle('restart-websocket-server', async () => {
   450→            try {
   451→                console.log('🔄 Restarting WebSocket server...');
   452→                await this.connectionPool.switchToNewServer();
   453→                return { success: true, message: 'WebSocket server restarted successfully' };
   454→            } catch (error) {
   455→                console.error('❌ WebSocket server restart failed:', error);
   456→                return { success: false, error: error.message };
   457→            }
   458→        });
   459→
   460→        // Handle user message queue
   461→        ipcMain.handle('queue-user-message', (event, message) => {
   462→            console.log('💬 User message queued:', message);
   463→            // Store message for Slither to retrieve
   464→            this.connectionPool.queueUserMessage(message);
   465→            return true;
   466→        });
   467→
   468→        // Handle real terminal streaming - write user input to PTY
   469→        ipcMain.on('terminal-write', (event, data) => {
   470→            console.log('📤 Received terminal write:', JSON.stringify(data));
   471→            if (this.ptyProcess) {
   472→                this.ptyProcess.write(data);
   473→            } else {
   474→                console.warn('⚠️ No PTY process available for write');
   475→            }
   476→        });
   477→
   478→        // Handle terminal resize
   479→        ipcMain.on('terminal-resize', (event, { cols, rows }) => {
   480→            if (this.ptyProcess) {
   481→                this.ptyProcess.resize(cols, rows);
   482→            }
   483→        });
   484→
   485→        // Handle webview errors
   486→        ipcMain.on('webview-error', (event, error) => {
   487→            console.error(`❌ Error from DevTools Viewport: ${error.message}`);
   488→            const errorObject = {
   489→                source: error.source || 'WebView',
   490→                message: error.message,
   491→                timestamp: new Date().toISOString(),
   492→                type: 'error'
   493→            };
   494→            // Broadcast the formatted error back to the renderer
   495→            if (this.mainWindow && !this.mainWindow.isDestroyed()) {
   496→                this.mainWindow.webContents.send('new-error-log', errorObject);
   497→            }
   498→        });
   499→
   500→        // Handle Chrome initialization (on-demand)
   501→        ipcMain.handle('initialize-chrome', async (event) => {
   502→            try {
   503→                console.log('🚀 User/AI requested Chrome initialization...');
   504→                const success = await this.initializeChrome();
   505→                // CRITICAL: Update the UI after initialization
   506→                this.updateRendererStatus();
   507→                return { success };
   508→            } catch (error) {
   509→                console.error('❌ Chrome initialization error:', error);
   510→                // Update UI even on error to show error state
   511→                this.updateRendererStatus();
   512→                return { success: false, error: error.message };
   513→            }
   514→        });
   515→
   516→        // Handle Chrome restart
   517→        ipcMain.handle('restart-chrome', async (event) => {
   518→            try {
   519→                console.log('🔄 Restarting Chrome via full cleanup and re-initialization...');
   520→                
   521→                // 1. Perform a full cleanup to shut down everything gracefully.
   522→                await this.cleanup();
   523→                
   524→                // 2. Wait a moment for the OS to release ports and processes.
   525→                await new Promise(resolve => setTimeout(resolve, 500));
   526→                
