// --- 4. CHROME VIEWPORT HELPER FUNCTIONS ---
async function setupChromeViewport(port) {
    try {
        console.log(`🎨 Setting up Canvas viewport (DevTools disabled)`);

        // Instead of loading DevTools, try to navigate to canvas
        const canvasUrl = await detectCanvasUrl();

        if (canvasUrl) {
            console.log(`🎨 Loading canvas at: ${canvasUrl}`);
            setTimeout(() => {
                if (chromeViewport.src !== canvasUrl) {
                    chromeViewport.src = canvasUrl;
                }
            }, 1000);
        } else {
            // Show a simple placeholder instead of DevTools
            const placeholderUrl = `data:text/html,
                <div style="display:flex;align-items:center;justify-content:center;height:100vh;font-family:Arial;background:#1a1a1a;color:#fff;">
                    <div style="text-align:center;">
                        <h2>🎨 Canvas Interface</h2>
                        <p>Canvas server not detected</p>
                        <p>Start your canvas server and click "Go to Canvas" to navigate</p>
                        <p style="color:#888;font-size:12px;">Chrome is running on port ${port}</p>
                    </div>
                </div>`;
            console.log(`📋 Loading canvas placeholder`);
            chromeViewport.src = placeholderUrl;
        }

    } catch (error) {
        console.error('❌ Failed to setup Canvas viewport:', error);
    }
}

// Helper function to detect canvas server
async function detectCanvasUrl() {
    const commonPorts = [8000, 8001, 8002, 8003, 8004, 8005];

    for (const port of commonPorts) {
        try {
            const response = await fetch(`http://localhost:${port}/canvas.html`, {
                method: 'HEAD',
                signal: AbortSignal.timeout(2000)
            });
            if (response.ok) {
                return `http://localhost:${port}/canvas.html`;
            }
        } catch (error) {
            // Port not available, continue
        }
    }

    return null;
}

