// renderer/app.js - FINAL Corrected Version

console.log('🚀 Loading app.js - CDN Terminal Version v13');

// Screenshot functionality implementation
function setupScreenshotFeature() {
    const takeScreenshotBtn = document.getElementById('take-screenshot');
    const clearScreenshotsBtn = document.getElementById('clear-screenshots');
    const screenshotsContainer = document.getElementById('screenshots-container');
    const screenshotCount = document.getElementById('screenshot-count');

    let screenshots = [];

    // Update screenshot count display
    function updateScreenshotCount() {
        screenshotCount.textContent = `${screenshots.length} image${screenshots.length !== 1 ? 's' : ''}`;
    }

    // Format timestamp for display
    function formatTimestamp(timestamp) {
        const date = new Date(timestamp);
        return date.toLocaleString();
    }

    // Create screenshot item element
    function createScreenshotItem(screenshot) {
        const item = document.createElement('div');
        item.className = 'screenshot-item';
        item.dataset.timestamp = screenshot.timestamp;

        const pathDisplay = screenshot.filepath !== 'memory-only' ? screenshot.relativePath : 'Stored in memory only';

        item.innerHTML = `
            <div class="screenshot-header" onclick="toggleScreenshot('${screenshot.id}')">
                <div class="screenshot-info">
                    <span class="screenshot-title">📸 Screenshot</span>
                    <span class="screenshot-timestamp">${formatTimestamp(screenshot.timestamp)}</span>
                </div>
                <div class="screenshot-controls">
                    <button onclick="event.stopPropagation(); copyScreenshotPath('${screenshot.id}')" class="screenshot-btn" title="Copy file path">📋 Copy Path</button>
                    <button onclick="event.stopPropagation(); downloadScreenshot('${screenshot.id}')" class="screenshot-btn">💾 Save</button>
                    <button onclick="event.stopPropagation(); deleteScreenshot('${screenshot.id}')" class="screenshot-btn">🗑️ Delete</button>
                    <span class="screenshot-toggle">▼</span>
                </div>
            </div>
            <div class="screenshot-content" id="screenshot-content-${screenshot.id}">
                <div class="screenshot-path">
                    <small><strong>Path:</strong> <code onclick="copyToClipboard('${screenshot.relativePath}')" title="Click to copy">${pathDisplay}</code></small>
                </div>
                <img src="${screenshot.dataUrl}" alt="Screenshot ${formatTimestamp(screenshot.timestamp)}" />
            </div>
        `;

        return item;
    }

    // Toggle screenshot collapse/expand
    window.toggleScreenshot = function(id) {
        const content = document.getElementById(`screenshot-content-${id}`);
        const toggle = content.parentElement.querySelector('.screenshot-toggle');

        if (content.style.display === 'none') {
            content.style.display = 'block';
            toggle.textContent = '▼';
        } else {
            content.style.display = 'none';
            toggle.textContent = '▶';
        }
    };

    // Copy screenshot path to clipboard
    window.copyScreenshotPath = function(id) {
        const screenshot = screenshots.find(s => s.id === id);
        if (screenshot && screenshot.relativePath !== 'memory-only') {
            copyToClipboard(screenshot.relativePath);
        } else {
            copyToClipboard('Screenshot stored in memory only - no file path available');
        }
    };

    // Copy text to clipboard
    window.copyToClipboard = function(text) {
        if (navigator.clipboard) {
            navigator.clipboard.writeText(text).then(() => {
                console.log('📋 Copied to clipboard:', text);
                // Show temporary feedback
                const toast = document.createElement('div');
                toast.textContent = 'Copied to clipboard!';
                toast.style.cssText = `
                    position: fixed;
                    top: 20px;
                    right: 20px;
                    background: #28a745;
                    color: white;
                    padding: 10px 15px;
                    border-radius: 5px;
                    z-index: 10000;
                    font-size: 14px;
                `;
                document.body.appendChild(toast);
                setTimeout(() => document.body.removeChild(toast), 2000);
            }).catch(err => {
                console.error('Failed to copy:', err);
            });
        } else {
            // Fallback for older browsers
            const textArea = document.createElement('textarea');
            textArea.value = text;
            document.body.appendChild(textArea);
            textArea.select();
            document.execCommand('copy');
            document.body.removeChild(textArea);
            console.log('📋 Copied to clipboard (fallback):', text);
        }
    };

    // Download screenshot
    window.downloadScreenshot = function(id) {
        const screenshot = screenshots.find(s => s.id === id);
        if (screenshot) {
            const link = document.createElement('a');
            link.href = screenshot.dataUrl;
            link.download = `screenshot-${formatTimestamp(screenshot.timestamp).replace(/[^\w-]/g, '_')}.png`;
            link.click();
        }
    };

    // Delete screenshot (including file)
    window.deleteScreenshot = async function(id) {
        const index = screenshots.findIndex(s => s.id === id);
        if (index !== -1) {
            const screenshot = screenshots[index];

            try {
                // Delete file if it exists
                if (screenshot.filepath !== 'memory-only' && window.electronAPI && window.electronAPI.deleteFile) {
                    await window.electronAPI.deleteFile(screenshot.filepath);
                    console.log(`🗑️ Deleted file: ${screenshot.filepath}`);
                }
            } catch (error) {
                console.warn('⚠️ Failed to delete screenshot file:', error);
            }

            // Remove from memory
            screenshots.splice(index, 1);
            renderScreenshots();
            console.log('✅ Screenshot removed from collection');
        }
    };

    // Render all screenshots
    function renderScreenshots() {
        if (screenshots.length === 0) {
            screenshotsContainer.innerHTML = `
                <div class="no-screenshots">
                    <span class="no-screenshots-icon">📷</span>
                    <p>No screenshots captured yet</p>
                    <small>Click "Take Screenshot" to capture and store images</small>
                </div>
            `;
        } else {
            screenshotsContainer.innerHTML = '';
            screenshots.forEach(screenshot => {
                screenshotsContainer.appendChild(createScreenshotItem(screenshot));
            });
        }
        updateScreenshotCount();
    }

    // Generate session ID for tmp directory in chrome-container root
    const sessionId = Date.now().toString();
    const tmpDir = `../tmp/session-${sessionId}`;

    // Capture screenshot using proper screen capture
    async function captureScreenshot() {
        try {
            let dataUrl = null;

            // Method 1: Try Electron's nativeImage screen capture
            if (window.electronAPI && window.electronAPI.captureScreen) {
                console.log('🖼️ Using Electron screen capture API');
                dataUrl = await window.electronAPI.captureScreen();
            }
            // Method 2: Try getDisplayMedia (requires user permission)
            else if (navigator.mediaDevices && navigator.mediaDevices.getDisplayMedia) {
                console.log('🖼️ Using getDisplayMedia API');
                const stream = await navigator.mediaDevices.getDisplayMedia({ 
                    video: { 
                        mediaSource: 'screen',
                        width: { ideal: 1920 },
                        height: { ideal: 1080 }
                    } 
                });

                const video = document.createElement('video');
                video.srcObject = stream;
                video.play();

                // Wait for video to load
                await new Promise((resolve) => {
                    video.addEventListener('loadedmetadata', resolve, { once: true });
                });

                // Create canvas and capture frame
                const canvas = document.createElement('canvas');
                const ctx = canvas.getContext('2d');
                canvas.width = video.videoWidth || 1920;
                canvas.height = video.videoHeight || 1080;
                ctx.drawImage(video, 0, 0);

                dataUrl = canvas.toDataURL('image/png', 1.0);

                // Stop the stream
                stream.getTracks().forEach(track => track.stop());
            }
            // Method 3: Try html2canvas if available
            else if (window.html2canvas) {
                console.log('🖼️ Using html2canvas');
                const canvas = await html2canvas(document.body, {
                    height: window.innerHeight,
                    width: window.innerWidth,
                    scrollX: 0,
                    scrollY: 0
                });
                dataUrl = canvas.toDataURL('image/png', 1.0);
            }
            // Method 4: Canvas screenshot of current viewport
            else {
                console.log('🖼️ Using canvas viewport capture');
                const canvas = document.createElement('canvas');
                const ctx = canvas.getContext('2d');
                canvas.width = window.innerWidth;
                canvas.height = window.innerHeight;

                // Capture DOM content to canvas (basic method)
                ctx.fillStyle = getComputedStyle(document.body).backgroundColor || '#ffffff';
                ctx.fillRect(0, 0, canvas.width, canvas.height);

                // Add text overlay showing what was captured
                ctx.fillStyle = '#333333';
                ctx.font = '16px Arial';
                ctx.textAlign = 'center';
                ctx.fillText('Viewport Screenshot', canvas.width / 2, 50);
                ctx.fillText(`${canvas.width}x${canvas.height}`, canvas.width / 2, 80);
                ctx.fillText(formatTimestamp(Date.now()), canvas.width / 2, 110);

                dataUrl = canvas.toDataURL('image/png', 1.0);
            }

            if (dataUrl && dataUrl !== 'data:,') {
                await saveScreenshotFile(dataUrl);
            } else {
                throw new Error('Failed to capture screenshot data');
            }

        } catch (error) {
            console.error('Screenshot capture failed:', error);

            // Create error image
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');
            canvas.width = 800;
            canvas.height = 400;
            ctx.fillStyle = '#f8f9fa';
            ctx.fillRect(0, 0, canvas.width, canvas.height);
            ctx.strokeStyle = '#dee2e6';
            ctx.strokeRect(0, 0, canvas.width, canvas.height);

            ctx.fillStyle = '#dc3545';
            ctx.font = 'bold 24px Arial';
            ctx.textAlign = 'center';
            ctx.fillText('Screenshot Capture Failed', canvas.width / 2, canvas.height / 2 - 40);

            ctx.fillStyle = '#6c757d';
            ctx.font = '16px Arial';
            ctx.fillText(`Error: ${error.message}`, canvas.width / 2, canvas.height / 2);
            ctx.fillText('Try using browser screen share permission', canvas.width / 2, canvas.height / 2 + 30);
            ctx.fillText(formatTimestamp(Date.now()), canvas.width / 2, canvas.height / 2 + 60);

            const errorDataUrl = canvas.toDataURL('image/png', 1.0);
            await saveScreenshotFile(errorDataUrl);
        }
    }

    // Save screenshot as file and add to collection
    async function saveScreenshotFile(dataUrl) {
        const timestamp = Date.now();
        const filename = `screenshot-${timestamp}.png`;
        const filepath = `${tmpDir}/${filename}`;

        try {
            console.log('🔍 Checking electronAPI availability:', {
                electronAPI: !!window.electronAPI,
                ensureDir: !!(window.electronAPI && window.electronAPI.ensureDir),
                saveFile: !!(window.electronAPI && window.electronAPI.saveFile)
            });

            let fileSaved = false;

            // Create tmp directory if it doesn't exist
            if (window.electronAPI && window.electronAPI.ensureDir) {
                const dirResult = await window.electronAPI.ensureDir(tmpDir);
                console.log('📁 Directory creation result:', dirResult);
            }

            // Convert dataUrl to buffer and save file
            const base64Data = dataUrl.replace(/^data:image\/png;base64,/, '');

            if (window.electronAPI && window.electronAPI.saveFile) {
                const saveResult = await window.electronAPI.saveFile(filepath, base64Data, 'base64');
                console.log('💾 File save result:', saveResult);
                if (saveResult && saveResult.success) {
                    fileSaved = true;
                    console.log(`✅ Screenshot saved to: ${filepath}`);
                } else {
                    console.warn('⚠️ File save failed:', saveResult);
                }
            } else {
                console.warn('⚠️ File system API not available, storing in memory only');
            }

            const screenshot = {
                id: timestamp.toString(),
                timestamp: timestamp,
                filename: filename,
                filepath: fileSaved ? filepath : 'memory-only',
                dataUrl: dataUrl, // Keep for display
                relativePath: fileSaved ? `chrome-container-app/${filepath}` : 'memory-only'
            };

            screenshots.unshift(screenshot); // Add to beginning
            renderScreenshots();

            console.log('✅ Screenshot captured and added to collection');

        } catch (error) {
            console.error('❌ Failed to save screenshot file:', error);

            // Fallback: store in memory only
            const screenshot = {
                id: timestamp.toString(),
                timestamp: timestamp,
                filename: filename,
                filepath: 'memory-only',
                dataUrl: dataUrl,
                relativePath: 'memory-only'
            };

            screenshots.unshift(screenshot);
            renderScreenshots();
        }
    }

    // Event listeners
    if (takeScreenshotBtn) {
        takeScreenshotBtn.addEventListener('click', async () => {
            takeScreenshotBtn.disabled = true;
            takeScreenshotBtn.textContent = '📸 Capturing...';

            try {
                await captureScreenshot();
            } catch (error) {
                console.error('Screenshot button error:', error);
            } finally {
                takeScreenshotBtn.disabled = false;
                takeScreenshotBtn.textContent = '📸 Take Screenshot';
            }
        });
    }

    if (clearScreenshotsBtn) {
        clearScreenshotsBtn.addEventListener('click', async () => {
            if (screenshots.length > 0 && confirm(`Delete all ${screenshots.length} screenshots and remove session folder?`)) {
                try {
                    // Delete individual files first
                    for (const screenshot of screenshots) {
                        if (screenshot.filepath !== 'memory-only' && window.electronAPI && window.electronAPI.deleteFile) {
                            try {
                                await window.electronAPI.deleteFile(screenshot.filepath);
                            } catch (err) {
                                console.warn(`Failed to delete ${screenshot.filepath}:`, err);
                            }
                        }
                    }

                    // Delete the entire session directory
                    if (window.electronAPI && window.electronAPI.deleteDir) {
                        try {
                            await window.electronAPI.deleteDir(tmpDir);
                            console.log(`🗑️ Deleted session directory: ${tmpDir}`);
                        } catch (err) {
                            console.warn(`Failed to delete directory ${tmpDir}:`, err);
                        }
                    }

                } catch (error) {
                    console.error('Error during cleanup:', error);
                }

                screenshots = [];
                renderScreenshots();
                console.log('✅ All screenshots cleared and session directory removed');
            }
        });
    }

    // Initial render
    renderScreenshots();

    console.log('✅ Screenshot feature initialized');
}

// --- 1. GET LIBRARIES AND UI ELEMENTS ---
// Wait for DOM and electronAPI to be available
document.addEventListener('DOMContentLoaded', () => {
    console.log('🔍 Checking window.electronAPI availability...');
    console.log('window.electronAPI:', window.electronAPI);
    console.log('Available window properties:', Object.keys(window));

    // Declare terminal variables at higher scope for tab switching access
    let term = null;
    let fitAddon = null;

    if (!window.electronAPI) {
        console.error('❌ window.electronAPI is not available');
        console.error('This means the preload script failed to load or contextBridge failed');
        alert('FATAL ERROR: Preload script failed to load.\n\nCheck browser console for details.');
        return;
    }

    console.log('✅ window.electronAPI is available with keys:', Object.keys(window.electronAPI));

    // Test the bridge connection
    if (window.electronAPI.testConnection) {
        console.log('🔗 Bridge test:', window.electronAPI.testConnection());
    }

    // Check if Terminal libraries are available (from global scope or electronAPI)
    // Note: CDN version has FitAddon.FitAddon constructor
    const terminalLibrariesAvailable = (window.Terminal && (window.FitAddon || window.FitAddon?.FitAddon)) || 
                                      (window.electronAPI?.Terminal && window.electronAPI?.FitAddon);
    if (!terminalLibrariesAvailable) {
        console.log('⚠️ Terminal libraries not available - checking for globals...');
        console.log('window.Terminal:', typeof window.Terminal);
        console.log('window.FitAddon:', typeof window.FitAddon);
        console.log('window.FitAddon?.FitAddon:', typeof window.FitAddon?.FitAddon);
    }

    // Get all element references from the HTML
    const initializeBtn = document.getElementById('initialize-chrome');
    const restartBtn = document.getElementById('restart-chrome');
    const reconnectDevToolsBtn = document.getElementById('reconnect-devtools');
    const canvasBtn = document.getElementById('navigate-canvas');
    const chromePlaceholder = document.getElementById('chrome-placeholder');
    const chromeContainer = document.querySelector('.chrome-container');
    let chromeViewport = document.getElementById('chrome-viewport'); // Use 'let' to allow reassignment
    const chromeStatusIndicator = document.getElementById('chrome-status');
    const cdpStatus = document.getElementById('cdp-connection');
    const debugPortDisplay = document.getElementById('debug-port');
    const memoryDisplay = document.getElementById('memory-usage');
    const importBtn = document.getElementById('import-md');
    const guidanceContent = document.getElementById('guidance-content');
    const tabButtons = document.querySelectorAll('.tab-btn');
    const tabContents = document.querySelectorAll('.tab-content');
    const terminalContainer = document.getElementById('terminal');

    // Message elements
    const userMessageInput = document.getElementById('user-message');
    const sendMessageBtn = document.getElementById('send-message');
    const queueMessageBtn = document.getElementById('queue-message');
    const urgentMessageBtn = document.getElementById('urgent-message');
    const queuedMessagesContainer = document.getElementById('queued-messages');

    // Error log elements
    const errorLog = document.getElementById('error-log');
    const clearErrorsBtn = document.getElementById('clear-errors');
    const exportErrorsBtn = document.getElementById('export-errors');
    const totalErrorsDisplay = document.getElementById('total-errors');
    const chromeErrorsDisplay = document.getElementById('chrome-errors');
    const appErrorsDisplay = document.getElementById('app-errors');

    // Session management elements
    const clearSessionsBtn = document.getElementById('clear-sessions');
    const refreshStatusBtn = document.getElementById('refresh-status');
    const exportLogsBtn = document.getElementById('export-logs');

    // Performance metrics elements
    const cpuUsageDisplay = document.getElementById('cpu-usage');
    const liveConnectionsDisplay = document.getElementById('live-connections');
    const chromeTabsDisplay = document.getElementById('chrome-tabs');

    // AI tracking elements
    const aiStatusIcon = document.getElementById('ai-status-icon');
    const aiStatusText = document.getElementById('ai-status-text');
    const aiLastAction = document.getElementById('ai-last-action');

    // --- 2. THE CENTRAL UI UPDATE FUNCTION ---
    function updateUI(status) {
        if (!status) return;
        console.log("🔄 Updating UI with live metrics:", status);

        // --- Update Live Performance Metrics ---
        if (status.cpu !== undefined) {
            if (cpuUsageDisplay) cpuUsageDisplay.textContent = `${status.cpu}%`;
            if (document.getElementById('cpu-bar')) {
                const cpuBar = document.getElementById('cpu-bar');
                cpuBar.style.width = `${status.cpu}%`;
                cpuBar.style.transition = 'width 0.5s ease';
                // Color code based on CPU usage
                if (status.cpu > 80) cpuBar.style.backgroundColor = '#e74c3c';
                else if (status.cpu > 50) cpuBar.style.backgroundColor = '#f39c12';
                else cpuBar.style.backgroundColor = '#27ae60';
            }
        }

        if (status.memory !== undefined) {
            if (memoryDisplay) memoryDisplay.textContent = `${status.memory} MB`;
            if (document.getElementById('memory-bar')) {
                const memoryBar = document.getElementById('memory-bar');
                // Animate the bar, assuming a max of 1024MB for visualization
                const memPercentage = Math.min(100, (status.memory / 1024) * 100);
                memoryBar.style.width = `${memPercentage}%`;
                memoryBar.style.transition = 'width 0.5s ease';
                // Color code based on memory usage
                if (memPercentage > 80) memoryBar.style.backgroundColor = '#e74c3c';
                else if (memPercentage > 50) memoryBar.style.backgroundColor = '#f39c12';
                else memoryBar.style.backgroundColor = '#3498db';
            }
        }

        if (status.chromeTabs !== undefined) {
            if (chromeTabsDisplay) chromeTabsDisplay.textContent = status.chromeTabs;
        }

        if (status.connections !== undefined) {
            if (liveConnectionsDisplay) liveConnectionsDisplay.textContent = status.connections;
        }

        // --- Update Chrome Status (existing logic) ---
        if (status.running) {
            // Chrome is running - SHOW the viewport, HIDE the placeholder
            chromePlaceholder.classList.add('hidden');
            chromeViewport.classList.remove('hidden');

            // Point the webview to the running Chrome instance's DevTools frontend
            // First get the available tabs, then use the DevTools frontend URL
            setupChromeViewport(status.port);

            // Update button states
            initializeBtn.disabled = true;
            restartBtn.disabled = false;
            canvasBtn.disabled = false;

            // Update status text
            chromeStatusIndicator.innerHTML = `
                <span class="status-icon" style="color: #27ae60;">🟢</span>
                <span class="status-text">Chrome: Running (PID: ${status.pid})</span>`;

            cdpStatus.className = 'connection-status connected';
            cdpStatus.textContent = `Port ${status.port}`;
            debugPortDisplay.textContent = status.port;

        } else {
            // Chrome is NOT running - HIDE the viewport, SHOW the placeholder
            chromePlaceholder.classList.remove('hidden');
            chromeViewport.classList.add('hidden');
            // Only clear the source if it's not already blank to prevent repeated errors
            if (chromeViewport.src !== 'about:blank' && chromeViewport.src !== '') {
                chromeViewport.src = 'about:blank'; // Clear the source only once
            }

            // Update button states
            initializeBtn.disabled = false;
            restartBtn.disabled = true;
            canvasBtn.disabled = true;

            // Update status text
            chromeStatusIndicator.innerHTML = `
                <span class="status-icon" style="color: #e74c3c;">🔴</span>
                <span class="status-text">${status.error ? 'Error' : 'Not Running'}</span>`;

            cdpStatus.className = 'connection-status disconnected';
            cdpStatus.textContent = 'Disconnected';
            debugPortDisplay.textContent = '--';
        }

        if (memoryDisplay && status.memory) {
            memoryDisplay.textContent = `${status.memory} MB`;
        }
    }

    // --- 3. HELPER FUNCTIONS ---
    function displayMessage(message) {
        if (!queuedMessagesContainer) return;
        const messageEl = document.createElement('div');
        messageEl.className = 'message-item';
        messageEl.innerHTML = `
