     1‚Üíconst { app, BrowserWindow, ipcMain, dialog } = require('electron');
     2‚Üíconst path = require('path');
     3‚Üíconst os = require('os');
     4‚Üíconst pty = require('node-pty');
     5‚Üíconst CDP = require('chrome-remote-interface');
     6‚Üíconst NativeChromeManager = require('./chrome-manager/native-chrome-launcher');
     7‚Üíconst ConnectionPool = require('./chrome-manager/connection-pool');
     8‚Üí
     9‚Üí// Enforce single instance of the application
    10‚Üíconst gotTheLock = app.requestSingleInstanceLock();
    11‚Üí
    12‚Üíif (!gotTheLock) {
    13‚Üí  app.quit();
    14‚Üí} else {
    15‚Üí  app.on('second-instance', (event, commandLine, workingDirectory) => {
    16‚Üí    // Someone tried to run a second instance, we should focus our window.
    17‚Üí    if (containerApp.mainWindow) {
    18‚Üí      if (containerApp.mainWindow.isMinimized()) containerApp.mainWindow.restore();
    19‚Üí      containerApp.mainWindow.focus();
    20‚Üí    }
    21‚Üí  });
    22‚Üí}
    23‚Üí
    24‚Üí// Global error handlers to prevent crashes from EPIPE and other stream errors
    25‚Üíprocess.on('uncaughtException', (error) => {
    26‚Üí    if (error.code === 'EPIPE' || error.errno === 'EPIPE') {
    27‚Üí        // Ignore EPIPE errors (broken pipe to console/stdout)
    28‚Üí        console.error('‚ö†Ô∏è EPIPE error ignored:', error.message);
    29‚Üí        return;
    30‚Üí    }
    31‚Üí    // Log other uncaught exceptions but don't crash
    32‚Üí    console.error('‚ùå Uncaught Exception:', error);
    33‚Üí});
    34‚Üí
    35‚Üíprocess.on('unhandledRejection', (reason, promise) => {
    36‚Üí    console.error('‚ùå Unhandled Rejection at:', promise, 'reason:', reason);
    37‚Üí});
    38‚Üí
    39‚Üí// Safe logging utility to prevent EPIPE errors
    40‚Üífunction safeLog(...args) {
    41‚Üí    try {
    42‚Üí        console.log(...args);
    43‚Üí    } catch (error) {
    44‚Üí        // Ignore EPIPE and other console write errors
    45‚Üí    }
    46‚Üí}
    47‚Üí
    48‚Üífunction safeError(...args) {
    49‚Üí    try {
    50‚Üí        console.error(...args);
    51‚Üí    } catch (error) {
    52‚Üí        // Ignore EPIPE and other console write errors
    53‚Üí    }
    54‚Üí}
    55‚Üí
    56‚Üíclass SlitherChromeContainer {
    57‚Üí    constructor() {
    58‚Üí        this.mainWindow = null;
    59‚Üí        this.chromeLauncher = new NativeChromeManager();
    60‚Üí        this.connectionPool = new ConnectionPool();
    61‚Üí        this.chromeInstance = null;
    62‚Üí        this.messageQueue = [];
    63‚Üí        this.ptyProcess = null; // Real terminal process
    64‚Üí        this.metricsInterval = null; // Live metrics timer
    65‚Üí        this.cdpClient = null; // CDP connection client
    66‚Üí    }
    67‚Üí
    68‚Üí    async createMainWindow() {
    69‚Üí        this.mainWindow = new BrowserWindow({
    70‚Üí            width: 1400,
    71‚Üí            height: 900,
    72‚Üí            webPreferences: {
    73‚Üí                preload: path.join(__dirname, 'preload.js'),
    74‚Üí                contextIsolation: true,  // Secure by default
    75‚Üí                nodeIntegration: false,  // Secure by default
    76‚Üí                webviewTag: true         // Enable <webview> for Chrome DevTools
    77‚Üí            },
    78‚Üí            icon: path.join(__dirname, 'assets', 'icon.png'),
    79‚Üí            title: 'Slither Chrome Container v1.0'
    80‚Üí        });
    81‚Üí
    82‚Üí        await this.mainWindow.loadFile('renderer/index.html');
    83‚Üí        
    84‚Üí        // Force hard refresh to load new terminal interface - clear ALL cache
    85‚Üí        this.mainWindow.webContents.session.clearCache();
    86‚Üí        this.mainWindow.webContents.reloadIgnoringCache();
    87‚Üí
    88‚Üí        // Enable dev tools to debug preload issues
    89‚Üí        this.mainWindow.webContents.openDevTools();
    90‚Üí
    91‚Üí        // Handle window closed - but don't cleanup, container should stay running
    92‚Üí        this.mainWindow.on('closed', () => {
    93‚Üí            console.log('üîÑ Main window closed, but container continues running...');
    94‚Üí            this.mainWindow = null;
    95‚Üí        });
    96‚Üí    }
    97‚Üí
    98‚Üí    // Wait for Chrome to be ready before accepting connections
    99‚Üí    async waitForChromeReady(port, timeout = 10000) {
   100‚Üí        const startTime = Date.now();
   101‚Üí        while (Date.now() - startTime < timeout) {
   102‚Üí            try {
   103‚Üí                const response = await fetch(`http://localhost:${port}/json/version`);
   104‚Üí                if (response.ok) {
   105‚Üí                    console.log(`‚úÖ Chrome on port ${port} is ready.`);
   106‚Üí                    return true;
   107‚Üí                }
   108‚Üí            } catch (error) {
   109‚Üí                // Ignore connection errors and retry
   110‚Üí            }
   111‚Üí            // Wait 200ms before trying again
   112‚Üí            await new Promise(resolve => setTimeout(resolve, 200));
   113‚Üí        }
   114‚Üí        throw new Error(`Chrome on port ${port} did not become ready in time.`);
   115‚Üí    }
   116‚Üí
   117‚Üí    async connectToChrome(port) {
   118‚Üí        if (this.cdpClient) {
   119‚Üí            try {
   120‚Üí                await this.cdpClient.close();
   121‚Üí            } catch (e) {
   122‚Üí                // Ignore errors closing old client
   123‚Üí            }
   124‚Üí            this.cdpClient = null;
   125‚Üí        }
   126‚Üí
   127‚Üí        try {
   128‚Üí            console.log(`üîó Connecting to Chrome on port ${port} using CDP...`);
   129‚Üí            // Connect to the first available target (usually the primary page)
   130‚Üí            this.cdpClient = await CDP({ port });
   131‚Üí
   132‚Üí            // Enable Page and Runtime domains for commands
   133‚Üí            await Promise.all([
   134‚Üí                this.cdpClient.Page.enable(),
   135‚Üí                this.cdpClient.Runtime.enable(),
   136‚Üí                this.cdpClient.Target.setDiscoverTargets({ discover: true })
   137‚Üí            ]);
   138‚Üí
   139‚Üí            // Listen for disconnection events
   140‚Üí            this.cdpClient.on('disconnect', () => {
   141‚Üí                console.log('üî¥ CDP client disconnected.');
   142‚Üí                this.cdpClient = null;
   143‚Üí                this.updateRendererStatus();
   144‚Üí            });
   145‚Üí
   146‚Üí            // **ADD THIS BLOCK TO LISTEN FOR BACKEND ERRORS**
   147‚Üí            this.cdpClient.on('error', (err) => {
   148‚Üí                console.error('üî¥ CDP Client Error:', err);
   149‚Üí                const errorObject = {
   150‚Üí                    source: 'CDP Backend',
   151‚Üí                    message: err.message,
   152‚Üí                    timestamp: new Date().toISOString(),
   153‚Üí                    type: 'error'
   154‚Üí                };
   155‚Üí                if (this.mainWindow && !this.mainWindow.isDestroyed()) {
   156‚Üí                    this.mainWindow.webContents.send('new-error-log', errorObject);
   157‚Üí                }
   158‚Üí            });
   159‚Üí
   160‚Üí            console.log('‚úÖ CDP connection successful.');
   161‚Üí            return true;
   162‚Üí        } catch (error) {
   163‚Üí            console.error('‚ùå CDP connection failed:', error);
   164‚Üí            this.cdpClient = null;
   165‚Üí            return false;
   166‚Üí        }
   167‚Üí    }
   168‚Üí
   169‚Üí    async initializeChrome() {
   170‚Üí        // **ANTI-GHOSTING FIX**: Always kill any lingering instances before starting.
   171‚Üí        console.log('üßπ Checking for and killing any lingering Chrome instances...');
   172‚Üí        if (this.chromeInstances && this.chromeInstances.length > 0) {
   173‚Üí            const instancesToKill = [...this.chromeInstances];
   174‚Üí            for (const instance of instancesToKill) {
   175‚Üí                await this.chromeLauncher.killChromeInstance(instance);
   176‚Üí            }
   177‚Üí            await new Promise(resolve => setTimeout(resolve, 500)); // Give processes time to die
   178‚Üí            this.chromeInstances = [];
   179‚Üí            this.chromeInstance = null;
   180‚Üí            console.log('‚úÖ Lingering instances cleaned up.');
   181‚Üí        }
   182‚Üí
   183‚Üí        try {
   184‚Üí            console.log('üöÄ Initializing a new Chrome instance...');
   185‚Üí            if (!this.chromeInstances) this.chromeInstances = [];
   186‚Üí
   187‚Üí            const chromeResult = await this.chromeLauncher.launchChrome();
   188‚Üí            if (!chromeResult.success) throw new Error(chromeResult.error || 'Failed to launch Chrome');
   189‚Üí
   190‚Üí            const newInstance = chromeResult.chromeInstance;
   191‚Üí            await this.waitForChromeReady(newInstance.port);
   192‚Üí
   193‚Üí            this.chromeInstances.push(newInstance);
   194‚Üí            if (!this.chromeInstance) this.chromeInstance = newInstance;
   195‚Üí            if (!this.connectionPool.isInitialized()) await this.connectionPool.initialize(this.chromeInstance.port);
   196‚Üí            
   197‚Üí            // Establish CDP connection for stable WebSocket communication
   198‚Üí            await this.connectToChrome(newInstance.port);
   199‚Üí
   200‚Üí            console.log(`‚úÖ Chrome instance #${this.chromeInstances.length} launched on port: ${newInstance.port} (PID: ${newInstance.pid})`);
   201‚Üí            
   202‚Üí            newInstance.process.on('exit', (code) => {
   203‚Üí                console.log(`üî¥ Chrome instance with PID ${newInstance.pid} exited with code: ${code}.`);
   204‚Üí                this.chromeInstances = this.chromeInstances.filter(inst => inst.pid !== newInstance.pid);
   205‚Üí                if (this.chromeInstance && this.chromeInstance.pid === newInstance.pid) {
   206‚Üí                    this.chromeInstance = this.chromeInstances[0] || null;
   207‚Üí                }
   208‚Üí                this.updateRendererStatus();
   209‚Üí            });
   210‚Üí
   211‚Üí            this.updateRendererStatus();
   212‚Üí            return { success: true };
   213‚Üí        } catch (error) {
   214‚Üí            console.error('‚ùå Failed to initialize Chrome:', error);
   215‚Üí            this.updateRendererStatus({ error: error.message });
   216‚Üí            return { success: false, error: error.message };
   217‚Üí        }
   218‚Üí    }
   219‚Üí
   220‚Üí    setupIPC() {
   221‚Üí        console.log('üîå Setting up IPC handlers...');
   222‚Üí        
   223‚Üí        // Handle get chrome status request (register this first)
   224‚Üí        console.log('üìù Registering handler: get-chrome-status');
   225‚Üí        ipcMain.handle('get-chrome-status', async (event) => {
   226‚Üí            try {
   227‚Üí                const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
   228‚Üí                const status = {
   229‚Üí                    running: isRunning,
   230‚Üí                    pid: this.chromeInstance?.pid || null,
   231‚Üí                    port: this.chromeInstance?.port || null,
   232‚Üí                    instanceCount: this.chromeInstances?.length || 0,
   233‚Üí                    memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024)
   234‚Üí                };
   235‚Üí                
   236‚Üí                safeLog('üì° Sending chrome status:', status);
   237‚Üí                return status;
   238‚Üí            } catch (error) {
   239‚Üí                console.error('‚ùå Error getting chrome status:', error);
   240‚Üí                return {
   241‚Üí                    running: false,
   242‚Üí                    error: error.message,
   243‚Üí                    instanceCount: 0
   244‚Üí                };
   245‚Üí            }
   246‚Üí        });
   247‚Üí
   248‚Üí        // Handle DevTools reconnection (full implementation)
   249‚Üí        console.log('üìù Registering handler: reconnect-devtools');
   250‚Üí        ipcMain.handle('reconnect-devtools', async (event) => {
   251‚Üí            try {
   252‚Üí                console.log('üîó Reconnecting to Chrome DevTools...');
   253‚Üí                
   254‚Üí                if (!this.chromeInstance || !this.chromeInstance.port) {
   255‚Üí                    throw new Error('No Chrome instance available to reconnect to');
   256‚Üí                }
   257‚Üí
   258‚Üí                // Close existing CDP connection if it exists
   259‚Üí                if (this.cdpClient) {
   260‚Üí                    try {
   261‚Üí                        await this.cdpClient.close();
   262‚Üí                        console.log('üî¥ Closed existing CDP connection');
   263‚Üí                    } catch (error) {
   264‚Üí                        console.log('‚ö†Ô∏è Error closing old CDP connection (ignored):', error.message);
   265‚Üí                    }
   266‚Üí                    this.cdpClient = null;
   267‚Üí                }
   268‚Üí
   269‚Üí                // Wait for Chrome to be ready
   270‚Üí                await this.waitForChromeReady(this.chromeInstance.port, 5000);
   271‚Üí                
   272‚Üí                // Re-establish CDP connection
   273‚Üí                const connected = await this.connectToChrome(this.chromeInstance.port);
   274‚Üí                
   275‚Üí                if (connected) {
   276‚Üí                    console.log('‚úÖ DevTools reconnection successful');
   277‚Üí                    this.updateRendererStatus();
   278‚Üí                    return { success: true, message: 'DevTools reconnected successfully' };
   279‚Üí                } else {
   280‚Üí                    throw new Error('Failed to establish CDP connection');
   281‚Üí                }
   282‚Üí                
   283‚Üí            } catch (error) {
   284‚Üí                console.error('‚ùå DevTools reconnection failed:', error);
   285‚Üí                this.updateRendererStatus();
   286‚Üí                return { success: false, error: error.message };
   287‚Üí            }
   288‚Üí        });
   289‚Üí        console.log('‚úÖ reconnect-devtools handler registered successfully');
   290‚Üí
   291‚Üí        // Handle MD file import
   292‚Üí        ipcMain.handle('import-md-file', async () => {
   293‚Üí            try {
   294‚Üí                const result = await dialog.showOpenDialog(this.mainWindow, {
   295‚Üí                    properties: ['openFile', 'multiSelections'],
   296‚Üí                    filters: [
   297‚Üí                        { name: 'Markdown Files', extensions: ['md'] },
   298‚Üí                        { name: 'Text Files', extensions: ['txt'] },
   299‚Üí                        { name: 'All Files', extensions: ['*'] }
   300‚Üí                    ]
   301‚Üí                });
   302‚Üí
   303‚Üí                if (!result.canceled && result.filePaths.length > 0) {
   304‚Üí                    const fs = require('fs');
   305‚Üí                    const path = require('path');
   306‚Üí                    
   307‚Üí                    const files = [];
   308‚Üí                    
   309‚Üí                    for (const filePath of result.filePaths) {
   310‚Üí                        try {
   311‚Üí                            const content = fs.readFileSync(filePath, 'utf8');
   312‚Üí                            const fileName = path.basename(filePath);
   313‚Üí                            
   314‚Üí                            files.push({
   315‚Üí                                name: fileName,
   316‚Üí                                path: filePath,
   317‚Üí                                content: content,
   318‚Üí                                size: content.length
   319‚Üí                            });
   320‚Üí                            
   321‚Üí                            console.log(`‚úÖ Loaded file: ${fileName} (${content.length} chars)`);
   322‚Üí                        } catch (error) {
   323‚Üí                            console.error(`‚ùå Error reading file ${filePath}:`, error);
   324‚Üí                        }
   325‚Üí                    }
   326‚Üí                    
   327‚Üí                    return {
   328‚Üí                        success: true,
   329‚Üí                        files: files,
   330‚Üí                        count: files.length
   331‚Üí                    };
   332‚Üí                } else {
   333‚Üí                    return {
   334‚Üí                        success: false,
   335‚Üí                        error: 'No files selected',
   336‚Üí                        files: []
   337‚Üí                    };
   338‚Üí                }
   339‚Üí            } catch (error) {
   340‚Üí                console.error('‚ùå MD import error:', error);
   341‚Üí                return {
   342‚Üí                    success: false,
   343‚Üí                    error: error.message,
   344‚Üí                    files: []
   345‚Üí                };
   346‚Üí            }
   347‚Üí        });
   348‚Üí
   349‚Üí        // Handle connection status requests
   350‚Üí        ipcMain.handle('get-connection-status', async () => {
   351‚Üí            try {
   352‚Üí                // Get real Chrome status
   353‚Üí                const chromeRunning = this.chromeInstance !== null;
   354‚Üí                const chromePid = this.chromeInstance?.pid || null;
   355‚Üí                const debugPort = this.chromeInstance?.port || null;
   356‚Üí                
   357‚Üí                // Get real connection data
   358‚Üí                const activeConnections = this.connectionPool.getActiveConnectionCount();
   359‚Üí                const sessions = this.connectionPool.getActiveSessions();
   360‚Üí                
   361‚Üí                // Check if Chrome is actually responsive
   362‚Üí                let chromeResponsive = false;
   363‚Üí                if (chromeRunning && debugPort) {
   364‚Üí                    try {
   365‚Üí                        const response = await fetch(`http://localhost:${debugPort}/json/version`, {
   366‚Üí                            signal: AbortSignal.timeout(2000)
   367‚Üí                        });
   368‚Üí                        chromeResponsive = response.ok;
   369‚Üí                    } catch (error) {
   370‚Üí                        chromeResponsive = false;
   371‚Üí                    }
   372‚Üí                }
   373‚Üí                
   374‚Üí                // Check WebSocket server status
   375‚Üí                let websocketRunning = false;
   376‚Üí                try {
   377‚Üí                    const wsResponse = await fetch('http://localhost:9223/health', {
   378‚Üí                        signal: AbortSignal.timeout(2000)
   379‚Üí                    });
   380‚Üí                    websocketRunning = wsResponse.ok;
   381‚Üí                } catch (error) {
   382‚Üí                    websocketRunning = false;
   383‚Üí                }
   384‚Üí                
   385‚Üí                // Check Slither connection
   386‚Üí                let slitherConnected = false;
   387‚Üí                try {
   388‚Üí                    const slitherResponse = await fetch('http://localhost:9223/status', {
   389‚Üí                        signal: AbortSignal.timeout(2000)
   390‚Üí                    });
   391‚Üí                    if (slitherResponse.ok) {
   392‚Üí                        const data = await slitherResponse.json();
   393‚Üí                        slitherConnected = data.connected || false;
   394‚Üí                    }
   395‚Üí                } catch (error) {
   396‚Üí                    slitherConnected = false;
   397‚Üí                }
   398‚Üí                
   399‚Üí                // Check if Chrome needs installation (only if not running)
   400‚Üí                let needsInstallation = false;
