   744‚Üí    setupTerminal() {
   745‚Üí        try {
   746‚Üí            const shell = os.platform() === 'win32' ? 'powershell.exe' : 'bash';
   747‚Üí            this.ptyProcess = pty.spawn(shell, [], {
   748‚Üí                name: 'xterm-color',
   749‚Üí                cols: 80,
   750‚Üí                rows: 30,
   751‚Üí                cwd: app.getPath('home'),
   752‚Üí                env: process.env
   753‚Üí            });
   754‚Üí
   755‚Üí            this.ptyProcess.onData(data => {
   756‚Üí                // **RACE CONDITION FIX**: Check if the window still exists before sending.
   757‚Üí                if (this.mainWindow && !this.mainWindow.isDestroyed()) {
   758‚Üí                    this.mainWindow.webContents.send('terminal-data', data);
   759‚Üí                }
   760‚Üí            });
   761‚Üí
   762‚Üí            // **FIXED RESPAWN LOGIC**: Only respawn on an actual error (non-zero exit code).
   763‚Üí            this.ptyProcess.onExit(({ exitCode }) => {
   764‚Üí                console.log(`üî¥ Terminal process exited with code ${exitCode}`);
   765‚Üí                if (exitCode !== 0) {
   766‚Üí                    console.log('Respawning terminal due to error...');
   767‚Üí                    this.setupTerminal();
   768‚Üí                }
   769‚Üí            });
   770‚Üí            
   771‚Üí            this.ptyProcess.write('\r'); // Kickstart the terminal
   772‚Üí        } catch (error) {
   773‚Üí            console.error('‚ùå PTY process failed to spawn:', error);
   774‚Üí        }
   775‚Üí    }
