   777‚Üí    // Live metrics gathering function
   778‚Üí    async gatherMetrics() {
   779‚Üí        let cpuUsage = 0;
   780‚Üí        try {
   781‚Üí            // app.getCPUUsage() may not be available in all Electron versions
   782‚Üí            if (typeof app.getCPUUsage === 'function') {
   783‚Üí                cpuUsage = app.getCPUUsage().percentCPUUsage;
   784‚Üí            } else {
   785‚Üí                // Fallback: use process CPU usage estimation
   786‚Üí                cpuUsage = process.cpuUsage().user / 1000000; // Convert to percentage approximation
   787‚Üí            }
   788‚Üí        } catch (error) {
   789‚Üí            cpuUsage = 0; // Fallback to 0 if unable to get CPU usage
   790‚Üí        }
   791‚Üí
   792‚Üí        const metrics = {
   793‚Üí            cpu: cpuUsage.toFixed(1),
   794‚Üí            memory: (process.memoryUsage().heapUsed / 1024 / 1024).toFixed(1),
   795‚Üí            chromeTabs: 0,
   796‚Üí            connections: this.connectionPool.getActiveConnectionCount()
   797‚Üí        };
   798‚Üí
   799‚Üí        if (this.cdpClient) {
   800‚Üí            try {
   801‚Üí                // Use CDP to get tab information instead of fetch
   802‚Üí                const targets = await this.cdpClient.Target.getTargets();
   803‚Üí                // Count page targets (not service workers, etc.)
   804‚Üí                metrics.chromeTabs = targets.targetInfos.filter(target => 
   805‚Üí                    target.type === 'page' && !target.url.startsWith('devtools://')
   806‚Üí                ).length;
   807‚Üí            } catch (error) {
   808‚Üí                // Ignore errors if Chrome is closing or not responsive
   809‚Üí                metrics.chromeTabs = 0;
   810‚Üí            }
   811‚Üí        }
   812‚Üí        return metrics;
   813‚Üí    }
   814‚Üí
   815‚Üí    // Centralized status updater - single source of truth for UI updates
   816‚Üí    async updateRendererStatus() {
   817‚Üí        if (this.mainWindow && !this.mainWindow.isDestroyed()) {
   818‚Üí            const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
   819‚Üí            const metrics = await this.gatherMetrics(); // Gather fresh metrics
   820‚Üí
   821‚Üí            const status = {
   822‚Üí                running: isRunning,
   823‚Üí                pid: this.chromeInstance?.pid || null,
   824‚Üí                port: this.chromeInstance?.port || null,
   825‚Üí                webview: true,
   826‚Üí                instanceCount: this.chromeInstances?.length || 0,
   827‚Üí                error: null,
   828‚Üí                ...metrics // Add all the live metrics to the status object
   829‚Üí            };
   830‚Üí            
   831‚Üí            safeLog('üì° Sending chrome-status with live metrics:', status);
   832‚Üí            this.mainWindow.webContents.send('chrome-status', status);
   833‚Üí        }
   834‚Üí    }
