                    fs.rmSync(fullPath, { recursive: true, force: true });
                    console.log(`üóëÔ∏è Directory deleted: ${fullPath}`);
                }
                return { success: true };
            } catch (error) {
                console.error('‚ùå Directory delete failed:', error);
                return { success: false, error: error.message };
            }
        });

