        // Debug: Test if handlers are accessible
        try {
            const reconnectResult = ipcMain._invokeHandlers?.get('reconnect-devtools');
            console.log('üìã reconnect-devtools handler:', reconnectResult ? 'FOUND' : 'NOT FOUND');

            const forceKillResult = ipcMain._invokeHandlers?.get('force-kill-websockets');
            console.log('üìã force-kill-websockets handler:', forceKillResult ? 'FOUND' : 'NOT FOUND');

            const restartWSResult = ipcMain._invokeHandlers?.get('restart-websocket-server');
            console.log('üìã restart-websocket-server handler:', restartWSResult ? 'FOUND' : 'NOT FOUND');
        } catch (error) {
            console.log('üìã Handler test error:', error.message);
        }

        // **TEST ERROR LOGGING PIPELINE**
        setTimeout(() => {
            console.log('üß™ Testing error logging pipeline...');
