    // --- 4. CHROME VIEWPORT HELPER FUNCTIONS ---
    async function setupChromeViewport(port) {
        try {
            console.log(`üåê Setting up Chrome viewport for port ${port}`);

            // Get list of available tabs from Chrome DevTools
            const response = await fetch(`http://localhost:${port}/json`);
            if (!response.ok) {
                throw new Error(`Failed to fetch tabs: ${response.status}`);
            }

            const tabs = await response.json();
            console.log(`üìã Found ${tabs.length} Chrome tabs:`, tabs);

            // Find the first tab that's not a DevTools page
            const targetTab = tabs.find(tab => 
                !tab.url.startsWith('devtools://') && 
                !tab.url.startsWith('chrome-extension://') &&
                tab.webSocketDebuggerUrl
            );

            if (targetTab) {
                // Wait a moment to ensure CDP domains are fully initialized
                setTimeout(() => {
                    // Use the external DevTools frontend URL provided by Chrome
                    const devtoolsUrl = targetTab.devtoolsFrontendUrl;
                    console.log(`üîó Loading Chrome-provided DevTools URL: ${devtoolsUrl}`);

                    if (chromeViewport.src !== devtoolsUrl) {
                        chromeViewport.src = devtoolsUrl;
                    }
                }, 2000); // Wait 2 seconds for CDP domains to initialize
            } else {
                // Fallback: load a simple status page
                const fallbackUrl = `data:text/html,<h2>Chrome DevTools</h2><p>Waiting for Chrome to be ready...</p><script>setTimeout(() => location.reload(), 3000);</script>`;
                console.log(`üìã Loading fallback status page`);
                chromeViewport.src = fallbackUrl;
            }

        } catch (error) {
            console.error('‚ùå Failed to setup Chrome viewport:', error);
            // Fallback to simple port URL
            const fallbackUrl = `http://localhost:${port}`;
            console.log(`üîÑ Falling back to: ${fallbackUrl}`);
            chromeViewport.src = fallbackUrl;
        }
    }

    // --- 5. DRAG AND DROP HELPER FUNCTIONS ---
    function setupTerminalDragAndDrop(container, terminal) {
        console.log('üéØ Setting up drag-and-drop for terminal...');

        // Visual feedback styles
        const dropZoneClass = 'terminal-drop-zone';
        const style = document.createElement('style');
        style.textContent = `
            .${dropZoneClass} {
                position: relative;
            }
            .${dropZoneClass}::after {
                content: "üìÅ Drop files here to insert paths";
                position: absolute;
                top: 0;
                left: 0;
                right: 0;
                bottom: 0;
                background: rgba(52, 152, 219, 0.9);
                color: white;
                display: flex;
                align-items: center;
                justify-content: center;
                font-family: monospace;
                font-size: 16px;
                font-weight: bold;
                z-index: 1000;
                pointer-events: none;
                border: 3px dashed #fff;
                border-radius: 4px;
            }
        `;
        document.head.appendChild(style);

        // Prevent default drag behaviors on the container
        ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
            container.addEventListener(eventName, preventDefaults, false);
        });

        function preventDefaults(e) {
            e.preventDefault();
            e.stopPropagation();
        }

        // Highlight drop zone when item is dragged over it
        ['dragenter', 'dragover'].forEach(eventName => {
            container.addEventListener(eventName, highlight, false);
        });

        ['dragleave', 'drop'].forEach(eventName => {
            container.addEventListener(eventName, unhighlight, false);
        });

        function highlight() {
            container.classList.add(dropZoneClass);
        }

        function unhighlight() {
            container.classList.remove(dropZoneClass);
        }

        // Handle dropped files
        container.addEventListener('drop', handleDrop, false);

        function handleDrop(e) {
            const dt = e.dataTransfer;
            const files = dt.files;

            handleFiles(files, terminal);
        }

        function handleFiles(files, terminal) {
            if (!files.length) return;

            console.log(`üìÅ Processing ${files.length} dropped file(s)`);

            // Process each file
            Array.from(files).forEach((file, index) => {
                const filePath = file.path || file.name;
                console.log(`üìÇ File ${index + 1}: ${filePath}`);

                // Insert the file path into the terminal with proper escaping
                const escapedPath = escapeShellPath(filePath);

                // Add a space before if not at the start of line, and space after
                const pathToInsert = ` "${escapedPath}" `;

                // Write to terminal (this will appear at cursor position)
                terminal.write(pathToInsert);

                // Also send to backend PTY so it's part of the actual command
                window.electronAPI.writeToTerminal(pathToInsert);
            });

            // Show success feedback
            showDropFeedback(files.length);
        }

        function escapeShellPath(path) {
            // Escape special characters for shell safety
            return path.replace(/(["\s'$`\\])/g, '\\$1');
        }

        function showDropFeedback(fileCount) {
            // Create temporary feedback message
            const feedback = document.createElement('div');
            feedback.style.cssText = `
                position: fixed;
                top: 20px;
                right: 20px;
                background: #27ae60;
                color: white;
                padding: 10px 20px;
                border-radius: 4px;
                font-family: monospace;
                font-size: 14px;
                z-index: 10000;
                box-shadow: 0 2px 8px rgba(0,0,0,0.3);
                transition: opacity 0.3s ease;
            `;
            feedback.textContent = `‚úÖ Inserted ${fileCount} file path${fileCount > 1 ? 's' : ''}`;

            document.body.appendChild(feedback);

            // Fade out and remove after 3 seconds
            setTimeout(() => {
                feedback.style.opacity = '0';
                setTimeout(() => {
                    if (feedback.parentNode) {
                        feedback.parentNode.removeChild(feedback);
                    }
                }, 300);
            }, 3000);
        }

        console.log('‚úÖ Terminal drag-and-drop setup complete');
    }

