    // **STABLE ERROR MONITORING SYSTEM**
    console.log('üîß Setting up stable error monitoring system...');

    let errorCount = { total: 0, chrome: 0, app: 0 };
    let errorBuffer = []; // Buffer to prevent spam
    let lastErrorTime = 0;
    const ERROR_THROTTLE_MS = 1000; // Max 1 error per second per type
    const MAX_ERRORS_PER_MINUTE = 10; // Prevent spam
    let errorTimestamps = [];

    // Throttled error logging to prevent infinite loops and spam
    function throttledAddError(error) {
        const now = Date.now();

        // Remove old timestamps (older than 1 minute)
        errorTimestamps = errorTimestamps.filter(t => now - t < 60000);

        // Check rate limit
        if (errorTimestamps.length >= MAX_ERRORS_PER_MINUTE) {
            return; // Drop error to prevent spam
        }

        // Check for duplicate recent errors
        const recentSimilar = errorBuffer.find(e => 
            e.message === error.message && 
            e.source === error.source && 
            (now - e.timestamp) < ERROR_THROTTLE_MS
        );

        if (recentSimilar) {
            return; // Drop duplicate
        }

        // Add to buffer and clean old entries
        errorBuffer.push(error);
        errorBuffer = errorBuffer.filter(e => now - e.timestamp < 5000); // Keep 5 seconds
        errorTimestamps.push(now);

        // Safe error logging
        try {
            addErrorLogEntry(error);

            if (error.source.includes('Chrome') || error.source.includes('WebSocket') || error.source.includes('Network')) {
                errorCount.chrome++;
            } else {
                errorCount.app++;
            }
            errorCount.total++;
            updateErrorCounts(errorCount.total, errorCount.chrome, errorCount.app);
        } catch (e) {
            // Prevent infinite loops if error logging itself fails
            console.warn('Error logging failed:', e.message);
        }
    }

    // Safe console error capture (prevents infinite loops)
    function captureConsoleErrors() {
        const originalConsoleError = console.error;
        const originalConsoleWarn = console.warn;

        let isLoggingError = false; // Prevent recursion

        console.error = function(...args) {
            if (!isLoggingError) {
                isLoggingError = true;
                try {
                    const errorMessage = args.join(' ');
                    // Skip our own error logging messages to prevent loops
                    if (!errorMessage.includes('addErrorLogEntry') && !errorMessage.includes('Error logging failed')) {
                        throttledAddError({
                            timestamp: Date.now(),
                            message: errorMessage.substring(0, 200), // Limit message length
                            source: 'App Errors',
                            type: 'error'
                        });
                    }
                } finally {
                    isLoggingError = false;
                }
            }
            originalConsoleError.apply(console, args);
        };

        console.warn = function(...args) {
            if (!isLoggingError) {
                isLoggingError = true;
                try {
                    const warningMessage = args.join(' ');
                    if (!warningMessage.includes('addErrorLogEntry') && !warningMessage.includes('Error logging failed')) {
                        throttledAddError({
                            timestamp: Date.now(),
                            message: warningMessage.substring(0, 200),
                            source: 'Warnings',
                            type: 'warning'
                        });
                    }
                } finally {
                    isLoggingError = false;
                }
            }
            originalConsoleWarn.apply(console, args);
        };
    }

    // Efficient WebSocket error monitoring
    function setupWebSocketErrorMonitoring() {
        if (!window.WebSocket) return; // Skip if WebSocket not available

        const originalWebSocket = window.WebSocket;
        window.WebSocket = function(url, protocols) {
            const ws = new originalWebSocket(url, protocols);

            // Only log significant WebSocket errors, not every connection attempt
            ws.addEventListener('error', (event) => {
                throttledAddError({
                    timestamp: Date.now(),
                    message: `WebSocket error: ${url.substring(0, 50)}...`,
                    source: 'WebSocket',
                    type: 'error'
                });
            });

            ws.addEventListener('close', (event) => {
                // Only log abnormal closures
                if (event.code !== 1000 && event.code !== 1001) {
                    throttledAddError({
                        timestamp: Date.now(),
                        message: `WebSocket closed abnormally: Code ${event.code}`,
                        source: 'WebSocket',
                        type: 'warning'
                    });
                }
            });

            return ws;
        };
    }

    // Chrome webview error monitoring
    function setupWebviewErrorMonitoring() {
        const chromeViewport = document.getElementById('chrome-viewport');
        if (chromeViewport) {
            chromeViewport.addEventListener('consolemessage', (event) => {
                if (event.level >= 2) { // Error level
                    const error = {
                        timestamp: Date.now(),
                        message: event.message,
                        source: 'Chrome Errors',
                        type: event.level === 2 ? 'warning' : 'error'
                    };
                    addErrorLogEntry(error);
                    errorCount.chrome++;
                    errorCount.total++;
                    updateErrorCounts(errorCount.total, errorCount.chrome, errorCount.app);
                }
            });
        }
    }

    // Efficient global error handlers
    window.addEventListener('error', (event) => {
        // Skip script loading errors and resource errors that are not critical
        if (event.filename && event.filename.includes('chrome-extension://')) return;

        throttledAddError({
            timestamp: Date.now(),
            message: `${event.error?.message || event.message} at ${event.filename}:${event.lineno}`.substring(0, 150),
            source: 'JavaScript Error',
            type: 'error'
        });
    });

    // Efficient promise rejection handler
    window.addEventListener('unhandledrejection', (event) => {
        throttledAddError({
            timestamp: Date.now(),
            message: `Promise Rejection: ${event.reason}`.substring(0, 150),
            source: 'Promise Rejection',
            type: 'error'
        });
    });

    // Lightweight network error monitoring (disabled by default to prevent overhead)
    function setupNetworkErrorMonitoring() {
        // Only monitor critical network failures, not every request
        const criticalFetch = ['localhost', '127.0.0.1', 'chrome-container'];
        const originalFetch = window.fetch;

        window.fetch = function(...args) {
            const url = args[0];
            const isCritical = criticalFetch.some(domain => url.toString().includes(domain));

            if (isCritical) {
                return originalFetch.apply(this, args).catch(error => {
                    throttledAddError({
                        timestamp: Date.now(),
                        message: `Critical network error: ${error.message}`,
                        source: 'Network Error',
                        type: 'error'
                    });
                    throw error;
                });
            }
            return originalFetch.apply(this, args);
        };
    }

    // Minimal media error monitoring (only for critical failures)
    function setupMediaErrorMonitoring() {
        // Using passive listeners to reduce overhead
        document.addEventListener('error', (event) => {
            if (event.target && event.target.tagName === 'VIDEO') { // Only videos, not images
                throttledAddError({
                    timestamp: Date.now(),
                    message: `Video loading failed: ${event.target.src.substring(0, 50)}...`,
                    source: 'Media Error',
                    type: 'warning'
                });
            }
        }, { passive: true, capture: true });
    }

    // Disabled extension monitoring (too resource intensive)
    function setupExtensionErrorMonitoring() {
        // Disabled to prevent CPU overhead
        return;
    }

    // Minimal performance monitoring (reduced frequency)
    function setupPerformanceMonitoring() {
        // Only monitor critical performance issues
        if (window.performance && window.performance.memory) {
            // Check memory only every 2 minutes to reduce overhead
            setInterval(() => {
                try {
                    const memory = window.performance.memory;
                    const usedMemory = memory.usedJSHeapSize / memory.jsHeapSizeLimit;
                    if (usedMemory > 0.9) { // Only warn at 90% usage
                        throttledAddError({
                            timestamp: Date.now(),
                            message: `Critical memory usage: ${Math.round(usedMemory * 100)}%`,
                            source: 'Memory Critical',
                            type: 'error'
                        });
                    }
                } catch (e) {
                    // Ignore performance monitoring errors
                }
            }, 120000); // Every 2 minutes
        }
    }

    // Lightweight Electron error monitoring
    function setupElectronErrorMonitoring() {
        // Only monitor critical IPC failures, not all communications
        if (window.electronAPI && window.electronAPI.onError) {
            try {
                window.electronAPI.onError((error) => {
                    throttledAddError({
                        timestamp: Date.now(),
                        message: `IPC Error: ${error.message}`.substring(0, 100),
                        source: 'IPC Error',
                        type: 'error'
                    });
                });
            } catch (e) {
                // Silently ignore if onError not available
            }
        }
    }

    // Initialize all monitoring systems
    captureConsoleErrors();
    setupWebSocketErrorMonitoring();
    setupWebviewErrorMonitoring();
    setupNetworkErrorMonitoring();
    setupMediaErrorMonitoring();
    setupExtensionErrorMonitoring();
    setupPerformanceMonitoring();
    setupElectronErrorMonitoring();

    // Cleanup mechanism to prevent memory leaks
    function cleanupErrorSystem() {
        // Clean old error timestamps every 5 minutes
        setInterval(() => {
            const now = Date.now();
            errorTimestamps = errorTimestamps.filter(t => now - t < 300000); // Keep 5 minutes
            errorBuffer = errorBuffer.filter(e => now - e.timestamp < 10000); // Keep 10 seconds

            // Limit error log DOM elements to prevent memory buildup
            if (errorLog && errorLog.children.length > 50) {
                // Remove oldest entries beyond 50
                while (errorLog.children.length > 50) {
                    errorLog.removeChild(errorLog.lastChild);
                }
            }
        }, 300000); // Every 5 minutes
    }

