      'overallScore',
      'dependencyScore',
      'connectivityScore',
      'complexityScore',
      'uploadStatus',
      'uploadedFileCount',
      'confidenceBadge',
      'directoryTree',
      'pathSuggestions',
      'suggestionsList',
      'diagramType',
      'generateDiagram',
      'diagramContainer',
      // Central Viewport
      'left-resizer',
      'initialize-chrome',
      'restart-chrome',
      'navigate-canvas',
      'refresh-webview',
      'chrome-viewport',
      // Right Toolbar Elements
      'rightToolbar',
      'rightToolbarToggle',
      'right-resizer',
      // Planning Tab
      'planning-tab',
      'import-md',
      'take-screenshot',
      'imported-files',
      'guidance-content',
      'clear-screenshots',
      'screenshot-count',
      'screenshots-container',
      'screenshots-list',
