// Terminal Tab Manager Class for managing multiple terminal instances
class TerminalTabManager {
  constructor() {
    console.log('üîç DOM TRACE: TerminalTabManager initialized');
    this.terminals = new Map(); // terminalId -> {terminal, tab, element, pty}
    this.activeTerminalId = null;
    this.nextId = 1;
  }

  createTerminal(options = {}) {
    console.log('üîç DOM TRACE: Creating terminal with options:', options);
    const id = `terminal-${this.nextId++}`;
    const tabContainer = document.getElementById('terminal-tabs');
    const contentContainer = document.getElementById('terminal-content');

    if (!tabContainer || !contentContainer) {
      console.error('üö® DOM TRACE ERROR: Terminal containers not found');
      return null;
    }

    // Create tab element
    const tab = document.createElement('div');
    tab.className = 'terminal-tab';
    tab.dataset.terminalId = id;
    tab.innerHTML = `
      <span class="tab-title">${options.name || `Terminal ${this.nextId - 1}`}</span>
      <button class="tab-close" data-terminal-id="${id}">√ó</button>
    `;

    // Add tab event listeners
    tab.addEventListener('click', e => {
      if (!e.target.classList.contains('tab-close')) {
        console.log('üîç DOM TRACE: Tab clicked:', id);
        this.switchToTerminal(id);
        // Enhanced focus for better input handling
        setTimeout(() => {
          const terminal = this.terminals.get(id)?.terminal;
          if (terminal) {
            terminal.focus();
            console.log('üîç DOM TRACE: Terminal focused for input:', id);
          }
        }, 50);
      }
    });

    tab.querySelector('.tab-close').addEventListener('click', e => {
      e.stopPropagation();
      console.log('üîç DOM TRACE: Close tab clicked:', id);
      this.killTerminal(id);
    });

    // Create terminal container
    const terminalDiv = document.createElement('div');
    terminalDiv.id = id;
    terminalDiv.className = 'terminal-instance';
    // REMOVED: terminalDiv.style.display = 'block'; - Let CSS .active class control visibility
    console.log('üîç TERMINAL DEBUG: terminalDiv created, visibility controlled by .active class');

    // Initialize xterm.js terminal
    const terminal = new window.Terminal({
      cursorBlink: true,
      fontSize: 13,
      fontFamily: 'SF Mono, Monaco, Menlo, monospace',
      theme: {
        background: '#1e1e1e',
        foreground: '#d4d4d4',
        cursor: '#f0f0f0',
        selection: 'rgba(255, 255, 255, 0.3)',
      },
      scrollback: 5000,
    });

    terminal.open(terminalDiv);

    // Setup terminal input handling
    terminal.onData(data => {
      console.log('üîç DOM TRACE: Terminal input:', id, 'data length:', data.length);
      // Send to backend PTY
      window.electronAPI?.writeToTerminal?.(data, id);
    });

    // Store terminal info
    console.log('üîç DOM TRACE: Terminal working directory set:', options.cwd || 'default');
    this.terminals.set(id, {
      terminal,
      tab,
      element: terminalDiv,
      pty: null,
      workingDirectory: options.cwd || '',
      fitAddon: null, // Store fitAddon to prevent recreation
    });

    // Add to DOM
    tabContainer.appendChild(tab);
    contentContainer.appendChild(terminalDiv);

    // Show tabs container if this is the first terminal
    if (this.terminals.size === 1) {
      tabContainer.style.display = 'flex';
      console.log('üîç DOM TRACE: Terminal tabs container shown (first terminal)');
    }

    // Set as active
    this.switchToTerminal(id);

    // Request PTY from main process
    console.log('üîç TERMINAL CREATE: Sending to backend:', { id, cwd: options.cwd });
    window.electronAPI?.createTerminal?.({ id, cwd: options.cwd });

    // EMERGENCY: Terminal data handler setup moved to global scope to prevent duplicates

    console.log('üîç DOM TRACE: Terminal created:', id);
    return id;
  }

  switchToTerminal(id) {
    console.log('üîç DOM TRACE: Switching to terminal:', id, 'from:', this.activeTerminalId);

    // Hide all terminals and deactivate tabs
    this.terminals.forEach((term, termId) => {
      term.element.classList.remove('active');
      term.tab.classList.remove('active');
    });

    // Show selected terminal
    const selected = this.terminals.get(id);
    if (selected) {
      selected.element.classList.add('active');
      selected.tab.classList.add('active');
      this.activeTerminalId = id;
      selected.terminal.focus();
      console.log('üîç DOM TRACE: Terminal activated with active class:', id, selected.element.classList.contains('active'));

      // Fit terminal to container (only if not already fitted)
      if (window.FitAddon?.FitAddon && !selected.fitAddon) {
        const fitAddon = new window.FitAddon.FitAddon();
        selected.terminal.loadAddon(fitAddon);
        selected.fitAddon = fitAddon;
        fitAddon.fit();
        console.log('üîç DOM TRACE: FitAddon applied to terminal:', id);
      } else if (selected.fitAddon) {
        // Just refit existing addon
        selected.fitAddon.fit();
      }
    }
  }

  killTerminal(id) {
    console.log('üîç DOM TRACE: Killing terminal:', id);
    const term = this.terminals.get(id);

    if (term) {
      // Dispose terminal
      term.terminal.dispose();

      // Remove from DOM
      term.tab.remove();
      term.element.remove();

      // Remove from map
      this.terminals.delete(id);

      console.log('üîç DOM TRACE: Terminal killed:', id, 'remaining:', this.terminals.size);

      // Switch to another terminal if this was active
      if (this.activeTerminalId === id) {
        const remaining = Array.from(this.terminals.keys());
        if (remaining.length > 0) {
          this.switchToTerminal(remaining[0]);
        } else {
          this.activeTerminalId = null;
          // Show empty state
          const contentContainer = document.getElementById('terminal-content');
          if (contentContainer) {
            contentContainer.innerHTML =
              '<div class="no-terminals">No active terminals. Click "New Terminal" to start.</div>';
          }
        }
      }

      // Notify main process
      window.electronAPI?.killTerminal?.({ id });
    }
  }

  killAllTerminals() {
    console.log('üîç DOM TRACE: Killing all terminals, count:', this.terminals.size);
    const ids = Array.from(this.terminals.keys());
    ids.forEach(id => this.killTerminal(id));
    console.log('üîç DOM TRACE: All terminals killed');
  }

  getActiveTerminal() {
    return this.terminals.get(this.activeTerminalId);
  }
}

