async function getContextTitle() {
  try {
    const recentCommand = await window.electronAPI?.getLastCommand();
    if (recentCommand) {
      return `Analyzing: ${recentCommand.command || 'command'}`;
    }

    const activeFiles = document.querySelectorAll('.file-context-item').length;
    if (activeFiles > 0) {
      return `Context: ${activeFiles} files loaded`;
    }

    return 'Terminal context ready';
  } catch (error) {
    return 'Context loading...';
  }
}

async function getContextDescription() {
  try {
    const errorCount = document.querySelectorAll('.error-item').length;
    const planningFiles = document.querySelectorAll('[id*="plan"]').length;

    if (errorCount > 0) {
      return `${errorCount} errors detected - trace analysis active`;
    }

    if (planningFiles > 0) {
      return `${planningFiles} planning files available for context`;
    }

    const lastActivity = await window.electronAPI?.getLastActivity();
    if (lastActivity) {
      return `Last activity: ${lastActivity.type} - ${new Date(lastActivity.timestamp).toLocaleTimeString()}`;
    }

    return 'Ready for terminal operations';
  } catch (error) {
    return 'Context analysis in progress...';
  }
}

async function getContextFiles() {
  try {
    const files = [];

    // Get uploaded files
    const uploadedFiles = await window.electronAPI?.getUploadedFiles();
    if (uploadedFiles && uploadedFiles.length > 0) {
      files.push(
        ...uploadedFiles.slice(0, 3).map(file => ({
          icon: getFileIcon(file.type || file.name),
          name: file.name,
          path: truncatePath(file.path || file.name),
          type: 'uploaded',
        }))
      );
    }

    // Get recent files from system
    const recentFiles = await window.electronAPI?.getRecentFiles();
    if (recentFiles && recentFiles.length > 0) {
      files.push(
        ...recentFiles.slice(0, 2).map(file => ({
          icon: getFileIcon(file.name),
          name: file.name,
          path: truncatePath(file.path),
          type: 'recent',
        }))
      );
    }

    // Get error log files if any
    const errorLogFiles = document.querySelectorAll('.error-item');
    if (errorLogFiles.length > 0 && files.length < 3) {
      files.push({
        icon: 'err',
        name: 'error.log',
        path: `${errorLogFiles.length} active errors`,
        type: 'system',
      });
    }

    return files.length > 0
      ? files
      : [
          {
            icon: 'sys',
            name: 'system.log',
            path: 'chrome-container-app/renderer',
            type: 'fallback',
          },
        ];
  } catch (error) {
    return [
      {
        icon: 'sys',
        name: 'loading...',
        path: 'context loading',
        type: 'loading',
      },
    ];
  }
}

async function getContextUsage() {
  try {
    const totalFiles = document.querySelectorAll('.file-context-item, .context-file-item').length;
    const maxFiles = 10; // Could be configurable
    const usage = Math.min((totalFiles / maxFiles) * 100, 100);

