function getFileIcon(filename) {
  const ext = filename.split('.').pop()?.toLowerCase();
  const iconMap = {
    js: 'js',
    json: 'js',
    html: 'ht',
    css: 'cs',
    md: 'md',
    txt: 'tx',
    log: 'lg',
    err: 'er',
    py: 'py',
    asar: 'ar',
  };
  return iconMap[ext] || 'fl';
}

function truncatePath(path, maxLength = 30) {
  if (!path || path.length <= maxLength) return path;
  return '...' + path.slice(-(maxLength - 3));
}

