function exportLogs() {
  try {
    const errorContainer = document.getElementById('error-log');
    if (!errorContainer) {
      console.warn('Error log container not found');
      return;
    }

    const errorItems = errorContainer.querySelectorAll('.error-item');
    const logs = [];

    errorItems.forEach(item => {
      const timestamp = item.querySelector('.error-timestamp')?.textContent || '';
      const source = item.querySelector('.error-source')?.textContent || '';
      const message = item.querySelector('.error-message')?.textContent || '';
      const level = item.classList.contains('warning')
        ? 'warning'
        : item.classList.contains('info')
          ? 'info'
          : 'error';

      logs.push({
        timestamp: timestamp.replace(/[\[\]]/g, ''),
        source: source.replace(':', ''),
        level,
        message,
      });
    });

    // Create export data
    const exportData = {
      exportTime: new Date().toISOString(),
      totalErrors: logs.length,
      errors: logs,
    };

    // Create and trigger download
    const dataStr = JSON.stringify(exportData, null, 2);
    const blob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(blob);

    const a = document.createElement('a');
    a.href = url;
    a.download = `error-logs-${new Date().toISOString().slice(0, 19).replace(/:/g, '-')}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    console.log(`üìã Exported ${logs.length} error log entries`);
  } catch (error) {
    console.error('Failed to export logs:', error);
  }
}

