function updateContextIndicator() {
  console.log('üîç DOM TRACE: updateContextIndicator called');
  console.log('üîç DOM TRACE: Selected context items:', selectedContextItems);
  console.log(
    'üîç DOM TRACE: Selected items count:',
    selectedContextItems ? selectedContextItems.length : 'undefined'
  );

  const contextBar = document.querySelector('.terminal-input-context');
  console.log('üîç DOM TRACE: Context bar element:', contextBar);

  if (!contextBar) {
    console.error('üö® DOM TRACE ERROR: Context bar (.terminal-input-context) not found!');
    // Try alternative selectors
    const alternatives = ['.input-container', '.terminal-input-container', '.context-controls'];
    for (const selector of alternatives) {
      const alt = document.querySelector(selector);
      if (alt) {
        console.log(`üîç DOM TRACE: Found alternative container: ${selector}`, alt);
        break;
      }
    }
    return;
  }

  let indicator = contextBar.querySelector('.context-indicator');
  console.log('üîç DOM TRACE: Existing context indicator:', indicator);

  if (!indicator && selectedContextItems && selectedContextItems.length > 0) {
    console.log('üîç DOM TRACE: Creating new context indicator...');
    indicator = document.createElement('div');
    indicator.className = 'context-indicator';
    contextBar.appendChild(indicator);
    console.log('üîç DOM TRACE: Context indicator created and added to DOM');
  }

  if (!selectedContextItems || selectedContextItems.length === 0) {
    console.log('üîç DOM TRACE: No context items, removing indicator if exists');
    if (indicator) {
      indicator.remove();
      console.log('üîç DOM TRACE: Context indicator removed');
    }
    return;
  }

  const count = selectedContextItems.length;
  console.log(`üîç DOM TRACE: Updating indicator with ${count} items`);

  // Log items being displayed
  const displayItems = selectedContextItems.slice(-3);
  console.log('üîç DOM TRACE: Items for chips display:', displayItems);

  const indicatorHTML = `
    <span class="context-count">${count} context${count !== 1 ? 's' : ''}</span>
    <div class="context-chips">
      ${displayItems
        .map((item, index) => {
          const icon = getContextIcon(item.type);
          const chipIndex = selectedContextItems.length - 3 + index;
          console.log(
            `üîç DOM TRACE: Creating chip ${index} for item:`,
            item.type,
            'at index',
            chipIndex
          );
          return `
          <span class="context-chip" data-type="${item.type}">
            ${icon}
            <span class="chip-close" data-index="${chipIndex}">√ó</span>
          </span>
        `;
        })
        .join('')}
    </div>
  `;

  console.log('üîç DOM TRACE: Setting indicator innerHTML...');
  indicator.innerHTML = indicatorHTML;
  console.log('üîç DOM TRACE: Indicator HTML updated');

  // Setup chip close handlers with debugging
  const closeButtons = indicator.querySelectorAll('.chip-close');
  console.log('üîç DOM TRACE: Found', closeButtons.length, 'close buttons for chips');

  closeButtons.forEach((closeBtn, btnIndex) => {
    console.log(`üîç DOM TRACE: Setting up close handler for button ${btnIndex}`);
    closeBtn.addEventListener('click', e => {
      console.log('üîç DOM TRACE: Chip close button clicked:', closeBtn);
      console.log('üîç DOM TRACE: Close button index:', closeBtn.dataset.index);
      e.stopPropagation();
      const index = parseInt(closeBtn.dataset.index);
      console.log('üîç DOM TRACE: Removing item at index:', index);
      console.log('üîç DOM TRACE: Items before removal:', selectedContextItems.length);
      selectedContextItems.splice(index, 1);
      console.log('üîç DOM TRACE: Items after removal:', selectedContextItems.length);
      updateContextIndicator();
    });
  });

  console.log('üîç DOM TRACE: Context indicator update complete');
}

