 */
function connectPhaseButton() {
  console.log('üîå Connecting phase button...');

  try {
    // Verify PhaseButton class is loaded
    if (typeof window.PhaseButton === 'undefined') {
      console.error('PhaseButton class not found. Component script may not have loaded.');
      return;
    }

    // Initialize phase button component
    window.phaseButtonInstance = new PhaseButton('phase-btn');

    console.log('‚úÖ Phase button connected successfully');

  } catch (error) {
    console.error('‚ùå Error connecting phase button:', error);
  }
}

/**
 * Stream data to planning tab
 * Integrates with existing tab switching system
 */
function streamToPlanningTab(data) {
  try {
    // Switch to planning tab if not active
    const planningTab = document.querySelector('[data-tab="planning"]');
    if (planningTab && !planningTab.classList.contains('active')) {
      planningTab.click();
    }

    // Update guidance content
    const guidanceContent = document.getElementById('guidance-content');
    if (guidanceContent) {
      const formattedData = typeof data === 'string' ? data : JSON.stringify(data, null, 2);
      const timestamp = new Date().toLocaleString();

      const content = `Phase Data Stream - ${timestamp}\n\n${formattedData}\n\nGenerated: ${timestamp} CST`;
      guidanceContent.value = content;
    }

    console.log('‚úÖ Data streamed to planning tab');

  } catch (error) {
    console.error('‚ùå Error streaming to planning tab:', error);
  }
}

/**
 * Add screenshot to planning tab with gallery functionality
 * Chicago Time: 2025-08-13 15:56:00 CST
 */
function addScreenshotToPlanning(screenshotData, timestamp) {
  try {
    const screenshotsList = document.getElementById('screenshots-list');
    if (!screenshotsList) {
      console.error('Screenshots list not found');
      return;
    }

    const noScreenshots = screenshotsList.querySelector('.no-screenshots');

    // Remove "no screenshots" message if present
    if (noScreenshots) {
      noScreenshots.style.display = 'none';
    }

    // Create screenshot element
    const screenshotElement = document.createElement('div');
    screenshotElement.className = 'screenshot-item phase-screenshot';
    screenshotElement.innerHTML = `
      <div class="screenshot-header">
        <span class="screenshot-title">Phase Result - ${new Date(timestamp).toLocaleTimeString()}</span>
        <span class="screenshot-type">Phase Execution</span>
      </div>
      <div class="screenshot-image-container">
        <img src="${screenshotData}" alt="Phase execution screenshot" class="screenshot-image"/>
      </div>
      <div class="screenshot-metadata">
        <span class="timestamp">${new Date(timestamp).toLocaleString()}</span>
        <button class="btn-41 xs screenshot-action" data-action="view-full">
          <i class="codicon codicon-zoom-in"></i>
        </button>
      </div>
    `;

    // Add to screenshots list
    screenshotsList.appendChild(screenshotElement);

    // Update screenshot count
    const countElement = document.getElementById('screenshot-count');
    if (countElement) {
      const count = screenshotsList.querySelectorAll('.screenshot-item').length;
      countElement.textContent = `${count} images`;
    }

    // Setup view full button
    const viewButton = screenshotElement.querySelector('[data-action="view-full"]');
    if (viewButton) {
      viewButton.addEventListener('click', () => {
        const img = screenshotElement.querySelector('.screenshot-image');
        if (img) {
          showFullScreenshot(img.src);
        }
      });
    }

    console.log('‚úÖ Screenshot added to planning tab');

  } catch (error) {
    console.error('‚ùå Error adding screenshot to planning:', error);
  }
}

/**
 * Update planning guidance with phase-specific content
 */
function updatePlanningGuidance(content) {
  try {
    const guidanceContent = document.getElementById('guidance-content');
    if (guidanceContent) {
      const timestamp = new Date().toLocaleString();
      const guidanceText = `Phase Guidance Update - ${timestamp}\n\n${content}\n\nUpdated: ${timestamp} CST`;
      guidanceContent.value = guidanceText;
    }

    console.log('‚úÖ Planning guidance updated');

  } catch (error) {
    console.error('‚ùå Error updating planning guidance:', error);
  }
}

/**
 * Stream output to terminal tab
 * Integrates with existing terminal system
 */
function streamToTerminal(output, level = 'info', source = 'phase-execution') {
  try {
    // Switch to terminal tab
    const terminalTab = document.querySelector('[data-tab="terminal"]');
    if (terminalTab && !terminalTab.classList.contains('active')) {
      terminalTab.click();
    }

    // Add to terminal content
    const terminalContent = document.getElementById('terminal-content');
    if (terminalContent) {
      const outputElement = document.createElement('div');
      outputElement.className = `terminal-output phase-output ${level}`;
      outputElement.innerHTML = `
        <span class="timestamp">[${new Date().toLocaleTimeString()}]</span>
        <span class="source">[${source}]</span>
        <span class="message">${output}</span>
      `;
      terminalContent.appendChild(outputElement);
      terminalContent.scrollTop = terminalContent.scrollHeight;
    }

    console.log('‚úÖ Output streamed to terminal');

  } catch (error) {
    console.error('‚ùå Error streaming to terminal:', error);
  }
}

/**
 * Append terminal output with formatting
 */
function appendTerminalOutput(data) {
  try {
    const terminalContent = document.getElementById('terminal-content');
    if (!terminalContent) {
      console.error('Terminal content not found');
      return;
    }

    const outputElement = document.createElement('div');
    outputElement.className = 'terminal-output phase-output';

    if (typeof data === 'string') {
      outputElement.textContent = data;
    } else {
      outputElement.innerHTML = `
        <span class="timestamp">[${new Date(data.timestamp || Date.now()).toLocaleTimeString()}]</span>
        <span class="level">[${data.level || 'info'}]</span>
        <span class="content">${data.content || data.message || JSON.stringify(data)}</span>
      `;
    }

    terminalContent.appendChild(outputElement);
    terminalContent.scrollTop = terminalContent.scrollHeight;

    console.log('‚úÖ Terminal output appended');

  } catch (error) {
    console.error('‚ùå Error appending terminal output:', error);
  }
}

/**
 * Show full-size screenshot in modal
 */
function showFullScreenshot(imageSrc) {
  const fullScreenModal = document.createElement('div');
  fullScreenModal.className = 'screenshot-fullscreen-modal';
  fullScreenModal.innerHTML = `
    <div class="fullscreen-overlay" style="position: fixed; top: 0; left: 0; width: 100vw; height: 100vh; background: rgba(0,0,0,0.9); z-index: 10000; display: flex; align-items: center; justify-content: center;">
      <div class="fullscreen-content" style="max-width: 90vw; max-height: 90vh;">
        <img src="${imageSrc}" style="max-width: 100%; max-height: 100%; object-fit: contain;" />
        <button class="close-fullscreen" style="position: absolute; top: 20px; right: 20px; background: rgba(255,255,255,0.2); border: none; color: white; font-size: 24px; cursor: pointer; padding: 10px;">&times;</button>
      </div>
    </div>
  `;

  document.body.appendChild(fullScreenModal);

  // Close handlers
  const closeBtn = fullScreenModal.querySelector('.close-fullscreen');
  const overlay = fullScreenModal.querySelector('.fullscreen-overlay');

  const closeModal = () => {
    document.body.removeChild(fullScreenModal);
  };

  closeBtn.addEventListener('click', closeModal);
  overlay.addEventListener('click', (e) => {
    if (e.target === overlay) closeModal();
  });
}

