async function setupFilesTabMermaid() {
  // After user picks directory, we will scan and render mermaid under Files tab
  const uploadBtn = document.getElementById('upload-files');
  if (!uploadBtn) return;

  uploadBtn.addEventListener('click', async () => {
    const chain = await domTracer.startSnowballChain('files-scan-mermaid');
    await chain.recordStep(1, 'Directory Picker', 'app.js:setupFilesTabMermaid', 'Open dialog');

    const pick = await window.electronAPI.openDirectory();
    if (!pick?.success || !pick?.path) {
      await chain.recordStep(2, 'Canceled/No Path', 'electronAPI.openDirectory', 'No selection');
      await chain.complete();
      return;
    }

    await chain.recordStep(2, 'Path Selected', 'electronAPI.openDirectory', pick.path);

    try {
      const scanStart = performance.now();
      const result = await window.electronAPI.scanDirectory(pick.path);
      const scanDuration = performance.now() - scanStart;
      await window.electronAPI.traceDebug?.('files:scan', pick.path, {
        count: result?.count,
        durationMs: scanDuration,
        truncated: result?.truncated,
      });
      await chain.recordStep(3, 'Scanned', 'preload.scanDirectory', `count=${result?.count}`);

      // Update Files tab badges
      const uploadedFileCountBadge = document.getElementById('uploadedFileCountBadge');
      if (uploadedFileCountBadge) uploadedFileCountBadge.textContent = String(result?.count || 0);
      const directoryReadyBadge = document.getElementById('directoryReadyBadge');
      if (directoryReadyBadge) directoryReadyBadge.textContent = 'Loaded';

      // Render mermaid of directory tree inside Files tab dedicated container
      const filesDiagram = document.getElementById('filesDiagramContainer');
      if (filesDiagram && result?.mermaid) {
        filesDiagram.innerHTML = `<pre class=\"mermaid\">${result.mermaid}</pre>`;
        if (typeof mermaid !== 'undefined') {
          try {
            mermaid.run();
          } catch {}
        }
      }
    } catch (error) {
      await window.electronAPI.traceDebug?.('files:scan:error', pick.path, {
        error: error?.message,
      });
      const filesDiagram = document.getElementById('filesDiagramContainer');
      if (filesDiagram)
        filesDiagram.innerHTML = `<div class=\"diagram-error\">Error: ${error.message}</div>`;
    }

    await chain.complete();
  });
}

// Render Files tab Mermaid from globally persisted project directory (no prompt)
async function renderFilesFromProject() {
  try {
    const project = await window.electronAPI.getProjectDirectory?.();
    if (!project?.path) return; // Nothing set yet

    const autoStart = performance.now();
    const result = await window.electronAPI.scanDirectory(); // defaults to global project on backend
    const autoDuration = performance.now() - autoStart;
    await window.electronAPI.traceDebug?.('files:auto-scan', project.path, {
      count: result?.count,
      durationMs: autoDuration,
      truncated: result?.truncated,
    });

    // Update badges
    const uploadedFileCountBadge = document.getElementById('uploadedFileCountBadge');
    if (uploadedFileCountBadge) uploadedFileCountBadge.textContent = String(result?.count || 0);
    const directoryReadyBadge = document.getElementById('directoryReadyBadge');
    if (directoryReadyBadge) directoryReadyBadge.textContent = 'Loaded';

    // Render into Files tab container
    const filesDiagram = document.getElementById('filesDiagramContainer');
    if (filesDiagram && result?.mermaid) {
      filesDiagram.innerHTML = `<pre class="mermaid">${result.mermaid}</pre>`;
      if (typeof mermaid !== 'undefined') {
        try {
          mermaid.run();
        } catch {}
      }
    }
  } catch (error) {
    await window.electronAPI.traceDebug?.('files:auto-scan:error', 'global-project', {
      error: error?.message,
    });
  }
}

function setupLeftTabs() {
  const tabs = document.querySelectorAll('.left-tabs .tab-btn');
  const panels = document.querySelectorAll('.left-tab-content');
  tabs.forEach(btn => {
    btn.addEventListener('click', async () => {
      const tab = btn.getAttribute('data-left-tab');

      // Start snowball chain for left-tab switch
      const chain = await domTracer.startSnowballChain('left-toolbar-tab-switch');
      await chain.recordStep(1, 'Tab Click', 'app.js:setupLeftTabs', `Tab=${tab}`);

      // Visual selection toggle
      tabs.forEach(b => b.classList.remove('active'));
      btn.classList.add('active');
      await chain.recordStep(2, 'Selection Update', 'app.js:setupLeftTabs', 'Active class applied');

      // Panel visibility
      panels.forEach(p => {
        const isActive = p.getAttribute('data-tab') === tab;
        p.classList.toggle('active', isActive);
      });
      await chain.recordStep(3, 'Panel Visibility', 'app.js:setupLeftTabs', 'Panels toggled');

      // Trace event
      await window.electronAPI.traceDebug?.('ui:tab-change', 'left-toolbar', { tab });

      // Persist selection for UX continuity
      localStorage.setItem('leftToolbarActiveTab', tab);
      await chain.recordStep(4, 'Persist', 'localStorage', 'Active tab stored');

      await chain.complete();
    });
  });

  // Restore last active tab if available
  const saved = localStorage.getItem('leftToolbarActiveTab');
  if (saved) {
    const target = document.querySelector(`.left-tabs .tab-btn[data-left-tab="${saved}"]`);
    if (target) target.click();
  }
}

