function attachHorizontalResizer(resizerEl, panelId, anchor) {
  const panel = document.getElementById(panelId);
  if (!panel) return;

  let startX = 0;
  let startWidth = 0;
  let dragging = false;

  const onMouseDown = e => {
    window.electronAPI.traceDebug?.('ui:gesture:start', `resizer:${panelId}`, {
      anchor,
      x: e.clientX,
    });
    dragging = true;
    startX = e.clientX;
    startWidth = panel.getBoundingClientRect().width;
    document.body.style.userSelect = 'none';
    document.body.style.cursor = 'col-resize';
  };

  const onMouseMove = e => {
    if (!dragging) return;
    const dx = e.clientX - startX;
    let newWidth = anchor === 'right' ? startWidth + dx : startWidth - dx;
    newWidth = Math.max(56, Math.min(520, newWidth));
    panel.style.width = `${newWidth}px`;
    window.electronAPI.traceDebug?.('ui:gesture:move', `resizer:${panelId}`, {
      x: e.clientX,
      width: newWidth,
    });
  };

  const onMouseUp = () => {
    dragging = false;
    document.body.style.userSelect = '';
    document.body.style.cursor = '';
    window.electronAPI.traceDebug?.('ui:gesture:end', `resizer:${panelId}`);
  };

  resizerEl.addEventListener('mousedown', onMouseDown);
  window.addEventListener('mousemove', onMouseMove);
  window.addEventListener('mouseup', onMouseUp);
}

// Snowball Chain implementation for comprehensive workflow tracking
class SnowballChain {
  constructor(chainId) {
    this.chainId = chainId;
    this.steps = [];
    this.startTime = performance.now();
  }

  async recordStep(stepNumber, action, fileLocation, outcome) {
    const step = {
      step: stepNumber,
      action,
      fileLocation,
      outcome,
      timestamp: performance.now(),
      duration: performance.now() - this.startTime,
    };

    this.steps.push(step);

    await window.electronAPI.traceDebug?.('ui:snowball-step', this.chainId, step);
  }

  async complete() {
    const totalDuration = performance.now() - this.startTime;

    await window.electronAPI.traceDebug?.('ui:snowball-complete', this.chainId, {
      steps: this.steps,
      totalDuration,
      success: true,
    });
  }
}

