async function setupTerminalDropdownActions() {
  const dropdownMenu = document.getElementById('terminal-dropdown-menu');
  if (!dropdownMenu) return;

  const dropdownActions = {
    'clear-terminal': async () => {
      const chain = await domTracer.startSnowballChain('terminal-clear-chain');
      await chain.recordStep(1, 'Clear Request', 'app.js', 'User clicked clear');

      if (xtermInstance) {
        const oldLines = xtermInstance.buffer?.active?.length || 0;
        xtermInstance.clear();

        await chain.recordStep(2, 'Terminal Cleared', 'xterm.js', `Cleared ${oldLines} lines`);

        await domTracer.traceStateChange(
          xtermInstance,
          { lines: oldLines },
          { lines: 0 },
          { trigger: 'clear-command' }
        );
      }

      await chain.recordStep(3, 'Clear Complete', 'app.js', 'Terminal ready for input');
      await chain.complete();
    },

    'restart-terminal': async () => {
      const chain = await domTracer.startSnowballChain('terminal-restart-chain');
      await chain.recordStep(1, 'Restart Request', 'app.js', 'User clicked restart');

      try {
        await window.electronAPI.restartTerminal?.(currentTerminalId);
        await chain.recordStep(
          2,
          'Backend Restart',
          'pty-manager.js',
          `Terminal ${currentTerminalId} restarted`
        );

        if (xtermInstance) {
          xtermInstance.reset();
          await chain.recordStep(3, 'Frontend Reset', 'xterm.js', 'Terminal display reset');
        }

        await chain.recordStep(4, 'Restart Complete', 'app.js', 'Terminal ready');
      } catch (error) {
        await chain.recordStep(99, 'Restart Error', 'app.js', error.message);
      }

      await chain.complete();
    },

    'change-directory': async () => {
      const chain = await domTracer.startSnowballChain('terminal-cd-chain');
      await chain.recordStep(1, 'Directory Change', 'app.js', 'User requested directory change');

      const path = prompt('Enter directory path:', '/');
      if (path) {
        await chain.recordStep(2, 'Path Input', 'app.js', `Target: ${path}`);

        await window.electronAPI.writeToTerminal?.(
          path.startsWith('cd ') ? `${path}\\r` : `cd ${path}\\r`,
          currentTerminalId
        );
        await chain.recordStep(3, 'Command Sent', 'pty-manager.js', `cd ${path}`);

        await domTracer.traceStateChange(
          document.getElementById('terminal-status'),
          { workingDir: null },
          { workingDir: path },
          { trigger: 'directory-change' }
        );
      }

      await chain.complete();
    },

    'copy-selection': async () => {
      if (xtermInstance?.hasSelection?.()) {
        const selection = xtermInstance.getSelection();
        await navigator.clipboard.writeText(selection);

        await window.electronAPI.traceDebug?.('ui:clipboard', 'terminal-copy', {
          length: selection.length,
          preview: selection.substring(0, 50),
        });
      }
    },
  };

  // Bind dropdown actions
  Object.keys(dropdownActions).forEach(action => {
    const menuItem = dropdownMenu.querySelector(`[data-action="${action}"]`);
    if (menuItem) {
      menuItem.addEventListener('click', dropdownActions[action]);
    }
  });
}

