        const newTerminalId = ptyResult?.terminalId || Date.now();

        await chain.recordStep(4, 'PTY Connection', 'pty-manager.js', `PTY ID: ${newTerminalId}`);

        // Store enhanced pane reference
        terminalPanes.set(newTerminalId, {
          pane: newPane,
