async function connectWebview() {
  const webview = document.getElementById('chrome-viewport');
  if (webview) {
    try {
      const response = await fetch('http://localhost:9222/json');
      const tabs = await response.json();
      if (tabs.length > 0) {
        const firstTab = tabs[0];
        webview.src = firstTab.devtoolsFrontendUrl;
        console.log('‚úÖ WebView connected to DevTools:', firstTab.title);
      } else {
        webview.src = 'http://localhost:9222';
      }
    } catch (error) {
      console.error('‚ùå WebView connection failed:', error);
      webview.src = 'about:blank';
    }
  }
}

async function setupWebviewContainer() {
  const webview = document.getElementById('chrome-viewport');
  if (!webview) return;

  const chain = await domTracer.startSnowballChain('webview-initialization-chain');

  // Step 1: Webview setup
  await chain.recordStep(1, 'Webview Setup', 'app.js:setupWebviewContainer', 'Container ready');

  // Ensure secure webview configuration (popups DISABLED for confinement)
  webview.setAttribute('nodeintegration', 'false');
  webview.setAttribute('webSecurity', 'true');
  webview.setAttribute('allowpopups', 'false'); // CRITICAL: Prevent external popups

  // Step 2: Wait for Chrome initialization
  window.electronAPI.onChromeStatus?.(async (event, status) => {
    if (status.connected && status.debugPort) {
      await chain.recordStep(2, 'Chrome Connected', 'main.js', `Port: ${status.debugPort}`);

      // Step 3: Create browseable Chrome tab via CDP API
      try {
        // First, create a new tab with browseable content
        const createResponse = await fetch(
          `http://localhost:${status.debugPort}/json/new?https://www.google.com`
        );
        const tabData = await createResponse.json();

        await chain.recordStep(3, 'Chrome Tab Created', 'CDP API', `Tab ID: ${tabData.id}`);

        // Step 4: Navigate webview to the actual browseable content
        // Use the tab's webSocketDebuggerUrl for DevTools OR direct URL for browsing
        if (tabData.webSocketDebuggerUrl) {
          // For contained browsing, we'll load the page directly in webview
          webview.src = 'https://www.google.com';
          await chain.recordStep(
            4,
            'Direct Navigation',
            'webview',
            'Loading Google directly in webview'
          );
        } else {
          // Fallback to basic about:blank
          webview.src = 'about:blank';
          await chain.recordStep(4, 'Fallback Navigation', 'webview', 'Loading blank page');
        }
      } catch (error) {
        console.error('Failed to create Chrome tab:', error);
        await chain.recordStep(99, 'CDP Error', 'fetch', error.message);
        // Fallback: Load content directly in webview without CDP
        webview.src = 'https://www.google.com';
        await chain.recordStep(4, 'Direct Fallback', 'webview', 'Loading Google directly');
      }

      await domTracer.traceStateChange(
        webview,
        { src: 'about:blank', connected: false },
        { src: webview.src, connected: true },
        { trigger: 'chrome-connection' }
      );
    }
  });

  // Step 5: Handle navigation events and enforce confinement
  webview.addEventListener('did-navigate', async e => {
    await chain.recordStep(5, 'Navigation', 'webview', `URL: ${e.url}`);
    await domTracer.traceStateChange(
      webview,
      { url: null },
      { url: e.url },
      { trigger: 'navigation' }
    );
  });

  // CRITICAL: Prevent new windows (popups) - enforce confinement
  webview.addEventListener('new-window', async e => {
    e.preventDefault(); // Block the popup
    console.log(`üö´ Blocked popup attempt: ${e.url}`);

    // Instead of opening popup, navigate within the webview
    webview.src = e.url;

    await domTracer.traceStateChange(
      webview,
      { action: 'popup-attempt' },
      { action: 'confined-navigation', url: e.url },
      { trigger: 'popup-prevention' }
    );

    await window.electronAPI.traceDebug?.('webview:popup-blocked', e.url, {
      reason: 'confinement-policy',
      redirectedTo: 'webview-navigation',
    });
  });

  // Handle external navigation attempts
  webview.addEventListener('will-navigate', async e => {
    console.log(`üîÑ Webview navigating to: ${e.url}`);
    await window.electronAPI.traceDebug?.('webview:navigation', e.url);
  });

  // Step 6: Handle successful load
  webview.addEventListener('did-finish-load', async () => {
    await chain.recordStep(6, 'Load Complete', 'webview', 'Content rendered successfully');
    webview.style.border = '2px solid var(--am-accent-green)';

    await domTracer.traceStateChange(
      webview,
      { loaded: false, border: 'none', confined: false },
      { loaded: true, border: '2px solid var(--am-accent-green)', confined: true },
      { trigger: 'load-success' }
    );

    // Step 7: Ready for confined browsing
    await chain.recordStep(
      7,
      'Confined Browsing Ready',
      'webview',
      'User can browse within webview - no popups allowed'
    );
    await chain.complete();

    console.log('‚úÖ Webview configured for confined browsing - popups blocked');
  });

  // Handle load failures
  webview.addEventListener('did-fail-load', async event => {
    console.error('‚ùå Webview failed to load:', event.errorDescription);
    webview.style.border = '2px solid #f44336';

    await domTracer.traceStateChange(
      webview,
      { loaded: false, error: null },
      { loaded: false, error: event.errorDescription },
      { trigger: 'load-failure' }
    );

    await chain.recordStep(99, 'Load Error', 'webview', event.errorDescription);
    await chain.complete();
  });

  // Handle security restrictions
  webview.addEventListener('console-message', e => {
    console.log(`Webview console: ${e.message}`);
  });
}

let xtermInstance = null;
let xtermFitAddon = null;
let terminalInitialized = false;
let currentTerminalId = 1;
let terminalPanes = new Map();

async function setupTerminalContainer() {
  const container = document.getElementById('terminal-container');
  const statusEl = document.getElementById('terminal-status');
  const inputEl = document.getElementById('terminal-input');
  const sendBtn = document.getElementById('terminal-send');
  if (!container) return;

  // Setup enhanced terminal input context like Cursor IDE
  await setupTerminalInputContext(inputEl);

  // Initialize live terminal status
  await initializeLiveTerminalStatus();

  // Initialize the terminal tab manager
  console.log('üîç DOM TRACE: Initializing terminal tab manager');
  terminalTabManager = new TerminalTabManager();

  // EMERGENCY: Setup single global terminal data handler (replaces competing handlers)
  console.log('üîç GLOBAL HANDLER: Setting up single terminal data router');
  window.electronAPI.onTerminalData?.((event, packet) => {
    console.log('üîç GLOBAL HANDLER: Terminal data received:', packet);

    // Extract terminal ID and data from packet
    const rawTerminalId = packet?.terminalId || packet?.id;
    const text = typeof packet === 'string' ? packet : (packet?.data || packet);

    // EMERGENCY FIX: Handle backend ID format properly
    // If backend already returns full ID (terminal-1), use as-is
    // If backend returns numeric ID (1), add prefix
    let terminalId = null;
    if (rawTerminalId) {
      if (typeof rawTerminalId === 'string' && rawTerminalId.startsWith('terminal-')) {
        terminalId = rawTerminalId; // Already has prefix
      } else {
        terminalId = `terminal-${rawTerminalId}`; // Add prefix to numeric ID
      }
    }

    console.log('üîç GLOBAL HANDLER: ID conversion:', {
      rawTerminalId,
      type: typeof rawTerminalId,
      hasPrefix: typeof rawTerminalId === 'string' && rawTerminalId.startsWith('terminal-'),
      convertedTo: terminalId
    });
    console.log('üîç GLOBAL HANDLER: Text length:', text?.length, 'Preview:', text?.substring(0, 50));

    if (terminalTabManager && terminalId) {
      // Route to TerminalTabManager
      const terminalData = terminalTabManager.terminals.get(terminalId);
      if (terminalData && terminalData.terminal) {
        terminalData.terminal.write(text);
        console.log('‚úÖ GLOBAL HANDLER: Data written successfully to terminal:', terminalId);
      } else {
        console.warn('üö® GLOBAL HANDLER: Terminal not found:', terminalId);
        console.warn('üö® GLOBAL HANDLER: Available terminals:', Array.from(terminalTabManager.terminals.keys()));
      }
    } else {
      console.error('üö® GLOBAL HANDLER: Missing data:', { terminalTabManager: !!terminalTabManager, terminalId, rawId: rawTerminalId });
    }
  });

  // Setup dropdown handlers
  setupTerminalDropdownHandlers();

  // Create initial terminal if window.Terminal is available
  try {
    if (window.Terminal) {
      console.log('üñ•Ô∏è Setting up initial terminal with tab manager...');
      // Create first terminal using the tab manager
      const firstTerminalId = terminalTabManager.createTerminal({ name: 'Terminal 1' });
      console.log('üîç DOM TRACE: Initial terminal created:', firstTerminalId);

      // Keep legacy xtermInstance reference for backward compatibility
      if (firstTerminalId) {
        const firstTerminal = terminalTabManager.terminals.get(firstTerminalId);
        if (firstTerminal) {
          xtermInstance = firstTerminal.terminal;

          // Setup fit addon for legacy reference
          if (window.FitAddon?.FitAddon) {
            xtermFitAddon = new window.FitAddon.FitAddon();
            xtermInstance.loadAddon(xtermFitAddon);

            // Initial fit after DOM rendering
            setTimeout(() => {
              try {
                xtermFitAddon.fit();
                // Force another fit after layout stabilizes
                setTimeout(() => xtermFitAddon.fit(), 100);
              } catch {}
            }, 50);

            window.addEventListener('resize', () => {
              try {
                xtermFitAddon.fit();
              } catch {}
            });
          }

          // Forward user input to backend PTY with performance tracking
          xtermInstance.onData(async data => {
            const keystrokeStart = performance.now();

            // Start snowball chain for terminal interaction
            const chain = await domTracer.startSnowballChain('terminal-keystroke-chain');
            await chain.recordStep(1, 'User Input', 'xterm.onData', `${data.length} chars`);

            try {
              await chain.recordStep(2, 'IPC Forward', 'app.js:writeToTerminal', 'Sending to PTY');
              await window.electronAPI.writeToTerminal(data, 1);

              const keystrokeDuration = performance.now() - keystrokeStart;

              // Track keystroke performance
              await domTracer.tracePerformance(
                container,
                'keystroke-response',
                keystrokeDuration,
                domTracer.performanceTargets.keystroke
              );

              await chain.recordStep(
                3,
                'PTY Processing',
                'src/terminal/pty-manager.js',
                `Latency: ${keystrokeDuration.toFixed(2)}ms`
              );

              // Warn if keystroke latency is too high
              if (keystrokeDuration > domTracer.performanceTargets.keystroke) {
                console.warn(
                  `‚ö†Ô∏è Terminal keystroke latency: ${keystrokeDuration.toFixed(2)}ms (target: ${domTracer.performanceTargets.keystroke}ms)`
                );
                await window.electronAPI.traceDebug?.(
                  'ui:performance-violation',
                  'terminal-keystroke',
                  {
                    duration: keystrokeDuration,
                    target: domTracer.performanceTargets.keystroke,
                    data: data.substring(0, 10),
                  }
                );
              }

              await chain.complete();
            } catch (e) {
              console.warn('‚ö†Ô∏è Forward input failed:', e.message);
              await chain.recordStep(99, 'Error', 'app.js', e.message);
              await chain.complete();
            }
          });

          // Click-to-focus for better UX
          container.addEventListener('click', () => xtermInstance?.focus());

          // EMERGENCY: LEGACY SYSTEM DISABLED - Using TerminalTabManager only
          // Legacy terminal data handler REMOVED to prevent conflicts

          const terminalReady = await getTerminalReadyMessage();
          xtermInstance.writeln(terminalReady);
          // Status is now handled by initializeLiveTerminalStatus()
          terminalInitialized = true;

          // Input bar behavior
          if (inputEl && sendBtn) {
            const sendInput = () => {
              console.log('üîç DOM TRACE: sendInput called');
              const userCommand = inputEl.value;
              console.log('üîç DOM TRACE: User command:', userCommand);

              if (userCommand.trim().length === 0) {
                console.log('üîç DOM TRACE: Empty command, returning');
                return;
              }

              console.log('üîç DOM TRACE: Selected context items:', selectedContextItems);
              console.log(
                'üîç DOM TRACE: Context items count:',
                selectedContextItems ? selectedContextItems.length : 'undefined'
              );

              // Build context string for background inclusion
              let contextString = '';
              if (selectedContextItems && selectedContextItems.length > 0) {
                console.log(
                  'üîç DOM TRACE: Building context string for',
                  selectedContextItems.length,
                  'items'
                );

                const contextParts = selectedContextItems.map((item, index) => {
                  let contextPart;
                  if (item.type === 'file' || item.type === 'screenshot') {
                    contextPart = `[Context: ${item.type} - ${item.path || item.name}]`;
                  } else if (item.content) {
                    contextPart = `[Context: ${item.type} - ${item.content.substring(0, 50)}...]`;
                  } else {
                    contextPart = `[Context: ${item.type} - ${item.name}]`;
                  }
                  console.log(`üîç DOM TRACE: Context part ${index}:`, contextPart);
                  return contextPart;
                });

                contextString = contextParts.join('\n');
                console.log('üîç DOM TRACE: Complete context string:', contextString);
              } else {
                console.log('üîç DOM TRACE: No context items, sending command without context');
              }

              // Send command with context (background inclusion like Cursor)
              const fullCommand = contextString ? `${contextString}\n${userCommand}` : userCommand;
              const terminalValue = fullCommand + '\r';

              console.log('üîç DOM TRACE: Full command to send:');
              console.log(fullCommand);
              console.log(
                'üîç DOM TRACE: Terminal value with carriage return:',
                terminalValue.length,
                'chars'
              );

              try {
                console.log('üîç DOM TRACE: Calling window.electronAPI.writeToTerminal...');
                window.electronAPI.writeToTerminal(terminalValue, 1);
                console.log('üîç DOM TRACE: Command sent to terminal successfully');
              } catch (error) {
                console.error('üö® DOM TRACE ERROR: Failed to write to terminal:', error);
              }

              // Clear input and context after sending
              console.log('üîç DOM TRACE: Clearing input and context after sending');
              inputEl.value = '';
              console.log('üîç DOM TRACE: Input cleared');

              if (selectedContextItems) {
                console.log('üîç DOM TRACE: Clearing', selectedContextItems.length, 'context items');
                selectedContextItems.length = 0;
                console.log('üîç DOM TRACE: Context items cleared, updating indicator');
                updateContextIndicator();
              }

              console.log('üîç DOM TRACE: sendInput complete');
            };
            inputEl.addEventListener('keydown', e => {
              if (e.key === 'Enter') sendInput();
            });

            // Flexible input sizing like Cursor IDE
            const autoResizeInput = () => {
              const minWidth = 200;
              const maxWidth = inputEl.parentElement.clientWidth - 100; // Leave space for button
              const testSpan = document.createElement('span');
              testSpan.style.font = window.getComputedStyle(inputEl).font;
              testSpan.style.visibility = 'hidden';
              testSpan.style.position = 'absolute';
              testSpan.style.whiteSpace = 'pre';
              testSpan.textContent = inputEl.value || inputEl.placeholder;
              document.body.appendChild(testSpan);
              const textWidth = testSpan.getBoundingClientRect().width + 20; // Add padding
              document.body.removeChild(testSpan);
              inputEl.style.width = Math.min(Math.max(textWidth, minWidth), maxWidth) + 'px';
            };

            inputEl.addEventListener('input', autoResizeInput);
            inputEl.addEventListener('focus', autoResizeInput);
            window.addEventListener('resize', autoResizeInput);

            // Initial resize
            setTimeout(autoResizeInput, 100);

            sendBtn.addEventListener('click', sendInput);
          }
        }
      }
    } else {
      console.warn('‚ö†Ô∏è xterm.js not available. Falling back to basic output.');
      if (statusEl) statusEl.textContent = 'Terminal library missing; fallback mode';
    }
  } catch (error) {
    console.error('‚ùå Terminal setup failed:', error);
    if (statusEl) statusEl.textContent = 'Terminal error';
  }

  // Debug: if terminal not initialized after delay, emit trace and guidance
  setTimeout(() => {
    if (!terminalInitialized) {
      window.electronAPI.traceDebug?.('terminal:init-timeout', 'Terminal not initialized in time', {
        hasTerminalLib: !!window.Terminal,
        hasFitAddon: !!window.FitAddon,
      });
      if (statusEl) statusEl.textContent = 'Terminal not initialized';
    }
  }, 1500);
}

