// Split terminal functionality
function splitTerminal() {
  console.log('üîç DOM TRACE: Split terminal requested');

  if (!terminalTabManager) {
    console.error('üö® DOM TRACE ERROR: Terminal manager not initialized');
    return;
  }

  const activeTerminal = terminalTabManager.getActiveTerminal();
  if (!activeTerminal) {
    console.warn('‚ö†Ô∏è DOM TRACE: No active terminal to split');
    // Create first terminal instead
    terminalTabManager.createTerminal({ name: 'Terminal 1' });
    return;
  }

  // Get current terminal's working directory
  const workingDir = activeTerminal.workingDirectory || '';
  console.log('üîç DOM TRACE: Split terminal working directory:', workingDir || 'default');

  // Create new terminal with same working directory
  const newTerminalId = terminalTabManager.createTerminal({
    cwd: workingDir,
    name: `Split ${terminalTabManager.nextId}`,
    splitFrom: terminalTabManager.activeTerminalId,
  });

  console.log('üîç DOM TRACE: Split terminal created:', newTerminalId);
  closeDropdown();
}

// Connect to Strike Errors functionality
async function connectToStrikeErrors() {
  console.log('üîç DOM TRACE: Connecting to Strike Errors');

  if (!terminalTabManager) {
    console.error('üö® DOM TRACE ERROR: Terminal manager not initialized');
    return;
  }

  try {
    // Check if Strike Errors is running
    const strikeStatus = await window.electronAPI?.getStrikeErrorsStatus?.();
    console.log('üîç DOM TRACE: Strike Errors status:', strikeStatus);

    if (!strikeStatus || !strikeStatus.running) {
      console.warn('‚ö†Ô∏è DOM TRACE: Strike Errors not running, launching...');
      // Launch Strike Errors first
      await window.electronAPI?.strikeErrorsOpen?.();
      // Wait for startup
      await new Promise(resolve => setTimeout(resolve, 2000));
    }

    // Create dedicated terminal for Strike Errors
    const terminalId = terminalTabManager.createTerminal({
      name: 'Strike Errors',
      type: 'strike-errors',
      port: strikeStatus?.port || 9229,
    });

    // Connect terminal to Strike Errors output stream
    if (terminalId) {
      await window.electronAPI?.connectTerminalToStrikeErrors?.({
        terminalId,
        streamType: 'all', // stdout, stderr, or all
      });
    }

    console.log('üîç DOM TRACE: Strike Errors terminal connected:', terminalId);
    closeDropdown();
  } catch (error) {
    console.error('üö® DOM TRACE ERROR: Failed to connect to Strike Errors:', error);
