function addImportedFile(fileName, content, type) {
  const container = document.getElementById('imported-files');
  const template = container.querySelector('.template');

  if (template) {
    const fileItem = template.cloneNode(true);
    fileItem.classList.remove('template');
    fileItem.querySelector('.file-name').textContent = fileName;
    fileItem.dataset.content = content;
    fileItem.dataset.type = type;

    // Add timestamp for sorting
    fileItem.dataset.imported = Date.now();

    container.appendChild(fileItem);
    console.log('üîç DOM TRACE: Added imported file to container:', fileName);
  }
}

