async function toggleToolbar(toolbarId) {
  const toolbar = document.getElementById(toolbarId);
  if (toolbar) {
    const startTime = await domTracer.traceInteraction(toolbar, 'ui:click', { action: 'toggle' });

    // Start snowball chain for complete workflow tracking
    const chain = await domTracer.startSnowballChain(`${toolbarId}-toggle-chain`);

    // Step 1: Event Binding
    await chain.recordStep(
      1,
      'Event Binding',
      `app.js:${toolbarId === 'leftToolbar' ? '149' : '153'}`,
      'Click handler triggered'
    );

    // Track state before change
    const oldState = {
      collapsed: toolbar.classList.contains('collapsed'),
      width: toolbar.style.width,
    };

    // Step 2: Toggle Classes
    const isCollapsed = toolbar.classList.toggle('collapsed');
    await chain.recordStep(2, 'Toggle Classes', 'app.js:813', `.collapsed toggled: ${isCollapsed}`);

    // Track state change
    await domTracer.traceStateChange(
      toolbar,
      oldState,
      {
        collapsed: isCollapsed,
        width: isCollapsed ? '' : toolbar.style.width,
      },
      {
        trigger: 'toolbar-toggle',
        direction: isCollapsed ? 'collapse' : 'expand',
      }
    );

    // Step 3: Arrow Update (use codicon chevrons, not emoji)
    const arrow = toolbar.querySelector('.toggle-arrow');
    if (arrow) {
      const wasRight = arrow.classList.contains('codicon-chevron-right');
      const wasLeft = arrow.classList.contains('codicon-chevron-left');
      const oldState = wasRight ? 'chevron-right' : wasLeft ? 'chevron-left' : 'unknown';

      const nextChevron = isCollapsed
        ? toolbar.id === 'leftToolbar'
          ? 'codicon-chevron-right'
          : 'codicon-chevron-left'
        : toolbar.id === 'leftToolbar'
          ? 'codicon-chevron-left'
          : 'codicon-chevron-right';

      arrow.classList.remove('codicon-chevron-right', 'codicon-chevron-left');
      arrow.classList.add('codicon', nextChevron);
      arrow.textContent = '';

      await chain.recordStep(
        3,
        'Arrow Update',
        'app.js:839',
        `Arrow: ${oldState} ‚Üí ${nextChevron}`
      );

      await domTracer.traceStateChange(
        arrow,
        { chevron: oldState },
        { chevron: nextChevron },
        { trigger: 'arrow-update' }
      );
    }

    // Step 4: Width Clear
    if (isCollapsed) {
      // clear inline width when collapsed to snap
      toolbar.style.width = '';
      await chain.recordStep(4, 'Width Clear', 'app.js:851', 'Inline width removed');

      await window.electronAPI.traceDebug?.('ui:style-clear', toolbarId, {
        property: 'width',
        reason: 'collapse',
      });
    }

    const duration = performance.now() - startTime;
    await domTracer.tracePerformance(
      toolbar,
      'toggle-animation',
      duration,
      domTracer.performanceTargets.animation
    );

    // Step 5: State Save
    localStorage.setItem(`${toolbarId}-collapsed`, isCollapsed);
    await chain.recordStep(5, 'State Save', 'localStorage', 'Persistence complete');

    await window.electronAPI.traceDebug?.('ui:persistence', toolbarId, {
      collapsed: isCollapsed,
      storage: 'localStorage',
    });

    // Complete the snowball chain
    await chain.complete();
  }
}

